        try {
          const newMilestones = await storage.checkAndUpdateMilestones(userId);
          if (newMilestones.length > 0) {
            console.log(`New milestones achieved by user ${userId} through automatic location check:`, newMilestones.map(m => m.milestoneName).join(', '));
          }
        } catch (error) {
          console.error('Error checking milestones after automatic island visit:', error);
          // Don't fail the location check if milestone checking fails
        }
      }

      res.json({
        nearbyIslands: result.visitedIslands,
        newlyVisited: result.newlyVisited,
        message: result.newlyVisited.length > 0 
