  // Manual island check-in endpoint
  app.post("/api/islands/:id/check-in", isAuthenticated, requireWriteAccess, async (req: any, res) => {
    try {
      const islandId = req.params.id;
      const userId = req.user.claims.sub;

      // Validate request body
      const checkInSchema = z.object({
        equipmentIds: z.array(z.string()).default([])
      });

      const validatedBody = checkInSchema.parse(req.body);
      let { equipmentIds } = validatedBody;

      // Get current user to check permissions
      const currentUser = await storage.getUser(userId);
      if (!currentUser) {
        return res.status(401).json({ message: "User not found" });
      }

      // Check if user has permission to check in with equipment
      if (equipmentIds.length > 0 && !hasPermission(currentUser.role as UserRole, 'checkInWithEquipment')) {
        // For attachment users, strip equipment IDs and warn them
        if (currentUser.role === 'attachment') {
          equipmentIds = []; // Strip equipment IDs for attachment users
        } else {
          return res.status(403).json({ 
            message: "You don't have permission to check in with equipment. Contact your administrator for access." 
          });
        }
      }

      // Get the island to check if it exists and isn't already visited
      const island = await storage.getIsland(islandId);
      if (!island) {
        return res.status(404).json({ message: "Island not found" });
      }

      if (island.isVisited) {
        return res.json({
          island,
          message: `${island.name} is already marked as visited`,
          alreadyVisited: true
        });
      }

      // Mark the island as visited with equipment tracking
      const updatedIsland = await storage.markIslandVisitedWithEquipment(islandId, userId, equipmentIds);

      const equipmentMessage = equipmentIds.length > 0 
        ? ` and transferred ${equipmentIds.length} equipment item(s) to this island`
        : "";

      // Add warning for attachment users who tried to check in with equipment
      const warningMessage = (currentUser.role === 'attachment' && validatedBody.equipmentIds.length > 0)
        ? " (Note: Equipment transfers were not processed due to insufficient permissions)"
        : "";

      res.json({
        island: updatedIsland,
        message: `Successfully checked in to ${updatedIsland.name}${equipmentMessage}!${warningMessage}`,
        alreadyVisited: false,
        equipmentCount: equipmentIds.length
      });
    } catch (error: any) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: "Invalid request data", 
          errors: error.errors 
        });
      }
      console.error("Error checking in to island:", error);
      res.status(500).json({ 
        message: "Failed to check in to island", 
        error: error.message 
      });
    }
  });

