      const currentUser = await storage.getUser(req.user.claims.sub);
      if (currentUser?.role !== 'admin' && currentUser?.role !== 'coordinator') {
        return res.status(403).json({ message: "Only admins and coordinators can delete equipment" });
      }

      await storage.deleteEquipment(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting equipment:", error);
      res.status(500).json({ message: "Failed to delete equipment" });
    }
  });

  // Milestone routes
  app.get("/api/milestones", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const milestones = await storage.getUserMilestones(userId);
      res.json(milestones);
    } catch (error) {
      console.error("Error fetching user milestones:", error);
      res.status(500).json({ message: "Failed to fetch milestones" });
    }
  });

  app.get("/api/milestones/stats", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const stats = await storage.getUserVisitStats(userId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching user visit stats:", error);
      res.status(500).json({ message: "Failed to fetch visit stats" });
    }
  });

  app.get("/api/milestones/history", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const history = await storage.getUserIslandVisitHistory(userId);
      res.json(history);
    } catch (error) {
      console.error("Error fetching user visit history:", error);
      res.status(500).json({ message: "Failed to fetch visit history" });
    }
  });

  app.post("/api/milestones/refresh", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const newMilestones = await storage.checkAndUpdateMilestones(userId);
      res.json({
        message: "Milestones refreshed successfully",
        newMilestones,
        count: newMilestones.length
      });
    } catch (error) {
      console.error("Error refreshing milestones:", error);
      res.status(500).json({ message: "Failed to refresh milestones" });
    }
  });

  // Admin milestone management endpoints
  app.post("/api/admin/milestones/clear-all", isAuthenticated, requireAdmin, async (req: any, res) => {
    try {
      const result = await storage.clearAllUserMilestones();
      res.json({
        message: "All user milestones cleared successfully",
        deletedCount: result
      });
    } catch (error) {
      console.error("Error clearing all milestones:", error);
      res.status(500).json({ message: "Failed to clear milestones" });
    }
  });

  app.post("/api/admin/milestones/refresh-all", isAuthenticated, requireAdmin, async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      const results = [];

      for (const user of users) {
        try {
          const newMilestones = await storage.checkAndUpdateMilestones(user.id);
          results.push({
            userId: user.id,
            email: user.email,
            milestonesCount: newMilestones.length
          });
        } catch (error) {
          console.error(`Error refreshing milestones for user ${user.id}:`, error);
          results.push({
            userId: user.id,
            email: user.email,
            error: error instanceof Error ? error.message : 'Unknown error'
          });
        }
      }

      res.json({
        message: "Milestone refresh completed for all users",
        results
      });
    } catch (error) {
      console.error("Error refreshing all milestones:", error);
      res.status(500).json({ message: "Failed to refresh all milestones" });
    }
  });

  app.delete("/api/admin/milestones/user/:userId", isAuthenticated, requireAdmin, async (req: any, res) => {
    try {
      const userId = req.params.userId;
      const result = await storage.clearUserMilestones(userId);
      res.json({
        message: `Milestones cleared for user ${userId}`,
        deletedCount: result
      });
    } catch (error) {
      console.error("Error clearing user milestones:", error);
      res.status(500).json({ message: "Failed to clear user milestones" });
    }
  });

  app.post("/api/admin/database/reset-cache", isAuthenticated, requireAdmin, async (req: any, res) => {
    try {
      // Clear any server-side caches if they exist
      // For now, just return success - we'll expand this if needed
      res.json({
        message: "Database cache cleared successfully"
      });
    } catch (error) {
      console.error("Error clearing database cache:", error);
      res.status(500).json({ message: "Failed to clear database cache" });
    }
  });

  // Get visit history for a specific island
  app.get("/api/island-visits", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const islandId = req.query.islandId as string;

      if (!islandId) {
        return res.status(400).json({ message: "Island ID is required" });
      }

      const visits = await storage.getIslandVisitHistory(islandId, userId);
      res.json(visits);
    } catch (error) {
      console.error("Error fetching island visit history:", error);
      res.status(500).json({ message: "Failed to fetch island visit history" });
    }
  });








  // Export endpoints for data
  app.get("/api/export/events", isAuthenticated, async (req: any, res) => {
    try {
      const format = req.query.format || 'json';
      const userRole = req.user?.claims?.role || 'agent';

      // Only coordinators and admins can export data
      if (userRole === 'agent' || userRole === 'attachment') {
        return res.status(403).json({ error: "Insufficient permissions to export data" });
      }

      const allEvents = await storage.getEvents();

      if (format === 'csv') {
        const createCsvWriter = require('csv-writer').createObjectCsvWriter;

        // Create CSV with proper headers
        const csvWriter = createCsvWriter({
          path: '/tmp/events_export.csv',
          header: [
            {id: 'id', title: 'ID'},
            {id: 'title', title: 'Title'},
            {id: 'island', title: 'Island'},
            {id: 'atoll', title: 'Atoll'},
            {id: 'eventLocation', title: 'Event Location'},
            {id: 'waitingLocation', title: 'Waiting Location'},
            {id: 'contact', title: 'Contact'},
            {id: 'comment', title: 'Comment'},
            {id: 'latitude', title: 'Latitude'},
            {id: 'longitude', title: 'Longitude'},
            {id: 'status', title: 'Status'},
            {id: 'participantCount', title: 'Participants'},
            {id: 'createdBy', title: 'Created By'},
            {id: 'createdAt', title: 'Created At'},
            {id: 'updatedAt', title: 'Updated At'}
          ]
        });

        await csvWriter.writeRecords(allEvents);
        res.setHeader('Content-Type', 'text/csv');
        res.setHeader('Content-Disposition', 'attachment; filename="events_export.csv"');
        res.sendFile('/tmp/events_export.csv');
      } else {
        res.setHeader('Content-Type', 'application/json');
        res.setHeader('Content-Disposition', 'attachment; filename="events_export.json"');
        res.json({
          exportDate: new Date().toISOString(),
          totalEvents: allEvents.length,
          data: allEvents
        });
      }
    } catch (error) {
      console.error("Error exporting events:", error);
      res.status(500).json({ error: "Failed to export events" });
    }
  });

  app.get("/api/export/users", isAuthenticated, async (req: any, res) => {
    try {
      const format = req.query.format || 'json';
      const userRole = req.user?.claims?.role || 'agent';

      // Only admins can export user data
      if (userRole !== 'admin') {
        return res.status(403).json({ error: "Only administrators can export user data" });
      }

      const allUsers = await storage.getAllUsers();

      if (format === 'csv') {
        const createCsvWriter = require('csv-writer').createObjectCsvWriter;

        const csvWriter = createCsvWriter({
          path: '/tmp/users_export.csv',
          header: [
            {id: 'id', title: 'ID'},
            {id: 'email', title: 'Email'},
            {id: 'firstName', title: 'First Name'},
            {id: 'lastName', title: 'Last Name'},
            {id: 'serviceNumber', title: 'Service Number'},
            {id: 'mobile', title: 'Mobile'},
            {id: 'role', title: 'Role'},
            {id: 'isActive', title: 'Active'},
            {id: 'createdAt', title: 'Created At'}
          ]
        });

        await csvWriter.writeRecords(allUsers);
        res.setHeader('Content-Type', 'text/csv');
        res.setHeader('Content-Disposition', 'attachment; filename="users_export.csv"');
        res.sendFile('/tmp/users_export.csv');
      } else {
        res.setHeader('Content-Type', 'application/json');
        res.setHeader('Content-Disposition', 'attachment; filename="users_export.json"');
        res.json({
          exportDate: new Date().toISOString(),
          totalUsers: allUsers.length,
          data: allUsers
        });
      }
    } catch (error) {
      console.error("Error exporting users:", error);
      res.status(500).json({ error: "Failed to export users" });
    }
  });



  app.get("/api/export/islands", isAuthenticated, async (req: any, res) => {
    try {
      const format = req.query.format || 'json';
      const userRole = req.user?.claims?.role || 'agent';

      // Only coordinators and admins can export data
      if (userRole === 'agent' || userRole === 'attachment') {
        return res.status(403).json({ error: "Insufficient permissions to export data" });
      }

      const allIslands = await storage.getIslands();

      if (format === 'csv') {
        const createCsvWriter = require('csv-writer').createObjectCsvWriter;

        const csvWriter = createCsvWriter({
          path: '/tmp/islands_export.csv',
          header: [
            {id: 'id', title: 'ID'},
            {id: 'name', title: 'Name'},
            {id: 'atoll', title: 'Atoll'},
            {id: 'latitude', title: 'Latitude'},
            {id: 'longitude', title: 'Longitude'},
            {id: 'isVisited', title: 'Visited'},
            {id: 'lastVisited', title: 'Last Visited'}
          ]
        });

        await csvWriter.writeRecords(allIslands);
        res.setHeader('Content-Type', 'text/csv');
        res.setHeader('Content-Disposition', 'attachment; filename="islands_export.csv"');
        res.sendFile('/tmp/islands_export.csv');
      } else {
        res.setHeader('Content-Type', 'application/json');
        res.setHeader('Content-Disposition', 'attachment; filename="islands_export.json"');
        res.json({
          exportDate: new Date().toISOString(),
          totalIslands: allIslands.length,
          data: allIslands
        });
      }
    } catch (error) {
      console.error("Error exporting islands:", error);
      res.status(500).json({ error: "Failed to export islands" });
    }
  });

  app.get("/api/export/equipment", isAuthenticated, async (req: any, res) => {
    try {
      const format = req.query.format || 'json';
      const userRole = req.user?.claims?.role || 'agent';

      // Only coordinators and admins can export data
      if (userRole === 'agent' || userRole === 'attachment') {
        return res.status(403).json({ error: "Insufficient permissions to export data" });
      }

      const allEquipment = await storage.getEquipment();

      if (format === 'csv') {
        const createCsvWriter = require('csv-writer').createObjectCsvWriter;

        const csvWriter = createCsvWriter({
          path: '/tmp/equipment_export.csv',
          header: [
            {id: 'id', title: 'ID'},
            {id: 'name', title: 'Name'},
            {id: 'type', title: 'Type'},
            {id: 'quantity', title: 'Quantity'},
            {id: 'status', title: 'Status'},
            {id: 'atoll', title: 'Atoll'},
            {id: 'island', title: 'Island'},
            {id: 'description', title: 'Description'},
            {id: 'condition', title: 'Condition'},
            {id: 'createdBy', title: 'Created By'},
            {id: 'createdAt', title: 'Created At'},
            {id: 'updatedAt', title: 'Updated At'}
          ]
        });

        await csvWriter.writeRecords(allEquipment);
        res.setHeader('Content-Type', 'text/csv');
        res.setHeader('Content-Disposition', 'attachment; filename="equipment_export.csv"');
        res.sendFile('/tmp/equipment_export.csv');
      } else {
        res.setHeader('Content-Type', 'application/json');
        res.setHeader('Content-Disposition', 'attachment; filename="equipment_export.json"');
        res.json({
          exportDate: new Date().toISOString(),
          totalEquipment: allEquipment.length,
          data: allEquipment
        });
      }
    } catch (error) {
      console.error("Error exporting equipment:", error);
      res.status(500).json({ error: "Failed to export equipment" });
    }
  });

  // Chat audio recording endpoints
  app.post("/api/chat/upload", isAuthenticated, requireWriteAccess, async (req: any, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error: any) {
      console.error('Error getting chat audio upload URL:', error);
      res.status(500).json({ error: 'Failed to get chat audio upload URL: ' + error.message });
    }
  });

  app.put("/api/chat/audio", isAuthenticated, async (req: any, res) => {
    if (!req.body.audioURL) {
      return res.status(400).json({ error: "audioURL is required" });
    }

    const userId = req.user.claims.sub;

    try {
      const objectStorageService = new ObjectStorageService();
      const audioPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.audioURL,
        {
          owner: userId,
          visibility: "public", // Chat audio messages are public for all team members
        },
      );


      res.status(200).json({
        audioPath: audioPath,
        message: "Chat audio message saved successfully"
      });
    } catch (error: any) {
      console.error("Error saving chat audio message:", error);
      res.status(500).json({ error: "Failed to save chat audio message: " + error.message });
    }
  });

  const httpServer = createServer(app);

  // Set up WebSocket server for chat
  const wss = new WebSocketServer({ server: httpServer, path: '/chat' });

  // Store connected clients
  const connectedClients = new Map<string, ConnectedClient>();

  wss.on('connection', (ws: WebSocket, req) => {

    ws.on('message', async (message: Buffer) => {
      try {
        const data = JSON.parse(message.toString());

        switch (data.type) {
          case 'join':
            // Add client to connected list
            connectedClients.set(data.clientId, {
              ws,
              userId: data.userId,
              userName: data.userName
            });

            // Load and send historical messages to the newly connected client
            try {
              const historicalMessages = await storage.getChatMessages(50); // Get last 50 messages
              // Reverse to show oldest first (database returns newest first)
              historicalMessages.reverse();

              for (const dbMessage of historicalMessages) {
                const messageType = dbMessage.isAudio ? 'audio-message' : 'text-message';
                const messageData: any = {
                  type: messageType,
                  messageId: dbMessage.id,
                  userId: dbMessage.userId,
                  userName: dbMessage.userName,
                  timestamp: dbMessage.createdAt!.toISOString()
                };

                if (dbMessage.isAudio) {
                  messageData.audioUrl = dbMessage.audioUrl;
                } else {
                  messageData.message = dbMessage.message;
                }

                // Send historical message only to the newly connected client
                if (ws.readyState === WebSocket.OPEN) {
                  ws.send(JSON.stringify(messageData));
                }
              }
            } catch (error) {
              console.error('Error loading historical messages:', error);
            }

            // Send current users list to all clients
            broadcastUserList();
            break;

          case 'subscribe-notifications':
            // Add notification listener without adding to user list
            connectedClients.set(data.clientId, {
              ws,
              userId: data.userId,
              userName: '[Notification Listener]',
              isNotificationOnly: true
            });
            // Don't broadcast user list or send history for notification-only connections
            break;

          case 'text-message':
            try {
              // Save text message to database
              const savedMessage = await storage.createChatMessage({
                userId: data.userId,
                userName: data.userName,
                message: data.message,
                isAudio: false
              });

              // Broadcast text message to all clients with database timestamp
              broadcast({
                type: 'text-message',
                messageId: savedMessage.id,
                userId: data.userId,
                userName: data.userName,
                message: data.message,
                timestamp: savedMessage.createdAt!.toISOString()
              });

              // Broadcast notification to all users except the sender
              broadcastNotification(data.userId, {
                type: 'chat-notification',
                from: data.userName,
                message: 'sent a message',
                timestamp: savedMessage.createdAt!.toISOString()
              });
            } catch (error) {
              console.error('Error saving text message to database:', error);
            }
            break;

          case 'audio-message':
            try {
              // Save audio message to database
              const savedMessage = await storage.createChatMessage({
                userId: data.userId,
                userName: data.userName,
                audioUrl: data.audioUrl,
                isAudio: true
              });

              // Broadcast audio message to all clients with database timestamp
              broadcast({
                type: 'audio-message',
                messageId: savedMessage.id,
                userId: data.userId,
                userName: data.userName,
                audioUrl: data.audioUrl,
                timestamp: savedMessage.createdAt!.toISOString()
              });

              // Broadcast notification to all users except the sender
              broadcastNotification(data.userId, {
                type: 'chat-notification',
                from: data.userName,
                message: 'sent an audio message',
                timestamp: savedMessage.createdAt!.toISOString()
              });
            } catch (error) {
              console.error('Error saving audio message to database:', error);
            }
            break;
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      // Remove client and update user list only if it wasn't notification-only
      for (const [clientId, client] of Array.from(connectedClients.entries())) {
        if (client.ws === ws) {
          const wasNotificationOnly = client.isNotificationOnly;
          connectedClients.delete(clientId);

          // Only broadcast user list if the disconnected client was not notification-only
          if (!wasNotificationOnly) {
            broadcastUserList();
          }
          break;
        }
      }
    });
  });

  function broadcast(message: any) {
    const messageStr = JSON.stringify(message);
    for (const [clientId, client] of Array.from(connectedClients.entries())) {
      if (client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(messageStr);
      }
    }
  }

  function broadcastUserList() {
    const usersList = Array.from(connectedClients.values())
      .filter(client => !client.isNotificationOnly)
      .map(client => ({
        userId: client.userId,
        userName: client.userName
      }));

    broadcast({
      type: 'user-list',
      users: usersList
    });
  }

  function broadcastNotification(excludeUserId: string, message: any) {
    const messageStr = JSON.stringify(message);
    for (const [clientId, client] of Array.from(connectedClients.entries())) {
      // Only send notification to users other than the sender
      if (client.ws.readyState === WebSocket.OPEN && client.userId !== excludeUserId) {
        client.ws.send(messageStr);
      }
    }
  }

  return httpServer;
}

