import {
  users,
  events,
  islands,
  eventParticipants,
  equipment,
  equipmentTransfers,
  equipmentAuditLog,
  chatMessages,
  userChatReadStatus,
  islandVisits,
  islandVisitEquipment,
  userMilestones,
  type User,
  type UpsertUser,
  type InsertUser,
  type Event,
  type InsertEvent,
  type Island,
  type InsertIsland,
  type EventParticipant,
  type Equipment,
  type InsertEquipment,
  type EquipmentTransfer,
  type InsertEquipmentTransfer,
  type EquipmentTransferWithUser,
  type EquipmentAuditLog,
  type InsertEquipmentAuditLog,
  type ChatMessage,
  type InsertChatMessage,
  type UserChatReadStatus,
  type InsertUserChatReadStatus,
  type IslandVisit,
  type InsertIslandVisit,
  type IslandVisitEquipment,
  type InsertIslandVisitEquipment,
  type UserMilestone,
  type InsertUserMilestone,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql, or, isNull, inArray } from "drizzle-orm";
import { findNearbyIslands } from "../shared/geolocation-utils";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Additional user operations
  getAllUsers(): Promise<User[]>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(userId: string, userData: Partial<InsertUser>): Promise<User | undefined>;
  updateUserRole(userId: string, role: string): Promise<User>;
  deleteUser(userId: string, deletingUserRole?: string): Promise<{ success: boolean; error?: string }>;

  // User approval operations
  approveUser(userId: string): Promise<User>;
  rejectUser(userId: string): Promise<User>;
  getUsersByApprovalStatus(status: string): Promise<User[]>;

  // Event operations
  getEvents(): Promise<Event[]>;
  getEvent(id: string): Promise<Event | undefined>;
  createEvent(event: InsertEvent): Promise<Event>;
  updateEvent(id: string, event: Partial<InsertEvent>): Promise<Event>;
  deleteEvent(id: string): Promise<void>;
  getEventsByStatus(status: string): Promise<Event[]>;
  getEventsByIsland(island: string): Promise<Event[]>;

  // Island operations
  getIslands(): Promise<Island[]>;
  getIsland(id: string): Promise<Island | undefined>;
  createIsland(island: InsertIsland): Promise<Island>;
  updateIsland(id: string, island: Partial<InsertIsland>): Promise<Island>;
  markIslandVisited(islandId: string, userId: string, visitType?: 'manual' | 'automatic'): Promise<Island>;
  markIslandVisitedWithEquipment(islandId: string, userId: string, equipmentIds: string[]): Promise<Island>;
  checkLocationAndMarkVisitedIslands(userLat: number, userLon: number, userId: string): Promise<{ visitedIslands: Array<{id: string, name: string, distance: number}>, newlyVisited: Array<{id: string, name: string}> }>;
  getIslandsWithUserVisitStatus(userId: string): Promise<Array<Island & { hasVisited: boolean }>>;

  // Event participant operations
  addEventParticipant(eventId: string, userId: string): Promise<EventParticipant>;
  removeEventParticipant(eventId: string, userId: string): Promise<void>;
  getEventParticipants(eventId: string): Promise<User[]>;

  // Event archive operations
  archiveEvent(id: string): Promise<Event>;
  getArchivedEvents(): Promise<Event[]>;

  // Equipment operations
  getEquipment(): Promise<Equipment[]>;
  getEquipmentByAtoll(atoll: string): Promise<Equipment[]>;
  getEquipmentByIsland(island: string): Promise<Equipment[]>;
  createEquipment(equipment: InsertEquipment): Promise<Equipment>;
  updateEquipment(id: string, equipment: Partial<InsertEquipment>): Promise<Equipment>;
  deleteEquipment(id: string): Promise<void>;
  getEquipmentByType(type: string): Promise<Equipment[]>;
  getEquipmentByStatus(status: string): Promise<Equipment[]>;

  // Equipment transfer operations
  transferEquipment(params: {
    sourceEquipmentId: string;
    quantityToMove: number;
    destinationAtoll: string;
    destinationIsland?: string;
    transferredBy: string;
    notes?: string;
  }): Promise<{ sourceEquipment: Equipment; destinationEquipment: Equipment; transfer: EquipmentTransfer }>;
  getEquipmentTransfers(equipmentId?: string): Promise<EquipmentTransfer[]>;
  getEquipmentTransfersWithUser(equipmentId?: string): Promise<EquipmentTransferWithUser[]>;

  // Chat message operations
  getChatMessages(limit?: number): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  deleteChatMessage(messageId: string): Promise<boolean>;
  deleteChatMessagesByUserId(userId: string): Promise<number>; // Returns count of deleted messages

  // Chat read status operations
  markMessagesAsRead(userId: string, lastReadMessageId: string): Promise<UserChatReadStatus>;
  getUnreadMessageCount(userId: string): Promise<number>;
  getUserChatReadStatus(userId: string): Promise<UserChatReadStatus | null>;

  // Milestone operations
  getUserMilestones(userId: string): Promise<UserMilestone[]>;
  checkAndUpdateMilestones(userId: string): Promise<UserMilestone[]>;
  createOrUpdateMilestone(milestone: InsertUserMilestone): Promise<UserMilestone>;
  getUserIslandVisitHistory(userId: string): Promise<Array<IslandVisit & { island: Island; equipment: Equipment[] }>>;
  getUserVisitStats(userId: string): Promise<{ totalIslands: number; totalVisits: number; uniqueAtolls: number }>;
  getIslandVisitHistory(islandId: string, userId: string): Promise<Array<IslandVisit & { equipmentUsed: string[] }>>;

  // Admin milestone operations
  clearAllUserMilestones(): Promise<number>;
  clearUserMilestones(userId: string): Promise<number>;

}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));

    // Normalize legacy "member" role to "agent"
    if (user && (user.role as any) === 'member') {
      console.log(`Normalizing legacy role 'member' to 'agent' for user ${user.id}`);
      return await this.updateUserRole(user.id, 'agent');
    }

    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    // Ensure user ID is provided
    if (!userData.id) {
      throw new Error('User ID is required for upsert operation');
    }

    try {
      // Check if user exists by ID first
      const existingUserById = await this.getUser(userData.id);

      if (existingUserById) {
        // User exists by ID - update safe fields, preserve role and approvalStatus
        const [user] = await db
          .update(users)
          .set({
            email: userData.email,
            firstName: userData.firstName,
            lastName: userData.lastName,
            profileImageUrl: userData.profileImageUrl,
            updatedAt: new Date(),
          })
          .where(eq(users.id, userData.id))
          .returning();
        return user;
      }

      // Check if user exists by email (different ID, same email)
      if (userData.email) {
        const [existingUserByEmail] = await db
          .select()
          .from(users)
          .where(eq(users.email, userData.email))
          .limit(1);

        if (existingUserByEmail) {
          // Same email exists with different ID - update the existing record with new ID and data
          const [user] = await db
            .update(users)
            .set({
              id: userData.id, // Update to new ID from OIDC
              firstName: userData.firstName,
              lastName: userData.lastName,
              profileImageUrl: userData.profileImageUrl,
              updatedAt: new Date(),
            })
            .where(eq(users.email, userData.email))
            .returning();
          return user;
        }
      }

      // New user - set defaults with optional admin auto-approval (development only)
      const isAdminUser = this.shouldBeAdmin(userData);

      const newUserData = {
        ...userData,
        // Only set defaults for new users
        role: isAdminUser ? 'admin' : 'agent',
        approvalStatus: isAdminUser ? 'approved' : 'pending',
      };

      const [user] = await db
        .insert(users)
        .values(newUserData)
        .returning();
      return user;
    } catch (error: any) {
      // Handle unique constraint violations more gracefully
      if (error.code === '23505' && error.constraint === 'users_email_unique' && userData.email) {
        console.log(`Email conflict detected for ${userData.email}, attempting to resolve...`);

        // Find the existing user by email and update their ID
        const [existingUser] = await db
          .select()
          .from(users)
          .where(eq(users.email, userData.email))
          .limit(1);

        if (existingUser) {
          const [user] = await db
            .update(users)
            .set({
              id: userData.id,
              firstName: userData.firstName,
              lastName: userData.lastName,
              profileImageUrl: userData.profileImageUrl,
              updatedAt: new Date(),
            })
            .where(eq(users.email, userData.email))
            .returning();
          return user;
        }
      }

      console.error('Error in upsertUser:', error);
      throw error;
    }
  }

  // Helper method to determine if user should be admin (DEVELOPMENT ONLY)
  private shouldBeAdmin(userData: UpsertUser): boolean {
    // Only enable auto-admin in development mode
    if (process.env.NODE_ENV === 'production') {
      return false;
    }

    // Use environment-based admin allowlist if available
    const adminEmails = process.env.ADMIN_EMAILS?.split(',') || [];

    if (!userData.email) return false;

    // Check for explicit admin emails from environment
    return adminEmails.includes(userData.email);
  }

