  async deleteUser(userId: string, deletingUserRole?: string): Promise<{ success: boolean; error?: string }> {
    try {
      // Get the user to be deleted to check their role
      const userToDelete = await this.getUser(userId);
      if (!userToDelete) {
        return { success: false, error: "User not found" };
      }

      // Prevent coordinator from deleting admins
      if (deletingUserRole === 'coordinator' && userToDelete.role === 'admin') {
        return { success: false, error: "Coordinators cannot delete admin users" };
      }

      // Check if this is the last admin
      if (userToDelete.role === 'admin') {
        const adminCount = await db
          .select({ count: sql<number>`count(*)` })
          .from(users)
          .where(eq(users.role, 'admin'));

        if (adminCount[0]?.count <= 1) {
          return { success: false, error: "Cannot delete the last admin user" };
        }
      }

      // Delete the user - foreign key cascades will handle related data
      await db.transaction(async (tx) => {
        await tx.delete(users).where(eq(users.id, userId));
      });

      return { success: true };
    } catch (error) {
      console.error('Error deleting user and related data:', error);
      return { success: false, error: error instanceof Error ? error.message : "Failed to delete user" };
    }
  }

