  async markIslandVisited(islandId: string, userId: string, visitType: 'manual' | 'automatic' = 'automatic'): Promise<Island> {
    // Check if user has already visited this island
    const existingVisit = await db
      .select()
      .from(islandVisits)
      .where(and(
        eq(islandVisits.islandId, islandId),
        eq(islandVisits.userId, userId)
      ))
      .limit(1);

    // If not already visited, create a visit record
    if (existingVisit.length === 0) {
      await db
        .insert(islandVisits)
        .values({
          islandId,
          userId,
          visitType,
          visitedAt: new Date()
        });
    }

    // Return the island info (without modifying global isVisited)
    const [island] = await db
      .select()
      .from(islands)
      .where(eq(islands.id, islandId))
      .limit(1);

    if (!island) {
      throw new Error(`Island with id ${islandId} not found`);
    }

    return island;
  }

  async markIslandVisitedWithEquipment(islandId: string, userId: string, equipmentIds: string[]): Promise<Island & { visitId?: string }> {
    const result = await db.transaction(async (tx) => {
      // Track destination equipment for visit linkage (declare at transaction scope)
      const destinationEquipmentIds: string[] = [];

      // First, verify the island exists
      const existingIsland = await tx
        .select()
        .from(islands)
        .where(eq(islands.id, islandId))
        .limit(1);

      if (existingIsland.length === 0) {
        throw new Error(`Island with id ${islandId} not found`);
      }

      const island = existingIsland[0];

      // Verify user exists
      const existingUser = await tx
        .select()
        .from(users)
        .where(eq(users.id, userId))
        .limit(1);

      if (existingUser.length === 0) {
        throw new Error(`User with id ${userId} not found`);
      }

      // If equipment IDs provided, verify they exist, are available, and have quantity > 0
      if (equipmentIds.length > 0) {
        const existingEquipment = await tx
          .select()
          .from(equipment)
          .where(and(
            inArray(equipment.id, equipmentIds),
            eq(equipment.status, 'available'),
            sql`${equipment.quantity} > 0`
          ));

        if (existingEquipment.length !== equipmentIds.length) {
          throw new Error(`Some equipment items are not found, not available, or have no quantity`);
        }

        // Transfer each selected equipment (1 unit each) to the island
        for (const selectedEquipment of existingEquipment) {
          // Atomic destination upsert: increment existing or create new
          const [destinationEquipment] = await tx
            .insert(equipment)
            .values({
              name: selectedEquipment.name,
              type: selectedEquipment.type,
              quantity: 1,
              status: selectedEquipment.status,
              atoll: island.atoll,
              island: island.name,
              description: selectedEquipment.description,
              condition: selectedEquipment.condition,
              parentEquipmentId: selectedEquipment.id,
              createdBy: userId,
            })
            .onConflictDoUpdate({
              target: [equipment.name, equipment.type, equipment.atoll, equipment.island],
              set: {
                quantity: sql`${equipment.quantity} + 1`,
                updatedAt: new Date()
              }
            })
            .returning();

          // Store destination equipment ID for visit linkage
          destinationEquipmentIds.push(destinationEquipment.id);

          // Create transfer record
          await tx
            .insert(equipmentTransfers)
            .values({
              sourceEquipmentId: selectedEquipment.id,
              destinationEquipmentId: destinationEquipment.id,
              quantityMoved: 1,
              fromAtoll: selectedEquipment.atoll,
              fromIsland: selectedEquipment.island,
              toAtoll: island.atoll,
              toIsland: island.name,
              transferredBy: userId,
              notes: `Equipment transferred during island check-in`,
            });

          // Atomic source decrement with guard
          const [updatedSource] = await tx
            .update(equipment)
            .set({ 
              quantity: sql`${equipment.quantity} - 1`,
              updatedAt: new Date()
            })
            .where(and(
              eq(equipment.id, selectedEquipment.id),
              sql`${equipment.quantity} > 0`
            ))
            .returning();

          if (!updatedSource) {
            throw new Error(`Equipment ${selectedEquipment.name} has insufficient quantity or was modified by another transaction`);
          }

          // Keep equipment record even when quantity becomes 0 to preserve historical visit data
          // This prevents foreign key constraint violations from islandVisitEquipment references
        }
      }

      // Get the island info (without marking it globally as visited)
      const [updatedIsland] = await tx
        .select()
        .from(islands)
        .where(eq(islands.id, islandId));

      // Create visit record
      const [visit] = await tx
        .insert(islandVisits)
        .values({
          islandId,
          userId,
          visitType: 'manual',
        })
        .returning();

      // Link destination equipment to the visit (equipment that was transferred to this island)
      if (equipmentIds.length > 0 && destinationEquipmentIds.length > 0) {
        // Dedupe destination equipment IDs to prevent duplicate visit-equipment links
        const uniqueDestinationIds = Array.from(new Set(destinationEquipmentIds));
        const equipmentVisitRecords = uniqueDestinationIds.map(equipmentId => ({
          visitId: visit.id,
          equipmentId,
        }));

        await tx
          .insert(islandVisitEquipment)
          .values(equipmentVisitRecords);
      }

      console.log(`Island visit recorded: User ${userId} visited ${updatedIsland.name} and transferred ${equipmentIds.length} equipment items`);

      return { ...updatedIsland, visitId: visit.id };
    });

    // Check and update milestones after successful visit (outside transaction)
    try {
      const newMilestones = await this.checkAndUpdateMilestones(userId);
      if (newMilestones.length > 0) {
        console.log(`New milestones achieved by user ${userId}:`, newMilestones.map(m => m.milestoneName).join(', '));
      }
    } catch (error) {
      console.error('Error checking milestones after island visit:', error);
      // Don't fail the visit if milestone checking fails
    }

    return result;
  }

  async checkLocationAndMarkVisitedIslands(userLat: number, userLon: number, userId: string): Promise<{ visitedIslands: Array<{id: string, name: string, distance: number}>, newlyVisited: Array<{id: string, name: string}> }> {
    // Get all islands with coordinates
    const allIslands = await this.getIslands();

    // Find nearby islands within visiting distance (500m)
    const nearbyIslands = findNearbyIslands(userLat, userLon, allIslands);

    const newlyVisited: Array<{id: string, name: string}> = [];

    // Get user's visit history to check if they've already visited nearby islands
    const userVisits = await db
      .select()
      .from(islandVisits)
      .where(eq(islandVisits.userId, userId));

    const visitedIslandIds = new Set(userVisits.map(visit => visit.islandId));

    // Mark nearby islands as visited if not already visited by this user
    for (const nearbyIsland of nearbyIslands) {
      if (!visitedIslandIds.has(nearbyIsland.id)) {
        await this.markIslandVisited(nearbyIsland.id, userId, 'automatic');
        newlyVisited.push({ id: nearbyIsland.id, name: nearbyIsland.name });
      }
    }

    return {
      visitedIslands: nearbyIslands,
      newlyVisited
    };
  }

