  async updateEquipmentWithTransferTracking(id: string, equipmentData: Partial<InsertEquipment>, updatedBy: string): Promise<Equipment> {
    return await db.transaction(async (tx) => {
      // Get current equipment state
      const [currentEquipment] = await tx
        .select()
        .from(equipment)
        .where(eq(equipment.id, id));

      if (!currentEquipment) {
        throw new Error("Equipment not found");
      }

      // Track all field changes for audit log
      const fieldsToAudit = ['name', 'type', 'quantity', 'status', 'atoll', 'island', 'description', 'condition'];
      const auditEntries: any[] = [];

      for (const field of fieldsToAudit) {
        if (equipmentData[field as keyof typeof equipmentData] !== undefined) {
          const oldValue = currentEquipment[field as keyof typeof currentEquipment];
          const newValue = equipmentData[field as keyof typeof equipmentData];

          if (oldValue !== newValue) {
            auditEntries.push({
              equipmentId: id,
              action: 'updated',
              fieldName: field,
              oldValue: oldValue?.toString() || null,
              newValue: newValue?.toString() || null,
              changedBy: updatedBy,
              notes: `${field} changed from "${oldValue}" to "${newValue}"`
            });
          }
        }
      }

      // Check if location is changing (for transfer record)
      const locationChanged = 
        (equipmentData.atoll && equipmentData.atoll !== currentEquipment.atoll) ||
        (equipmentData.island !== undefined && equipmentData.island !== currentEquipment.island);

      // Update the equipment
      const [updatedEquipment] = await tx
        .update(equipment)
        .set({ ...equipmentData, updatedAt: new Date() })
        .where(eq(equipment.id, id))
        .returning();

      // Create audit log entries for all changes
      if (auditEntries.length > 0) {
        await tx.insert(equipmentAuditLog).values(auditEntries);
      }

      // If location changed, also create transfer record
      if (locationChanged) {
        const newAtoll = equipmentData.atoll || currentEquipment.atoll;
        const newIsland = equipmentData.island !== undefined ? equipmentData.island : currentEquipment.island;

        await tx.insert(equipmentTransfers).values({
          sourceEquipmentId: id,
          destinationEquipmentId: id, // Same equipment, just moved
          quantityMoved: currentEquipment.quantity,
          fromAtoll: currentEquipment.atoll,
          fromIsland: currentEquipment.island,
          toAtoll: newAtoll,
          toIsland: newIsland,
          transferredBy: updatedBy,
          notes: `Equipment location updated via direct edit`
        });
      }

      return updatedEquipment;
    });
  }

  // Equipment transfer operations for complex quantity tracking
  async transferEquipment(params: {
    sourceEquipmentId: string;
    quantityToMove: number;
    destinationAtoll: string;
    destinationIsland?: string;
    transferredBy: string;
    notes?: string;
  }): Promise<{ sourceEquipment: Equipment; destinationEquipment: Equipment; transfer: EquipmentTransfer }> {
    const { sourceEquipmentId, quantityToMove, destinationAtoll, destinationIsland, transferredBy, notes } = params;

    return await db.transaction(async (tx) => {
      // Get the source equipment
      const [sourceEquipment] = await tx
        .select()
        .from(equipment)
        .where(eq(equipment.id, sourceEquipmentId));

      if (!sourceEquipment) {
        throw new Error("Source equipment not found");
      }

      if (sourceEquipment.quantity < quantityToMove) {
        throw new Error("Insufficient quantity to transfer");
      }

      // Check if destination already has same equipment type
      const [existingDestination] = await tx
        .select()
        .from(equipment)
        .where(and(
          eq(equipment.name, sourceEquipment.name),
          eq(equipment.type, sourceEquipment.type),
          eq(equipment.atoll, destinationAtoll),
          destinationIsland ? eq(equipment.island, destinationIsland) : isNull(equipment.island)
        ));

      let destinationEquipment: Equipment;

      if (existingDestination) {
        // Add to existing equipment at destination
        const [updated] = await tx
          .update(equipment)
          .set({ 
            quantity: existingDestination.quantity + quantityToMove,
            updatedAt: new Date()
          })
          .where(eq(equipment.id, existingDestination.id))
          .returning();
        destinationEquipment = updated;
      } else {
        // Create new equipment record at destination
        const [created] = await tx
          .insert(equipment)
          .values({
            name: sourceEquipment.name,
            type: sourceEquipment.type,
            quantity: quantityToMove,
            status: sourceEquipment.status,
            atoll: destinationAtoll,
            island: destinationIsland || null,
            description: sourceEquipment.description,
            condition: sourceEquipment.condition,
            parentEquipmentId: sourceEquipmentId,
            createdBy: transferredBy,
          })
          .returning();
        destinationEquipment = created;
      }

      // Create transfer record BEFORE updating/deleting source equipment
      const [transfer] = await tx
        .insert(equipmentTransfers)
        .values({
          sourceEquipmentId: sourceEquipmentId,
          destinationEquipmentId: destinationEquipment.id,
          quantityMoved: quantityToMove,
          fromAtoll: sourceEquipment.atoll,
          fromIsland: sourceEquipment.island,
          toAtoll: destinationAtoll,
          toIsland: destinationIsland,
          transferredBy,
          notes,
        })
        .returning();

      // Update source equipment quantity
      const [updatedSource] = await tx
        .update(equipment)
        .set({ 
          quantity: sourceEquipment.quantity - quantityToMove,
          updatedAt: new Date()
        })
        .where(eq(equipment.id, sourceEquipmentId))
        .returning();

      // Keep equipment record even when quantity becomes 0 to preserve historical data
      // This prevents foreign key constraint violations from islandVisitEquipment references

      return {
        sourceEquipment: updatedSource,
        destinationEquipment,
        transfer
      };
    });
  }

  async getEquipmentTransfers(equipmentId?: string): Promise<EquipmentTransfer[]> {
    const query = db
      .select()
      .from(equipmentTransfers)
      .orderBy(desc(equipmentTransfers.transferredAt));

    if (equipmentId) {
      return await query.where(
        or(
          eq(equipmentTransfers.sourceEquipmentId, equipmentId),
          eq(equipmentTransfers.destinationEquipmentId, equipmentId)
        )
      );
    }

    return await query;
  }

  async getEquipmentTransfersWithUser(equipmentId?: string): Promise<EquipmentTransferWithUser[]> {
    const query = db
      .select({
        id: equipmentTransfers.id,
        sourceEquipmentId: equipmentTransfers.sourceEquipmentId,
        destinationEquipmentId: equipmentTransfers.destinationEquipmentId,
        quantityMoved: equipmentTransfers.quantityMoved,
        fromAtoll: equipmentTransfers.fromAtoll,
        fromIsland: equipmentTransfers.fromIsland,
        toAtoll: equipmentTransfers.toAtoll,
        toIsland: equipmentTransfers.toIsland,
        transferredBy: equipmentTransfers.transferredBy,
        transferredAt: equipmentTransfers.transferredAt,
        notes: equipmentTransfers.notes,
        transferredByUser: {
          firstName: users.firstName,
          lastName: users.lastName,
          email: users.email,
          serviceNumber: users.serviceNumber,
        },
      })
      .from(equipmentTransfers)
      .innerJoin(users, eq(equipmentTransfers.transferredBy, users.id))
      .orderBy(desc(equipmentTransfers.transferredAt));

    if (equipmentId) {
      return await query.where(
        or(
          eq(equipmentTransfers.sourceEquipmentId, equipmentId),
          eq(equipmentTransfers.destinationEquipmentId, equipmentId)
        )
      );
    }

    return await query;
  }

  async deleteEquipment(id: string): Promise<void> {
    // With CASCADE DELETE on foreign keys, we can now simply delete the equipment
    // and all related transfer records will be deleted automatically
    await db.delete(equipment).where(eq(equipment.id, id));
  }

  async getEquipmentByType(type: string): Promise<Equipment[]> {
    return await db
      .select()
      .from(equipment)
      .where(eq(equipment.type, type))
      .orderBy(equipment.name);
  }

  async getEquipmentByStatus(status: string): Promise<Equipment[]> {
    return await db
      .select()
      .from(equipment)
      .where(eq(equipment.status, status))
      .orderBy(equipment.name);
  }

  // Chat message operations
  async getChatMessages(limit = 100): Promise<ChatMessage[]> {
    return await db
      .select()
      .from(chatMessages)
      .orderBy(desc(chatMessages.createdAt))
      .limit(limit);
  }

  async createChatMessage(messageData: InsertChatMessage): Promise<ChatMessage> {
    const [message] = await db
      .insert(chatMessages)
      .values(messageData)
      .returning();
    return message;
  }

  async deleteChatMessage(messageId: string): Promise<boolean> {
    const result = await db
      .delete(chatMessages)
      .where(eq(chatMessages.id, messageId))
      .returning();
    return result.length > 0;
  }

  async deleteChatMessagesByUserId(userId: string): Promise<number> {
    const result = await db
      .delete(chatMessages)
      .where(eq(chatMessages.userId, userId))
      .returning();
    return result.length;
  }

  // Chat read status operations
  async markMessagesAsRead(userId: string, lastReadMessageId: string): Promise<UserChatReadStatus> {
    // Get the creation timestamp of the message being marked as read
    const [message] = await db
      .select({ createdAt: chatMessages.createdAt })
      .from(chatMessages)
      .where(eq(chatMessages.id, lastReadMessageId))
      .limit(1);

    if (!message) {
      throw new Error(`Message with ID ${lastReadMessageId} not found`);
    }

    const newCreatedAt = message.createdAt!;

    // Use atomic upsert with conditional update to ensure monotonicity at database level
    const [readStatus] = await db
      .insert(userChatReadStatus)
      .values({
        userId,
        lastReadMessageId,
        lastReadCreatedAt: newCreatedAt,
        lastReadAt: new Date(),
      })
      .onConflictDoUpdate({
        target: [userChatReadStatus.userId],
        set: {
          lastReadCreatedAt: sql`CASE WHEN ${userChatReadStatus.lastReadCreatedAt} IS NULL OR EXCLUDED.last_read_created_at > ${userChatReadStatus.lastReadCreatedAt} THEN EXCLUDED.last_read_created_at ELSE ${userChatReadStatus.lastReadCreatedAt} END`,
          lastReadMessageId: sql`CASE WHEN ${userChatReadStatus.lastReadCreatedAt} IS NULL OR EXCLUDED.last_read_created_at > ${userChatReadStatus.lastReadCreatedAt} THEN EXCLUDED.last_read_message_id ELSE ${userChatReadStatus.lastReadMessageId} END`,
          lastReadAt: sql`CASE WHEN ${userChatReadStatus.lastReadCreatedAt} IS NULL OR EXCLUDED.last_read_created_at > ${userChatReadStatus.lastReadCreatedAt} THEN now() ELSE ${userChatReadStatus.lastReadAt} END`,
        }
      })
      .returning();

    return readStatus;
  }

  async getUnreadMessageCount(userId: string): Promise<number> {
    // Get user's last read timestamp
    const [readStatus] = await db
      .select()
      .from(userChatReadStatus)
      .where(eq(userChatReadStatus.userId, userId))
      .limit(1);

    if (!readStatus?.lastReadCreatedAt) {
      // User has never read any messages, count all messages except their own
      const [result] = await db
        .select({ count: sql<number>`count(*)` })
        .from(chatMessages)
        .where(sql`${chatMessages.userId} != ${userId}`);
      return result?.count || 0;
    }

    // Count messages newer than the last read timestamp, excluding user's own messages
    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(chatMessages)
      .where(
        and(
          sql`${chatMessages.createdAt} > ${readStatus.lastReadCreatedAt}`,
          sql`${chatMessages.userId} != ${userId}`
        )
      );

    return result?.count || 0;
  }

  async getUserChatReadStatus(userId: string): Promise<UserChatReadStatus | null> {
    const [readStatus] = await db
      .select()
      .from(userChatReadStatus)
      .where(eq(userChatReadStatus.userId, userId))
      .limit(1);

    return readStatus || null;
  }

  // Equipment audit log operations
  async getEquipmentAuditLogs(equipmentId?: string): Promise<EquipmentAuditLog[]> {
    const query = db
      .select()
      .from(equipmentAuditLog)
      .orderBy(desc(equipmentAuditLog.changedAt));

    if (equipmentId) {
      return await query.where(eq(equipmentAuditLog.equipmentId, equipmentId));
    }

    return await query;
  }

  async getEquipmentAuditLogsWithUser(equipmentId?: string) {
    const baseQuery = db
      .select({
        id: equipmentAuditLog.id,
        equipmentId: equipmentAuditLog.equipmentId,
        action: equipmentAuditLog.action,
        fieldName: equipmentAuditLog.fieldName,
        oldValue: equipmentAuditLog.oldValue,
        newValue: equipmentAuditLog.newValue,
        changedBy: equipmentAuditLog.changedBy,
        changedAt: equipmentAuditLog.changedAt,
        notes: equipmentAuditLog.notes,
        changedByUser: {
          firstName: users.firstName,
          lastName: users.lastName,
          email: users.email,
          serviceNumber: users.serviceNumber,
        }
      })
      .from(equipmentAuditLog)
      .innerJoin(users, eq(equipmentAuditLog.changedBy, users.id))
      .orderBy(desc(equipmentAuditLog.changedAt));

    if (equipmentId) {
      return await baseQuery.where(eq(equipmentAuditLog.equipmentId, equipmentId));
    }

    return await baseQuery;
  }

  // Milestone operations
  async getUserMilestones(userId: string): Promise<UserMilestone[]> {
    return await db
      .select()
      .from(userMilestones)
      .where(eq(userMilestones.userId, userId))
      .orderBy(desc(userMilestones.achievedAt));
  }

  async createOrUpdateMilestone(milestone: InsertUserMilestone): Promise<UserMilestone> {
    // Use atomic upsert to prevent race conditions
    const [result] = await db
      .insert(userMilestones)
      .values({
        ...milestone,
        achievedAt: milestone.progress >= milestone.targetValue ? new Date() : null
      })
      .onConflictDoUpdate({
        target: [userMilestones.userId, userMilestones.milestoneType, userMilestones.milestoneLevel],
        set: {
          progress: sql`GREATEST(${userMilestones.progress}, ${milestone.progress})`,
          achievedAt: milestone.progress >= milestone.targetValue ? new Date() : sql`${userMilestones.achievedAt}`,
          description: milestone.description,
          badgeIcon: milestone.badgeIcon,
          badgeColor: milestone.badgeColor,
          targetValue: milestone.targetValue
        }
      })
      .returning();

    return result;
  }

  async getUserVisitStats(userId: string): Promise<{ totalIslands: number; totalVisits: number; uniqueAtolls: number }> {
    // Get total visits count
    const totalVisitsResult = await db
      .select({ count: sql<number>`count(*)` })
      .from(islandVisits)
      .where(eq(islandVisits.userId, userId));

    // Get unique islands count
    const uniqueIslandsResult = await db
      .select({ count: sql<number>`count(distinct ${islandVisits.islandId})` })
      .from(islandVisits)
      .where(eq(islandVisits.userId, userId));

    // Get unique atolls count
    const uniqueAtollsResult = await db
      .select({ count: sql<number>`count(distinct ${islands.atoll})` })
      .from(islandVisits)
      .innerJoin(islands, eq(islandVisits.islandId, islands.id))
      .where(eq(islandVisits.userId, userId));

    return {
      totalVisits: totalVisitsResult[0]?.count || 0,
      totalIslands: uniqueIslandsResult[0]?.count || 0,
      uniqueAtolls: uniqueAtollsResult[0]?.count || 0
    };
  }

  async getUserIslandVisitHistory(userId: string): Promise<Array<IslandVisit & { island: Island; equipment: Equipment[] }>> {
    // Get visits with island details
    const visits = await db
      .select({
        id: islandVisits.id,
        islandId: islandVisits.islandId,
        userId: islandVisits.userId,
        visitedAt: islandVisits.visitedAt,
        visitType: islandVisits.visitType,
        notes: islandVisits.notes,
        island: {
          id: islands.id,
          name: islands.name,
          atoll: islands.atoll,
          latitude: islands.latitude,
          longitude: islands.longitude,
          isVisited: islands.isVisited,
          lastVisited: islands.lastVisited,
        }
      })
      .from(islandVisits)
      .innerJoin(islands, eq(islandVisits.islandId, islands.id))
      .where(eq(islandVisits.userId, userId))
      .orderBy(desc(islandVisits.visitedAt));

    // Get equipment for each visit
    const visitIds = visits.map(v => v.id);
    const visitEquipment = visitIds.length > 0 ? await db
      .select({
        visitId: islandVisitEquipment.visitId,
        equipment: {
          id: equipment.id,
          name: equipment.name,
          type: equipment.type,
          quantity: equipment.quantity,
          status: equipment.status,
          atoll: equipment.atoll,
          island: equipment.island,
          description: equipment.description,
          condition: equipment.condition,
          parentEquipmentId: equipment.parentEquipmentId,
          createdBy: equipment.createdBy,
          createdAt: equipment.createdAt,
          updatedAt: equipment.updatedAt,
        }
      })
      .from(islandVisitEquipment)
      .innerJoin(equipment, eq(islandVisitEquipment.equipmentId, equipment.id))
      .where(inArray(islandVisitEquipment.visitId, visitIds)) : [];

    // Group equipment by visit ID
    const equipmentByVisit: Record<string, Equipment[]> = {};
    visitEquipment.forEach(ve => {
      if (!equipmentByVisit[ve.visitId]) {
        equipmentByVisit[ve.visitId] = [];
      }
      equipmentByVisit[ve.visitId].push(ve.equipment);
    });

    // Combine visits with their equipment
    return visits.map(visit => ({
      ...visit,
      equipment: equipmentByVisit[visit.id] || []
    }));
  }

  async getIslandVisitHistory(islandId: string, userId: string): Promise<Array<IslandVisit & { equipmentUsed: string[] }>> {
    // Get visits for the specific island and user
    const visits = await db
      .select({
        id: islandVisits.id,
        islandId: islandVisits.islandId,
        userId: islandVisits.userId,
        visitedAt: islandVisits.visitedAt,
        visitType: islandVisits.visitType,
        notes: islandVisits.notes,
      })
      .from(islandVisits)
      .where(and(
        eq(islandVisits.islandId, islandId),
        eq(islandVisits.userId, userId)
      ))
      .orderBy(desc(islandVisits.visitedAt));

    // Get equipment for each visit
    const visitIds = visits.map(v => v.id);
    const visitEquipment = visitIds.length > 0 ? await db
      .select({
        visitId: islandVisitEquipment.visitId,
        equipmentName: equipment.name,
      })
      .from(islandVisitEquipment)
      .innerJoin(equipment, eq(islandVisitEquipment.equipmentId, equipment.id))
      .where(inArray(islandVisitEquipment.visitId, visitIds)) : [];

    // Group equipment names by visit ID
    const equipmentByVisit: Record<string, string[]> = {};
    visitEquipment.forEach(ve => {
      if (!equipmentByVisit[ve.visitId]) {
        equipmentByVisit[ve.visitId] = [];
      }
      equipmentByVisit[ve.visitId].push(ve.equipmentName);
    });

    // Combine visits with their equipment names
    return visits.map(visit => ({
      ...visit,
      equipmentUsed: equipmentByVisit[visit.id] || []
    }));
  }

  async checkAndUpdateMilestones(userId: string): Promise<UserMilestone[]> {
    const stats = await this.getUserVisitStats(userId);

    // Define milestone definitions - adjusted for 198 total islands and 20 total atolls
    const milestoneDefinitions = [
      // Island visit milestones (198 total islands)
      { type: "island_visits", name: "First Step", level: 1, target: 1, description: "Visit your first island", icon: "ðŸï¸", color: "#22C55E" },
      { type: "island_visits", name: "Explorer", level: 3, target: 3, description: "Visit 3 different islands", icon: "ðŸ—ºï¸", color: "#3B82F6" },
      { type: "island_visits", name: "Adventurer", level: 5, target: 5, description: "Visit 5 different islands", icon: "ðŸ§­", color: "#8B5CF6" },
      { type: "island_visits", name: "Navigator", level: 10, target: 10, description: "Visit 10 different islands", icon: "â›µ", color: "#10B981" },
      { type: "island_visits", name: "Island Hopper", level: 20, target: 20, description: "Visit 20 different islands", icon: "ðŸ„", color: "#F59E0B" },
      { type: "island_visits", name: "Seasoned Traveler", level: 35, target: 35, description: "Visit 35 different islands", icon: "ðŸŽ’", color: "#EF4444" },
      { type: "island_visits", name: "Island Master", level: 55, target: 55, description: "Visit 55 different islands", icon: "ðŸ†", color: "#DC2626" },
      { type: "island_visits", name: "Expert Explorer", level: 80, target: 80, description: "Visit 80 different islands", icon: "â­", color: "#9333EA" },
      { type: "island_visits", name: "Archipelago Legend", level: 120, target: 120, description: "Visit 120 different islands", icon: "ðŸ‘‘", color: "#DB2777" },
      { type: "island_visits", name: "Ultimate Explorer", level: 170, target: 170, description: "Visit 170 different islands", icon: "ðŸ’Ž", color: "#059669" },
      { type: "island_visits", name: "Completionist", level: 198, target: 198, description: "Visit all 198 islands in the Maldives", icon: "ðŸŒŸ", color: "#DC2626" },

      // Atoll exploration milestones (20 total atolls)
      { type: "atolls_visited", name: "Atoll Starter", level: 1, target: 1, description: "Visit islands in your first atoll", icon: "ðŸŒŠ", color: "#06B6D4" },
      { type: "atolls_visited", name: "Atoll Explorer", level: 3, target: 3, description: "Visit islands in 3 different atolls", icon: "ðŸŒ´", color: "#10B981" },
      { type: "atolls_visited", name: "Atoll Navigator", level: 5, target: 5, description: "Visit islands in 5 different atolls", icon: "ðŸ§­", color: "#8B5CF6" },
      { type: "atolls_visited", name: "Atoll Master", level: 8, target: 8, description: "Visit islands in 8 different atolls", icon: "ðŸï¸", color: "#F59E0B" },
      { type: "atolls_visited", name: "Atoll Expert", level: 12, target: 12, description: "Visit islands in 12 different atolls", icon: "â›µ", color: "#EF4444" },
      { type: "atolls_visited", name: "Maldives Explorer", level: 16, target: 16, description: "Visit islands in 16 different atolls", icon: "ðŸ—ºï¸", color: "#DC2626" },
      { type: "atolls_visited", name: "Atoll Legend", level: 20, target: 20, description: "Visit islands in all 20 atolls", icon: "ðŸ‘‘", color: "#9333EA" },
    ].sort((a, b) => a.target - b.target); // Sort by target from low to high

    const newMilestones: UserMilestone[] = [];

    for (const def of milestoneDefinitions) {
      const progress = def.type === "island_visits" ? stats.totalIslands : stats.uniqueAtolls;

      // Create or update milestone
      const milestone = await this.createOrUpdateMilestone({
        userId,
        milestoneType: def.type as "island_visits" | "atolls_visited",
        milestoneName: def.name,
        milestoneLevel: def.level,
        description: def.description,
        badgeIcon: def.icon,
        badgeColor: def.color,
        progress,
        targetValue: def.target
      });

      // If milestone was just achieved, add to new milestones
      if (progress >= def.target && milestone.achievedAt) {
        newMilestones.push(milestone);
      }
    }

    return newMilestones;
  }

  // Admin milestone operations
  async clearAllUserMilestones(): Promise<number> {
    const result = await db.delete(userMilestones);
    return result.rowCount || 0;
  }

  async clearUserMilestones(userId: string): Promise<number> {
    const result = await db
      .delete(userMilestones)
      .where(eq(userMilestones.userId, userId));
    return result.rowCount || 0;
  }

}

export const storage = new DatabaseStorage();

