import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  uniqueIndex,
  jsonb,
  pgTable,
  timestamp,
  varchar,
  text,
  integer,
  boolean,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table (required for Replit Auth)
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table (required for Replit Auth)
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  serviceNumber: varchar("service_number").unique(),
  mobile: varchar("mobile"),
  role: varchar("role").default("agent").notNull(), // agent, coordinator, admin, attachment
  isActive: boolean("is_active").default(true).notNull(),
  approvalStatus: varchar("approval_status").default("pending").notNull(), // pending, approved, rejected
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Events table
export const events = pgTable("events", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: varchar("title").notNull(),
  island: varchar("island").notNull(),
  atoll: varchar("atoll").notNull(),
  eventLocation: text("event_location"),
  waitingLocation: text("waiting_location"),
  contact: varchar("contact"),
  comment: text("comment"),
  photoUrl: varchar("photo_url"), // URL to stored photo in App Storage
  latitude: varchar("latitude"), // GPS coordinates for location-based events
  longitude: varchar("longitude"), // GPS coordinates for location-based events
  status: varchar("status").default("scheduled").notNull(), // scheduled, active, completed, cancelled, archived
  participantCount: integer("participant_count").default(0),
  createdBy: varchar("created_by").references(() => users.id, { onDelete: "set null" }),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Event participants table
export const eventParticipants = pgTable("event_participants", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  eventId: varchar("event_id").references(() => events.id, { onDelete: "cascade" }).notNull(),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  joinedAt: timestamp("joined_at").defaultNow(),
});

// Islands table
export const islands = pgTable("islands", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  atoll: varchar("atoll").notNull(),
  latitude: varchar("latitude"),
  longitude: varchar("longitude"),
  isVisited: boolean("is_visited").default(false),
  lastVisited: timestamp("last_visited"),
});

// Island visits table to track check-ins with equipment
export const islandVisits = pgTable("island_visits", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  islandId: varchar("island_id").references(() => islands.id, { onDelete: "cascade" }).notNull(),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  visitedAt: timestamp("visited_at").defaultNow(),
  visitType: varchar("visit_type").default("manual").notNull(), // manual, automatic
  notes: text("notes"),
});

// Island visit equipment table to track which equipment was used during check-ins
export const islandVisitEquipment = pgTable("island_visit_equipment", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  visitId: varchar("visit_id").references(() => islandVisits.id, { onDelete: "cascade" }).notNull(),
  equipmentId: varchar("equipment_id").references(() => equipment.id, { onDelete: "cascade" }).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
}, (table) => [
  // Unique constraint to prevent duplicate visit-equipment links
  uniqueIndex("unique_visit_equipment").on(table.visitId, table.equipmentId),
]);

// Equipment table
export const equipment = pgTable("equipment", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  type: varchar("type").notNull(), // tools, electronics, visual, network, others
  quantity: integer("quantity").default(1).notNull(),
  status: varchar("status").default("available").notNull(), // available, in_use, maintenance, damaged
  atoll: varchar("atoll").notNull(),
  island: varchar("island"),
  description: text("description"),
  condition: varchar("condition").default("good").notNull(), // excellent, good, fair, poor
  parentEquipmentId: varchar("parent_equipment_id"), // Links to original equipment when split/moved
  createdBy: varchar("created_by").references(() => users.id, { onDelete: "set null" }),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
}, (table) => [
  // Unique constraint to prevent duplicate equipment records at the same location
  uniqueIndex("unique_equipment_location").on(table.name, table.type, table.atoll, table.island),
]);

// Equipment transfers table to track movement history
export const equipmentTransfers = pgTable("equipment_transfers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sourceEquipmentId: varchar("source_equipment_id").references(() => equipment.id, { onDelete: "set null" }),
  destinationEquipmentId: varchar("destination_equipment_id").references(() => equipment.id, { onDelete: "set null" }),
  quantityMoved: integer("quantity_moved").notNull(),
  fromAtoll: varchar("from_atoll").notNull(),
  fromIsland: varchar("from_island"),
  toAtoll: varchar("to_atoll").notNull(),
  toIsland: varchar("to_island"),
  transferredBy: varchar("transferred_by").references(() => users.id, { onDelete: "set null" }),
  transferredAt: timestamp("transferred_at").defaultNow(),
  notes: text("notes"),
});

// Equipment audit log table to track all changes
export const equipmentAuditLog = pgTable("equipment_audit_log", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  equipmentId: varchar("equipment_id").references(() => equipment.id, { onDelete: "cascade" }).notNull(),
  action: varchar("action").notNull(), // created, updated, deleted
  fieldName: varchar("field_name"), // Which field was changed (status, condition, etc.)
  oldValue: text("old_value"), // Previous value
  newValue: text("new_value"), // New value
  changedBy: varchar("changed_by").references(() => users.id, { onDelete: "set null" }),
  changedAt: timestamp("changed_at").defaultNow(),
  notes: text("notes"),
});

// Chat messages table
export const chatMessages = pgTable("chat_messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  userName: varchar("user_name").notNull(),
  message: text("message"), // For text messages
  audioUrl: varchar("audio_url"), // For audio messages
  isAudio: boolean("is_audio").default(false).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
}, (table) => ({
  // Index for efficient timestamp-based queries
  createdAtIndex: index("chat_messages_created_at_index").on(table.createdAt),
}));

// User chat read status table to track which messages each user has read
export const userChatReadStatus = pgTable("user_chat_read_status", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  lastReadMessageId: varchar("last_read_message_id").references(() => chatMessages.id, { onDelete: "set null" }),
  lastReadCreatedAt: timestamp("last_read_created_at"),
  lastReadAt: timestamp("last_read_at").defaultNow(),
}, (table) => ({
  // Unique constraint to ensure one read status per user
  uniqueUserReadStatus: uniqueIndex("unique_user_read_status").on(table.userId),
}));

// User milestones table to track badges and achievements
export const userMilestones = pgTable("user_milestones", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  milestoneType: varchar("milestone_type").notNull(), // island_visits, events_created, equipment_used, etc.
  milestoneName: varchar("milestone_name").notNull(), // Explorer, Navigator, Adventurer, etc.
  milestoneLevel: integer("milestone_level").notNull(), // 1, 5, 10, 25, 50, 100, etc.
  description: text("description").notNull(),
  badgeIcon: varchar("badge_icon").notNull(), // emoji or icon identifier
  badgeColor: varchar("badge_color").default("#3B82F6").notNull(), // hex color code
  achievedAt: timestamp("achieved_at").defaultNow(),
  progress: integer("progress").notNull(), // current count towards milestone
  targetValue: integer("target_value").notNull(), // required count to achieve milestone
}, (table) => ({
  // Unique constraint to prevent duplicate milestones per user
  uniqueUserMilestone: uniqueIndex("unique_user_milestone").on(table.userId, table.milestoneType, table.milestoneLevel),
}));


export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
}).extend({
  email: z.string().email("Invalid email address"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  role: z.enum(["agent", "coordinator", "admin", "attachment"]).default("agent"),
});

export type InsertUser = z.infer<typeof insertUserSchema>;

// Equipment types and schemas
export type Equipment = typeof equipment.$inferSelect;
export type InsertEquipment = typeof equipment.$inferInsert;
export type EquipmentTransfer = typeof equipmentTransfers.$inferSelect;
export type InsertEquipmentTransfer = typeof equipmentTransfers.$inferInsert;
export type EquipmentAuditLog = typeof equipmentAuditLog.$inferSelect;
export type InsertEquipmentAuditLog = typeof equipmentAuditLog.$inferInsert;

// Enhanced transfer type with user details
export type EquipmentTransferWithUser = EquipmentTransfer & {
  transferredByUser: {
    firstName: string | null;
    lastName: string | null;
    email: string | null;
    serviceNumber: string | null;
  };
};

export const insertEquipmentSchema = createInsertSchema(equipment).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  createdBy: true,
}).extend({
  name: z.string().min(1, "Equipment name is required"),
  type: z.enum(["tools", "electronics", "visual", "network", "others"]),
  quantity: z.number().int().min(1, "Quantity must be at least 1"),
  status: z.enum(["available", "in_use", "maintenance", "damaged"]),
  condition: z.enum(["excellent", "good", "fair", "poor"]),
  atoll: z.string().min(1, "Atoll is required"),
});

export const equipmentTransferSchema = createInsertSchema(equipmentTransfers).omit({
  id: true,
  transferredAt: true,
  transferredBy: true,
});

export const insertEventSchema = createInsertSchema(events).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  participantCount: true,
});
export type InsertEvent = z.infer<typeof insertEventSchema>;
export type Event = typeof events.$inferSelect;

export const insertIslandSchema = createInsertSchema(islands).omit({
  id: true,
});
export type InsertIsland = z.infer<typeof insertIslandSchema>;
export type Island = typeof islands.$inferSelect;

// Type for islands with user visit status (used in API responses)
export type IslandWithUserVisitStatus = Island & {
  hasVisited: boolean;
};

// Island visit types and schemas
export const insertIslandVisitSchema = createInsertSchema(islandVisits).omit({
  id: true,
  visitedAt: true,
});
export type InsertIslandVisit = z.infer<typeof insertIslandVisitSchema>;
export type IslandVisit = typeof islandVisits.$inferSelect;

export const insertIslandVisitEquipmentSchema = createInsertSchema(islandVisitEquipment).omit({
  id: true,
  createdAt: true,
});
export type InsertIslandVisitEquipment = z.infer<typeof insertIslandVisitEquipmentSchema>;
export type IslandVisitEquipment = typeof islandVisitEquipment.$inferSelect;

export type EventParticipant = typeof eventParticipants.$inferSelect;

// Chat message types and schemas
export type ChatMessage = typeof chatMessages.$inferSelect;
export type InsertChatMessage = typeof chatMessages.$inferInsert;

export const insertChatMessageSchema = createInsertSchema(chatMessages).omit({
  id: true,
  createdAt: true,
}).extend({
  userName: z.string().min(1, "User name is required"),
  message: z.string().optional(),
  audioUrl: z.string().optional(),
  isAudio: z.boolean().default(false),
}).refine((data) => {
  // Either message or audioUrl must be provided, but not both
  return (data.message && !data.audioUrl) || (!data.message && data.audioUrl);
}, {
  message: "Either message or audioUrl must be provided, but not both",
});

// User chat read status types and schemas
export type UserChatReadStatus = typeof userChatReadStatus.$inferSelect;
export type InsertUserChatReadStatus = typeof userChatReadStatus.$inferInsert;

export const insertUserChatReadStatusSchema = createInsertSchema(userChatReadStatus).omit({
  id: true,
  lastReadAt: true,
});

// User milestone types and schemas
export type UserMilestone = typeof userMilestones.$inferSelect;
export type InsertUserMilestone = typeof userMilestones.$inferInsert;

export const insertUserMilestoneSchema = createInsertSchema(userMilestones).omit({
  id: true,
  achievedAt: true,
}).extend({
  milestoneType: z.enum(["island_visits", "events_created", "equipment_used", "atolls_visited"]),
  milestoneName: z.string().min(1, "Milestone name is required"),
  milestoneLevel: z.number().int().min(1, "Milestone level must be at least 1"),
  description: z.string().min(1, "Description is required"),
  badgeIcon: z.string().min(1, "Badge icon is required"),
  badgeColor: z.string().min(7, "Badge color must be a valid hex color").max(7),
  progress: z.number().int().min(0, "Progress cannot be negative"),
  targetValue: z.number().int().min(1, "Target value must be at least 1"),
});

