}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer components {
  /* Overlay modal styles for dialogs that appear over other modals */
  .dialog-overlay-modal [data-radix-dialog-overlay] {
    z-index: var(--z-overlay-modal-backdrop) !important;
  }

  .dialog-overlay-modal [data-radix-dialog-content] {
    z-index: var(--z-overlay-modal) !important;
  }

  /* Custom location marker styles - Google Maps inspired */
  .custom-location-icon {
    background: transparent !important;
    border: none !important;
  }

  .current-location-marker {
    position: relative;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .location-pulse {
    position: absolute;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: rgba(59, 130, 246, 0.2);
    border: 2px solid rgba(59, 130, 246, 0.3);
    animation: location-pulse 2s ease-out infinite;
    z-index: 1;
  }

  .location-dot {
    width: 16px;
    height: 16px;
    background: #3b82f6;
    border-radius: 50%;
    border: 3px solid white;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    z-index: 2;
    position: relative;
  }

  @keyframes location-pulse {
    0% {
      transform: scale(0.8);
      opacity: 1;
    }
    100% {
      transform: scale(1.2);
      opacity: 0;
    }
  }

  /* New directional location marker styles */
  .location-marker {
    position: relative;
    z-index: 2;
    transition: transform 0.3s ease-out;
    transform-origin: center;
  }

  .dot-marker {
    width: 16px;
    height: 16px;
    background: #3b82f6;
    border-radius: 50%;
    border: 3px solid white;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  }

  .directional-marker {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 20px;
    height: 20px;
    color: #dc2626;
    font-size: 16px;
    font-weight: bold;
    background: rgba(255, 255, 255, 0.95);
    border-radius: 50%;
    border: 2px solid #dc2626;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    text-shadow: 0 0 2px rgba(255, 255, 255, 0.8);
  }

  /* Compass button styling */
  .compass-needle {
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    transform-origin: center;
    transition: transform 0.3s ease-out;
  }

  /* Compass button animations */
  @keyframes compass-active-pulse {
    0%, 100% {
      box-shadow: 0 0 0 0 rgba(239, 68, 68, 0.4);
    }
    50% {
      box-shadow: 0 0 0 4px rgba(239, 68, 68, 0.1);
    }
  }

  .compass-active {
    animation: compass-active-pulse 2s infinite;
  }

  /* Direction arrow styles */
  .route-arrow-icon {
    background: transparent !important;
    border: none !important;
  }

  .route-direction-arrow {
    transform: rotate(var(--rotation, 0deg));
    transform-origin: center;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 16px;
    height: 16px;
  }

  .arrow-glyph {
    color: #3b82f6;
    font-size: 12px;
    font-weight: bold;
    text-shadow: 0 0 3px rgba(255, 255, 255, 0.8);
    animation: arrow-pulse 2s ease-in-out infinite;
    display: block;
  }

  @keyframes arrow-pulse {
    0%, 100% {
      opacity: 0.7;
      transform: scale(1);
    }
    50% {
      opacity: 1;
      transform: scale(1.1);
    }
  }

  /* Enhanced route visualization styles */
  .route-step-icon {
    background: transparent !important;
    border: none !important;
  }

  .route-step-marker {
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    animation: route-marker-bounce 0.5s ease-out;
  }

  .route-step-number {
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #3b82f6, #1d4ed8);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 10px;
    font-weight: bold;
    border: 2px solid white;
    box-shadow: 0 2px 8px rgba(59, 130, 246, 0.4), 
                0 0 0 1px rgba(59, 130, 246, 0.2);
    transition: all 0.3s ease;
  }

  .route-step-marker:hover .route-step-number {
    transform: scale(1.1);
    box-shadow: 0 4px 12px rgba(59, 130, 246, 0.6),
                0 0 0 2px rgba(59, 130, 246, 0.3);
  }

  /* Route line animations */
  .route-line-gradient-segment {
    stroke-linecap: round;
    stroke-linejoin: round;
    animation: route-draw 1.5s ease-out forwards;
    animation-delay: calc(var(--segment-index, 0) * 0.05s);
  }

  .route-line-shadow {
    stroke-linecap: round;
    stroke-linejoin: round;
  }

  @keyframes route-marker-bounce {
    0% {
      transform: scale(0) translateY(-20px);
      opacity: 0;
    }
    50% {
      transform: scale(1.2) translateY(-5px);
      opacity: 0.8;
    }
    100% {
      transform: scale(1) translateY(0);
      opacity: 1;
    }
  }

  @keyframes route-draw {
    0% {
      stroke-dasharray: 0, 1000;
    }
    100% {
      stroke-dasharray: 1000, 0;
    }
  }

  /* Modern directions panel animations */
  .directions-slide-up {
    animation: directions-slide-up 0.3s ease-out;
  }

  @keyframes directions-slide-up {
    0% {
      transform: translateY(100%);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  /* Progress indicator styles */
  .step-progress-bar {
    position: relative;
    background: linear-gradient(90deg, #10b981, #3b82f6);
    height: 3px;
    border-radius: 1.5px;
    overflow: hidden;
  }

  .step-progress-bar::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
    animation: progress-shimmer 2s infinite;
  }

  @keyframes progress-shimmer {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

}

