import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { MapLocationPicker } from "@/components/MapLocationPicker";
import { ObjectUploader } from "@/components/ObjectUploader";
import { MapPin, Camera, X } from "lucide-react";

interface EventFormProps {
  event?: any;
  onSuccess: () => void;
}

export default function EventForm({ event, onSuccess }: EventFormProps) {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    title: event?.title || "",
    island: event?.island || "",
    atoll: event?.atoll || "",
    eventLocation: event?.eventLocation || "",
    waitingLocation: event?.waitingLocation || "",
    contact: event?.contact || "",
    comment: event?.comment || "",
    photoUrl: event?.photoUrl || "",
    latitude: event?.latitude || "",
    longitude: event?.longitude || "",
  });

  const [showLocationPicker, setShowLocationPicker] = useState(false);
  const [locationPickerType, setLocationPickerType] = useState<'event' | 'waiting'>('event');
  const [photoPreview, setPhotoPreview] = useState<string | null>(event?.photoUrl || null);
  const [isUploadingPhoto, setIsUploadingPhoto] = useState(false);

  const mutation = useMutation({
    mutationFn: async (data: any) => {
      if (event) {
        await apiRequest("PATCH", `/api/events/${event.id}`, data);
      } else {
        await apiRequest("POST", "/api/events", data);
      }
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: event ? "Event updated successfully" : "Event created successfully",
      });
      onSuccess();
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: event ? "Failed to update event" : "Failed to create event",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    mutation.mutate(formData);
  };

  const { data: islandsData = [] } = useQuery({
    queryKey: ["/api/islands"],
  });

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleLocationSelect = (location: { latitude: number; longitude: number; address: string }) => {
    // Always update form data regardless of picker type for now
    setFormData(prev => ({
      ...prev,
      eventLocation: location.address,
      latitude: location.latitude.toString(),
      longitude: location.longitude.toString()
    }));

    // Show success toast
    toast({
      title: "Location Set",
      description: `Event location updated to: ${location.latitude.toFixed(4)}, ${location.longitude.toFixed(4)}`,
    });
  };

  // Photo upload handlers
  const getPhotoUploadParams = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json() as { uploadURL: string };
      if (!data.uploadURL) {
        throw new Error("No upload URL received from server");
      }
      return {
        method: "PUT" as const,
        url: data.uploadURL
      };
    } catch (error: any) {
      if (error.message?.includes('401') || error.message?.includes('Unauthorized')) {
        throw new Error("Authentication required. Please refresh the page and log in again.");
      }
      throw new Error(`Failed to get upload URL: ${error.message}`);
    }
  };

  const handlePhotoUploadComplete = async (result: any) => {
    const uploadUrl = result.successful[0]?.uploadURL;

    if (!uploadUrl) {
      toast({
        title: "Upload Error",
        description: "Failed to get upload URL",
        variant: "destructive"
      });
      return;
    }

    setIsUploadingPhoto(true);
    try {
      if (event?.id) {
        // For existing events, use the existing photo endpoint
        const response = await apiRequest("PUT", `/api/events/${event.id}/photo`, {
          photoUrl: uploadUrl
        }) as unknown as { objectPath: string; event: any };
        setFormData(prev => ({ ...prev, photoUrl: response.objectPath }));
        setPhotoPreview(response.objectPath);
      } else {
        // For new events, just store the URL - backend will process it during creation
        setFormData(prev => ({ ...prev, photoUrl: uploadUrl }));
        setPhotoPreview(uploadUrl);
      }

      toast({
        title: "Photo Uploaded",
        description: "Event photo has been added successfully"
      });
    } catch (error) {
      console.error("Photo upload error:", error);
      toast({
        title: "Upload Error",
        description: "Failed to process uploaded photo",
        variant: "destructive"
      });
    } finally {
      setIsUploadingPhoto(false);
    }
  };

  const removePhoto = () => {
    setFormData(prev => ({ ...prev, photoUrl: "" }));
    setPhotoPreview(null);
    toast({
      title: "Photo Removed",
      description: "Event photo has been removed"
    });
  };

  // Get unique atolls
  const atolls = Array.from(new Set((islandsData as any[]).map((island: any) => island.atoll))).sort();

  // Filter islands by selected atoll
  const availableIslands = formData.atoll 
    ? (islandsData as any[]).filter((island: any) => island.atoll === formData.atoll)
    : (islandsData as any[]);

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="space-y-4">
        {/* Atoll */}
        <div>
          <Label htmlFor="atoll">Atoll</Label>
          <Select 
            value={formData.atoll} 
            onValueChange={(value) => {
              handleChange("atoll", value);
              // Clear island selection when atoll changes
              handleChange("island", "");
            }}
          >
            <SelectTrigger data-testid="select-event-atoll">
              <SelectValue placeholder="Select Atoll" />
            </SelectTrigger>
            <SelectContent>
              {atolls.map((atoll, index) => (
                <SelectItem key={`form-atoll-${index}`} value={atoll}>
                  {atoll}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Island */}
        <div>
          <Label htmlFor="island">Island</Label>
          <Select 
            value={formData.island} 
            onValueChange={(value) => handleChange("island", value)}
            disabled={!formData.atoll}
          >
            <SelectTrigger data-testid="select-event-island">
              <SelectValue placeholder={formData.atoll ? "Select Island" : "Select Atoll First"} />
            </SelectTrigger>
            <SelectContent>
              {availableIslands.map((island: any) => (
                <SelectItem key={island.id} value={island.name}>
                  {island.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Event */}
        <div>
          <Label htmlFor="title">Event</Label>
          <Input
            id="title"
            value={formData.title}
            onChange={(e) => handleChange("title", e.target.value)}
            placeholder="Enter event name"
            required
            data-testid="input-event-title"
          />
        </div>

        {/* Event Location */}
        <div>
          <Label htmlFor="eventLocation">Event Location</Label>
          <div className="flex gap-2">
            <Input
              id="eventLocation"
              value={formData.eventLocation}
              onChange={(e) => handleChange("eventLocation", e.target.value)}
              placeholder="Enter event location details"
              className="flex-1"
              data-testid="input-event-location"
            />
            <Button
              type="button"
              variant="outline"
              onClick={() => {
                setLocationPickerType('event');
                setShowLocationPicker(true);
              }}
              className="flex items-center gap-1"
              data-testid="button-select-event-location"
            >
              <MapPin className="h-4 w-4" />
              Map
            </Button>
          </div>
          {formData.latitude && formData.longitude && (
            <div className="text-xs text-gray-500 mt-1">
              GPS: {parseFloat(formData.latitude).toFixed(4)}, {parseFloat(formData.longitude).toFixed(4)}
            </div>
          )}
        </div>

        {/* Waiting Location */}
        <div>
          <Label htmlFor="waitingLocation">Waiting Location</Label>
          <Input
            id="waitingLocation"
            value={formData.waitingLocation}
            onChange={(e) => handleChange("waitingLocation", e.target.value)}
            placeholder="Enter waiting location details"
            data-testid="input-waiting-location"
          />
        </div>

        {/* Contact */}
        <div>
          <Label htmlFor="contact">Contact</Label>
          <Input
            id="contact"
            value={formData.contact}
            onChange={(e) => handleChange("contact", e.target.value)}
            placeholder="Enter contact information"
            data-testid="input-contact"
          />
        </div>

        {/* Comment */}
        <div>
          <Label htmlFor="comment">Comment</Label>
          <Textarea
            id="comment"
            value={formData.comment}
            onChange={(e) => handleChange("comment", e.target.value)}
            placeholder="Enter additional comments"
            rows={3}
            data-testid="textarea-comment"
          />
        </div>

        {/* Event Photo */}
        <div>
          <Label>Event Photo</Label>
          <div className="space-y-3">
            {photoPreview ? (
              <div className="relative">
                <img
                  src={photoPreview}
                  alt="Event preview"
                  className="w-full max-w-sm h-48 object-cover rounded-lg border"
                />
                <Button
                  type="button"
                  variant="destructive"
                  size="sm"
                  onClick={removePhoto}
                  className="absolute top-2 right-2 h-8 w-8 p-0"
                  data-testid="button-remove-photo"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            ) : (
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
                <Camera className="mx-auto h-12 w-12 text-gray-400 mb-2" />
                <p className="text-sm text-gray-500 mb-4">Add a photo to showcase your conservation event</p>
                <ObjectUploader
                  maxNumberOfFiles={1}
                  maxFileSize={2097152} // 2MB after compression
                  onGetUploadParameters={getPhotoUploadParams}
                  onComplete={handlePhotoUploadComplete}
                  buttonClassName="bg-blue-600 hover:bg-blue-700"
                >
                  <Camera className="h-4 w-4 mr-2" />
                  {isUploadingPhoto ? "Processing..." : "Upload Photo"}
                </ObjectUploader>
              </div>
            )}
            <p className="text-xs text-gray-500">
              Photos are automatically compressed to save storage space. Supported formats: JPEG, PNG, WebP
            </p>
          </div>
        </div>

      </div>

      <div className="flex justify-end space-x-3 pt-4">
        <Button 
          type="submit" 
          disabled={mutation.isPending}
          data-testid="button-submit-event"
        >
          {mutation.isPending ? "Saving..." : event ? "Update Event" : "Create Event"}
        </Button>
      </div>

      {/* Map Location Picker Modal */}
      <MapLocationPicker
        isOpen={showLocationPicker}
        onClose={() => setShowLocationPicker(false)}
        onLocationSelect={handleLocationSelect}
        initialLocation={
          formData.latitude && formData.longitude 
            ? {
                latitude: parseFloat(formData.latitude),
                longitude: parseFloat(formData.longitude),
                address: locationPickerType === 'event' ? formData.eventLocation : formData.waitingLocation
              }
            : undefined
        }
        title={locationPickerType === 'event' ? 'Select Event Location' : 'Select Waiting Location'}
      />
    </form>
  );
}

