import { ReactNode } from "react";
import { X } from "lucide-react";
import { Button } from "@/components/ui/button";

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  children: ReactNode;
}

export default function Modal({ isOpen, onClose, title, children }: ModalProps) {
  if (!isOpen) return null;

  return (
    <div 
      className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-start justify-center p-2 sm:p-4 overflow-y-auto" 
      style={{ zIndex: "var(--z-modal-backdrop)" }}
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          onClose();
        }
      }}
    >
      <div 
        className="bg-card rounded-lg w-full max-w-2xl min-h-0 mt-12 mb-4 sm:mt-16 sm:mb-8 flex flex-col shadow-xl"
        style={{ zIndex: "var(--z-modal)" }}
      >
        <div className="p-4 sm:p-6 border-b border-border flex-shrink-0 sticky top-0 bg-card rounded-t-lg">
          <div className="flex items-center justify-between">
            <h2 className="text-lg sm:text-xl font-semibold text-foreground pr-4">{title}</h2>
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              data-testid="button-close-modal"
              className="flex-shrink-0"
            >
              <X className="h-5 w-5" />
            </Button>
          </div>
        </div>
        <div className="p-4 sm:p-6 overflow-y-auto flex-1">
          {children}
        </div>
      </div>
    </div>
  );
}

client/src/components/navigation.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { 
  BarChart3, 
  Calendar, 
  Users, 
  Map, 
  Package,
  Archive, 
  Settings, 
  Menu, 
  X,
  LogOut,
  ChevronLeft,
  ChevronRight,
  User,
  MessageCircle
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { usePermissions } from "@/hooks/usePermissions";
import { Chat } from "./Chat";
import { getUserDisplayInfo } from "@/lib/userDisplayUtils";
import { apiRequest } from "@/lib/queryClient";

const navItems = [
  { icon: BarChart3, label: "Overview", path: "/" },
  { icon: Calendar, label: "Events", path: "/events" },
  { icon: Users, label: "Members", path: "/members" },
  { icon: Map, label: "Island Map", path: "/map" },
  { icon: Package, label: "Equipment", path: "/equipment" },
  { icon: Archive, label: "Archive", path: "/archive" },
  { icon: User, label: "My Profile", path: "/profile" },
  { icon: Settings, label: "Admin Panel", path: "/admin" },
];

export default function Navigation() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [unreadMessagesCount, setUnreadMessagesCount] = useState(0);
  const [location] = useLocation();
  const { user } = useAuth();
  const { 
    canViewEvents, 
    canViewMembers, 
    canViewMap, 
    canViewEquipment, 
    canAccessAdminPanel 
  } = usePermissions();

  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Load unread count from API  
  const fetchUnreadCount = async () => {
    if (!user) return;

    try {
      const response = await apiRequest("GET", '/api/chat/unread-count');
      const data = await response.json();
      setUnreadMessagesCount(data.unreadCount || 0);
      return data;
    } catch (error) {
      console.error('Error fetching unread count:', error);
      return null;
    }
  };

  // Initial load of unread count
  useEffect(() => {
    fetchUnreadCount();
  }, [user]);

  // Set up WebSocket connection for real-time notifications
  useEffect(() => {
    if (!user) return;

    const connectWebSocket = () => {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/chat`;

      wsRef.current = new WebSocket(wsUrl);

      wsRef.current.onopen = () => {
        // Join for notifications only (don't appear in user list)
        wsRef.current?.send(JSON.stringify({
          type: 'subscribe-notifications',
          clientId: `notification_${Date.now()}_${Math.random()}`,
          userId: (user as any).id,
          listener: true
        }));
      };

      wsRef.current.onmessage = (event) => {
        const data = JSON.parse(event.data);

        if (data.type === 'chat-notification') {
          // Refresh unread count when someone sends a message
          fetchUnreadCount();
        }
      };

      wsRef.current.onclose = () => {
        // Reconnect after 3 seconds if connection is lost
        reconnectTimeoutRef.current = setTimeout(connectWebSocket, 3000);
      };

      wsRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    };

    connectWebSocket();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, [user]);

  const handleChatOpen = async () => {
    setIsChatOpen(true);
    // Clear unread messages when opening chat
    setUnreadMessagesCount(0);

    // Mark all messages as read by getting the latest message ID from unread-count API
    try {
      const data = await fetchUnreadCount();
      if (data && data.latestMessageId) {
        await markAllMessagesAsRead(data.latestMessageId);
      }
    } catch (error) {
      console.error('Error marking messages as read on chat open:', error);
    }
  };

  const markAllMessagesAsRead = async (lastMessageId?: string) => {
    if (!user || !lastMessageId) return;

    try {
      await apiRequest("POST", '/api/chat/mark-read', { lastReadMessageId: lastMessageId });

      // Refresh unread count after marking as read
      fetchUnreadCount();
    } catch (error) {
      console.error('Error marking messages as read:', error);
    }
  };

  const filteredNavItems = navItems.filter(item => {
    switch (item.path) {
      case "/events":
        return canViewEvents;
      case "/members":
        return canViewMembers;
      case "/map":
        return canViewMap;
      case "/equipment":
        return canViewEquipment;
      case "/admin":
        return canAccessAdminPanel;
      // Always show overview, profile, and archive for all authenticated users
      case "/":
      case "/profile":
      case "/archive":
        return true;
      default:
        return true;
    }
  });

  return (
    <>
      {/* Header */}
      <header className="bg-card shadow-sm border-b border-border fixed top-0 left-0 right-0 z-[9998] lg:relative lg:z-auto">
        <div className="px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Button 
                variant="ghost" 
                size="sm"
                className="lg:hidden"
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                data-testid="button-mobile-menu"
              >
                {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
              </Button>
              <Button 
                variant="ghost" 
                size="sm"
                className="hidden lg:block"
                onClick={() => setIsMinimized(!isMinimized)}
                data-testid="button-minimize-sidebar"
              >
                {isMinimized ? <ChevronRight className="h-5 w-5" /> : <ChevronLeft className="h-5 w-5" />}
              </Button>
              <h1 className="text-xl font-semibold text-foreground">
                {navItems.find(item => item.path === location)?.label || "Dashboard"}
              </h1>
            </div>
            <div className="flex items-center space-x-3">
              <span className="hidden sm:inline text-sm text-muted-foreground" data-testid="text-user-name">
                {getUserDisplayInfo(user as any).displayName}
              </span>
              <span 
                className={`text-xs px-2 py-1 rounded ${
                  canAccessAdminPanel ? 'bg-primary text-primary-foreground' :
                  canViewEquipment ? 'bg-secondary text-secondary-foreground' :
                  'bg-muted text-muted-foreground'
                }`}
                data-testid="text-user-role"
              >
                {(user as any)?.role}
              </span>
              <Button
                variant="ghost"
                size="sm"
                onClick={handleChatOpen}
                data-testid="button-chat"
                title="Team Chat"
                className="relative"
              >
                <MessageCircle className="h-4 w-4" />
                {unreadMessagesCount > 0 && (
                  <span 
                    className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center min-w-[20px]"
                    data-testid="chat-unread-badge"
                  >
                    {unreadMessagesCount > 99 ? '99+' : unreadMessagesCount}
                  </span>
                )}
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => window.location.href = '/api/logout'}
                data-testid="button-logout"
              >
                <LogOut className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </div>
      </header>

      {/* Desktop Sidebar */}
      <aside className={`hidden lg:block ${isMinimized ? 'w-16' : 'w-64'} bg-card border-r border-border min-h-screen transition-all duration-300 fixed lg:relative top-0 left-0 z-30`}>
        <nav className="p-4 space-y-2">
          {filteredNavItems.map((item) => {
            const isActive = location === item.path;
            return (
              <Link key={item.path} href={item.path}>
                <Button
                  variant={isActive ? "default" : "ghost"}
                  className={`w-full ${isMinimized ? 'justify-center p-2' : 'justify-start'} transition-all duration-300`}
                  data-testid={`nav-${item.label.toLowerCase().replace(/\s+/g, '-')}`}
                  title={isMinimized ? item.label : undefined}
                >
                  <item.icon className={`h-4 w-4 ${isMinimized ? '' : 'mr-2'}`} />
                  {!isMinimized && item.label}
                </Button>
              </Link>
            );
          })}
        </nav>
      </aside>

      {/* Mobile Sidebar Overlay */}
      {isMobileMenuOpen && (
        <div className="lg:hidden fixed inset-0 z-[9999] bg-black bg-opacity-50" onClick={() => setIsMobileMenuOpen(false)}>
          <aside className="w-64 bg-card h-full shadow-lg" onClick={(e) => e.stopPropagation()}>
            <div className="p-4 border-b border-border">
              <h2 className="text-lg font-semibold text-foreground">Menu</h2>
            </div>
            <nav className="p-4 space-y-2">
              {filteredNavItems.map((item) => {
                const isActive = location === item.path;
                return (
                  <Link key={item.path} href={item.path}>
                    <Button
                      variant={isActive ? "default" : "ghost"}
                      className="w-full justify-start"
                      onClick={() => setIsMobileMenuOpen(false)}
                      data-testid={`mobile-nav-${item.label.toLowerCase().replace(/\s+/g, '-')}`}
                    >
                      <item.icon className="h-4 w-4 mr-2" />
                      {item.label}
                    </Button>
                  </Link>
                );
              })}
            </nav>
          </aside>
        </div>
      )}

      {/* Mobile Bottom Navigation */}
      <nav className="lg:hidden fixed bottom-0 left-0 right-0 bg-card border-t border-border z-40">
        <div className="grid grid-cols-5 gap-1 p-2">
          {filteredNavItems.slice(0, 5).map((item) => {
            const isActive = location === item.path;
            return (
              <Link key={item.path} href={item.path}>
                <Button
                  variant="ghost"
                  className={`flex flex-col items-center py-2 px-1 h-auto ${
                    isActive ? 'text-primary' : 'text-muted-foreground'
                  }`}
                  data-testid={`bottom-nav-${item.label.toLowerCase().replace(/\s+/g, '-')}`}
                >
                  <item.icon className="h-5 w-5 mb-1" />
                  <span className="text-xs">{item.label.split(' ')[0]}</span>
                </Button>
              </Link>
            );
          })}
        </div>
      </nav>

      {/* Chat Modal */}
      <Chat 
        isOpen={isChatOpen}
        onClose={() => setIsChatOpen(false)}
        onMarkAllRead={markAllMessagesAsRead}
      />
    </>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

