  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
    enabled: isAuthenticated && canAccessAdminPanel,
  });

  const updateRoleMutation = useMutation({
    mutationFn: async ({ userId, role }: { userId: string; role: string }) => {
      await apiRequest("PATCH", `/api/users/${userId}/role`, { role });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User role updated successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update user role",
        variant: "destructive",
      });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (userId: string) => {
      await apiRequest("DELETE", `/api/users/${userId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User deleted successfully",
      });
      setDeletingUser(null);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const handleUserCreated = () => {
    setShowUserForm(false);
    setEditingUser(null);
    queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    toast({
      title: "Success",
      description: editingUser ? "User updated successfully" : "User created successfully",
    });
  };

  const handleEditUser = (user: any) => {
    setEditingUser(user);
    setShowUserForm(true);
  };

  const handleCancelEdit = () => {
    setShowUserForm(false);
    setEditingUser(null);
  };

  const handleDeleteUser = (userToDelete: any) => {
    // Prevent self-deletion
    if (userToDelete.id === (user as any)?.id) {
      toast({
        title: "Cannot Delete",
        description: "You cannot delete your own account",
        variant: "destructive",
      });
      return;
    }
    setDeletingUser(userToDelete);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!canAccessAdminPanel) {
    return (
      <div className="p-4 lg:p-6">
        <Card>
          <CardContent className="p-8 text-center">
            <p className="text-muted-foreground" data-testid="text-admin-access-denied">
              You don't have admin access. Contact your administrator for permissions.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-2 sm:p-4 lg:p-6">
      <div className="mb-6 sm:mb-8">
        <h1 className="text-2xl sm:text-3xl font-bold text-foreground mb-2">Admin Panel</h1>
        <p className="text-sm sm:text-base text-muted-foreground">Administrative controls and system settings</p>
      </div>

      {/* Admin Actions Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-4 sm:gap-6 mb-6 sm:mb-8">

        {/* Debug Tools */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Bug className="h-5 w-5" />
              <span>ðŸ› ï¸ Debug Tools</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setActiveSection('system-monitor')}
              data-testid="button-system-monitor"
            >
              <div className="flex items-center space-x-3">
                <BarChart3 className="h-5 w-5 text-accent" />
                <span>System Monitor</span>
              </div>
              <span className="text-muted-foreground">â†’</span>
            </Button>
          </CardContent>
        </Card>

        {/* User Management */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <UserCog className="h-5 w-5" />
              <span>User Management</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setShowUserForm(true)}
              data-testid="button-add-user"
            >
              <div className="flex items-center space-x-3">
                <UserPlus className="h-5 w-5 text-primary" />
                <span>Add New User</span>
              </div>
              <span className="text-muted-foreground">â†’</span>
            </Button>
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setActiveSection('bulk-operations')}
              data-testid="button-bulk-operations"
            >
              <div className="flex items-center space-x-3">
                <Users className="h-5 w-5 text-secondary" />
                <span>Bulk User Operations</span>
              </div>
              <span className="text-muted-foreground">â†’</span>
            </Button>
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setActiveSection('user-approvals')}
              data-testid="button-user-approvals"
            >
              <div className="flex items-center space-x-3">
                <CheckCircle className="h-5 w-5 text-green-600" />
                <span>User Approvals</span>
              </div>
              <span className="text-muted-foreground">â†’</span>
            </Button>
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setActiveSection('suspended-accounts')}
              data-testid="button-suspended-accounts"
            >
              <div className="flex items-center space-x-3">
                <Ban className="h-5 w-5 text-destructive" />
                <span>Suspended Accounts</span>
              </div>
              <span className="text-muted-foreground">â†’</span>
            </Button>
          </CardContent>
        </Card>

        {/* System Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Database className="h-5 w-5" />
              <span>System Settings</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <Button 
              variant="ghost" 
              className="w-full justify-between p-3 h-auto"
              onClick={() => setActiveSection('backup-restore')}
              data-testid="button-backup-restore"
            >
              <div className="flex items-center space-x-3">
                <ArchiveRestore className="h-5 w-5 text-primary" />
                <span>ArchiveRestore & Restore</span>
              </div>
