                  <th className="text-left p-4 text-sm font-medium text-foreground">Current Role</th>
                  <th className="text-left p-4 text-sm font-medium text-foreground">Permissions</th>
                  <th className="text-left p-4 text-sm font-medium text-foreground">Last Active</th>
                  <th className="text-left p-4 text-sm font-medium text-foreground">Actions</th>
                </tr>
              </thead>
              <tbody>
                {(users as any[]).length === 0 ? (
                  <tr>
                    <td colSpan={5} className="p-8 text-center text-muted-foreground">
                      No users found.
                    </td>
                  </tr>
                ) : (
                  (users as any[]).map((userItem: any) => (
                    <tr key={userItem.id} className="border-b border-border" data-testid={`row-user-${userItem.id}`}>
                      <td className="p-4">
                        <div className="flex items-center space-x-3">
                          <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center text-primary-foreground text-sm font-medium">
                            {getUserDisplayInfo(userItem).initials}
                          </div>
                          <div>
                            <p className="font-medium text-foreground" data-testid={`text-user-name-${userItem.id}`}>
                              {getUserDisplayInfo(userItem).displayName}
                            </p>
                            <p className="text-sm text-muted-foreground" data-testid={`text-user-email-${userItem.id}`}>
                              {userItem.email}
                            </p>
                          </div>
                        </div>
                      </td>
                      <td className="p-4">
                        <Select
                          value={userItem.role}
                          onValueChange={(role) => updateRoleMutation.mutate({ userId: userItem.id, role })}
                        >
                          <SelectTrigger 
                            className="w-32" 
                            data-testid={`select-user-role-${userItem.id}`}
                          >
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="agent">Agent</SelectItem>
                            <SelectItem value="attachment">Attachment</SelectItem>
                            <SelectItem value="coordinator">Coordinator</SelectItem>
                            <SelectItem value="admin">Admin</SelectItem>
                          </SelectContent>
                        </Select>
                      </td>
                      <td className="p-4">
                        <div className="flex flex-wrap gap-1">
                          <span className={`px-2 py-1 text-xs rounded ${
                            userItem.role === 'admin' ? 'bg-primary/10 text-primary' : 'bg-muted text-muted-foreground'
                          }`}>
                            Events
                          </span>
                          <span className={`px-2 py-1 text-xs rounded ${
                            userItem.role === 'admin' || userItem.role === 'coordinator' ? 'bg-secondary/10 text-secondary' : 'bg-muted text-muted-foreground'
                          }`}>
                            Members
                          </span>
                          <span className={`px-2 py-1 text-xs rounded ${
                            userItem.role === 'admin' ? 'bg-accent/10 text-accent' : 'bg-muted text-muted-foreground'
                          }`}>
                            Admin
                          </span>
                        </div>
                      </td>
                      <td className="p-4 text-sm text-muted-foreground" data-testid={`text-user-last-active-${userItem.id}`}>
                        {userItem.updatedAt ? new Date(userItem.updatedAt).toLocaleDateString() : 'Never'}
                      </td>
                      <td className="p-4">
                        <div className="flex space-x-2">
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => handleEditUser(userItem)}
                            data-testid={`button-edit-user-${userItem.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => handleDeleteUser(userItem)}
                            disabled={userItem.id === (user as any)?.id || deleteUserMutation.isPending}
                            data-testid={`button-delete-user-${userItem.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            data-testid={`button-ban-user-${userItem.id}`}
                          >
                            <Ban className="h-4 w-4" />
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* User Form Modal (Create/Edit) */}
      {showUserForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <UserForm
            user={editingUser}
            onSuccess={handleUserCreated}
            onCancel={handleCancelEdit}
          />
        </div>
      )}

      {/* Admin Section Modals */}
      {activeSection && (
        <AdminSection 
          section={activeSection} 
          onClose={() => setActiveSection(null)} 
        />
      )}

      {/* Delete User Confirmation Dialog */}
      <ConfirmDialog
        isOpen={!!deletingUser}
        onClose={() => setDeletingUser(null)}
        onConfirm={() => {
          if (deletingUser) {
            deleteUserMutation.mutate(deletingUser.id);
          }
        }}
        title="Delete User"
        description={`Are you sure you want to delete "${getUserDisplayInfo(deletingUser).displayName}"? This will permanently remove their account and all associated data.`}
        confirmText="Delete User"
        isLoading={deleteUserMutation.isPending}
      />
    </div>
  );
}

