    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: archivedEvents = [] } = useQuery<Event[]>({
    queryKey: ["/api/archive"],
    enabled: isAuthenticated,
  });

  const filteredArchivedEvents = archivedEvents.filter((event: Event) => {
    const eventYear = new Date(event.createdAt || Date.now()).getFullYear().toString();
    const matchesYear = eventYear === yearFilter;
    const matchesSearch = !searchQuery || 
      event.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      event.island.toLowerCase().includes(searchQuery.toLowerCase()) ||
      event.atoll.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (event.comment && event.comment.toLowerCase().includes(searchQuery.toLowerCase()));

    return matchesYear && matchesSearch;
  });

  const totalArchived = archivedEvents.length;
  const totalParticipants = archivedEvents.reduce((sum: number, event: any) => sum + (event.participantCount || 0), 0);

  const toggleEventExpanded = (eventId: string) => {
    setExpandedEvents(prev => {
      const newSet = new Set(prev);
      if (newSet.has(eventId)) {
        newSet.delete(eventId);
      } else {
        newSet.add(eventId);
      }
      return newSet;
    });
  };

  const handleViewEvent = (event: any) => {
    // Navigate to event details or show more info
    toast({
      title: "View Event",
      description: `Viewing details for "${event.title}"`,
    });
  };

  const handleDownloadEvent = (event: any) => {
    // Generate and download event report
    toast({
      title: "Download Report",
      description: `Downloading report for "${event.title}"`,
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="p-2 sm:p-4 lg:p-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground mb-2">Event Archive</h1>
          <p className="text-sm sm:text-base text-muted-foreground">Historical record of completed and cancelled events</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-2 mt-4 sm:mt-0">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search archived events..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 w-64"
              data-testid="input-archive-search"
            />
          </div>
          <Select value={yearFilter} onValueChange={setYearFilter}>
            <SelectTrigger className="w-32" data-testid="select-year-filter">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="2025">2025</SelectItem>
              <SelectItem value="2024">2024</SelectItem>
              <SelectItem value="2023">2023</SelectItem>
              <SelectItem value="2022">2022</SelectItem>
            </SelectContent>
          </Select>
          <ExportButton 
            dataType="events" 
            label="Archive"
          />
        </div>
      </div>

      {/* Archive Summary */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-2xl font-bold text-foreground" data-testid="text-total-archived">
              {totalArchived}
            </p>
            <p className="text-sm text-muted-foreground">Total Archived</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-2xl font-bold text-primary" data-testid="text-total-participants">
              {totalParticipants}
            </p>
            <p className="text-sm text-muted-foreground">Total Participants</p>
          </CardContent>
        </Card>
      </div>

      {/* Archived Events List */}
      <div className="space-y-4">
        {filteredArchivedEvents.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground" data-testid="text-no-archived-events">
                No archived events found for {yearFilter}.
              </p>
            </CardContent>
          </Card>
        ) : (
          filteredArchivedEvents.map((event: any) => {
            const isExpanded = expandedEvents.has(event.id);
            return (
              <Card key={event.id} data-testid={`card-archived-event-${event.id}`}>
                <CardContent className="p-3">
                  {/* Compact header - always visible */}
                  <div 
                    className="flex items-center justify-between cursor-pointer"
                    onClick={() => toggleEventExpanded(event.id)}
                    data-testid={`button-expand-archived-${event.id}`}
                  >
                    <div className="flex items-center space-x-3 flex-1">
                      <h3 className="text-base font-semibold text-foreground" data-testid={`text-archived-title-${event.id}`}>
                        {event.title}
                      </h3>
                      <span 
                        className="px-2 py-1 text-xs rounded-full bg-gray-100 text-gray-800"
                        data-testid={`text-archived-status-${event.id}`}
                      >
                        archived
                      </span>
                      <div className="flex items-center text-sm text-muted-foreground">
                        <MapPin className="h-3 w-3 mr-1" />
                        <span>{event.island}</span>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      {/* Quick actions - always visible */}
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleViewEvent(event);
                        }}
                        data-testid={`button-view-${event.id}`}
                        title="View Event Details"
                      >
                        <Eye className="h-4 w-4" />
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDownloadEvent(event);
                        }}
                        data-testid={`button-download-${event.id}`}
                        title="Download Report"
                      >
                        <Download className="h-4 w-4" />
                      </Button>
                      {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                    </div>
                  </div>

                  {/* Expanded details */}
                  {isExpanded && (
                    <div className="mt-4 pt-4 border-t border-gray-200">
                      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 text-sm text-muted-foreground">
                        <div className="flex items-center">
                          <MapPin className="h-4 w-4 mr-2" />
                          <span data-testid={`text-archived-location-${event.id}`}>{event.island}, {event.atoll}</span>
                        </div>
                        <div className="flex items-center">
                          <Calendar className="h-4 w-4 mr-2" />
                          <span data-testid={`text-archived-date-${event.id}`}>
                            {new Date(event.createdAt).toLocaleDateString()}
                          </span>
                        </div>
                        <div className="flex items-center">
                          <Users className="h-4 w-4 mr-2" />
                          <span data-testid={`text-archived-participants-${event.id}`}>
                            {event.participantCount} participants
                          </span>
                        </div>
                      </div>
                      {event.comment && (
                        <div className="mt-3 pt-3 border-t border-gray-100">
                          <p className="text-foreground text-sm" data-testid={`text-archived-description-${event.id}`}>
                            {event.comment}
                          </p>
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            );
          })
        )}
      </div>
    </div>
  );
}

