import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar, Users, MapPin, CheckCircle, ChevronDown, ChevronUp, Phone } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useEffect, useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import IslandCheckIn from "@/components/island-check-in";
import { MilestoneBadges, MilestoneQuickStats } from "@/components/milestone-badges";

export default function Dashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [expandedEvents, setExpandedEvents] = useState<Set<string>>(new Set());
  const [sortBy, setSortBy] = useState<string>("date"); // date, atoll, island

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: events = [] } = useQuery({
    queryKey: ["/api/events"],
    enabled: isAuthenticated,
  });

  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
    enabled: isAuthenticated,
  });

  const { data: islands = [] } = useQuery({
    queryKey: ["/api/islands"],
    enabled: isAuthenticated,
  });

  // Get individual user milestone stats for accurate visited islands count
  const { data: milestoneStats } = useQuery<{ totalIslands: number; totalVisits: number; uniqueAtolls: number }>({
    queryKey: ['/api/milestones/stats'],
    enabled: isAuthenticated,
  });

  const activeEvents = (events as any[]).filter((event: any) => event.status === 'active' || event.status === 'scheduled');
  const completedEvents = (events as any[]).filter((event: any) => event.status === 'completed');

  const recentEvents = (events as any[])
    .sort((a: any, b: any) => {
      if (sortBy === "atoll") {
        return a.atoll.localeCompare(b.atoll);
      } else if (sortBy === "island") {
        return a.island.localeCompare(b.island);
      } else {
        return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
      }
    })
    .slice(0, 6); // Show more events

  const toggleEventExpansion = (eventId: string) => {
    const newExpandedEvents = new Set(expandedEvents);
    if (newExpandedEvents.has(eventId)) {
      newExpandedEvents.delete(eventId);
    } else {
      newExpandedEvents.add(eventId);
    }
    setExpandedEvents(newExpandedEvents);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="p-2 sm:p-4 lg:p-6">
      <div className="mb-6 sm:mb-8">
        <h1 className="text-2xl sm:text-3xl font-bold text-foreground">Dashboard</h1>
        <p className="text-sm sm:text-base text-muted-foreground mt-2">Overview of events and activities across Maldives</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-2 sm:gap-4 mb-6 sm:mb-8">
        <Card data-testid="card-active-events">
          <CardContent className="p-3 sm:p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Active Events</p>
                <p className="text-xl sm:text-2xl font-bold text-foreground" data-testid="text-active-events">
                  {activeEvents.length}
                </p>
              </div>
              <Calendar className="h-6 w-6 sm:h-8 sm:w-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-total-members">
          <CardContent className="p-3 sm:p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Total Members</p>
                <p className="text-xl sm:text-2xl font-bold text-foreground" data-testid="text-total-members">
                  {(users as any[]).length}
                </p>
              </div>
              <Users className="h-6 w-6 sm:h-8 sm:w-8 text-secondary" />
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-visited-islands">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Islands Visited</p>
                <p className="text-2xl font-bold text-foreground" data-testid="text-visited-islands">
                  {milestoneStats?.totalIslands || 0}
                </p>
              </div>
              <MapPin className="h-8 w-8 text-accent" />
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-completed-events">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Completed Events</p>
                <p className="text-2xl font-bold text-foreground" data-testid="text-completed-events">
                  {completedEvents.length}
                </p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Island Check-in Section */}
      <div className="mb-6 sm:mb-8">
        <IslandCheckIn />
      </div>

      {/* Milestone Badges Section */}
      <div className="mb-6 sm:mb-8">
        <MilestoneBadges />
      </div>

      {/* Recent Events */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-semibold">Recent Events</CardTitle>
            <div className="flex items-center gap-2">
              <span className="text-sm text-muted-foreground">Sort by:</span>
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="date">Date</SelectItem>
                  <SelectItem value="atoll">Atoll</SelectItem>
                  <SelectItem value="island">Island</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
        <CardContent className="p-6 pt-0">
          <div className="space-y-3">
            {recentEvents.length === 0 ? (
              <p className="text-muted-foreground text-center py-4" data-testid="text-no-events">
                No events found. Create your first event to get started.
              </p>
            ) : (
              recentEvents.map((event: any) => {
                const isExpanded = expandedEvents.has(event.id);
                return (
                  <div 
                    key={event.id} 
                    className="border rounded-lg p-3"
                    data-testid={`card-event-${event.id}`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <p className="font-medium text-foreground" data-testid={`text-event-title-${event.id}`}>
                            {event.title}
                          </p>
                          <span 
                            className={`px-2 py-1 text-xs rounded-full ${
                              event.status === 'active' ? 'bg-green-100 text-green-800' :
                              event.status === 'scheduled' ? 'bg-blue-100 text-blue-800' :
                              event.status === 'completed' ? 'bg-gray-100 text-gray-800' :
                              'bg-yellow-100 text-yellow-800'
                            }`}
                            data-testid={`text-event-status-${event.id}`}
                          >
                            {event.status}
                          </span>
                        </div>
                        <p className="text-sm text-muted-foreground">
                          {event.atoll} - {event.island}
                        </p>
                        {event.date && !isNaN(new Date(event.date).getTime()) && (
                          <p className="text-sm text-muted-foreground">
                            {new Date(event.date).toLocaleDateString()} at {event.startTime || 'TBD'}
                          </p>
                        )}
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => toggleEventExpansion(event.id)}
                        data-testid={`button-expand-${event.id}`}
                      >
                        {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                      </Button>
                    </div>

                    {isExpanded && (
                      <div className="mt-4 pt-4 border-t space-y-2">
                        {event.eventLocation && (
                          <p className="text-sm text-muted-foreground">
                            <strong>Event Location:</strong> {event.eventLocation}
                          </p>
                        )}
                        {event.waitingLocation && (
                          <p className="text-sm text-muted-foreground">
                            <strong>Waiting Location:</strong> {event.waitingLocation}
                          </p>
                        )}
                        {event.contact && (
                          <div className="text-sm text-muted-foreground flex items-center gap-2">
                            <span><strong>Contact:</strong> {event.contact}</span>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => window.location.href = `tel:${event.contact}`}
                              className="h-6 w-6 p-0"
                              title={`Call ${event.contact}`}
                            >
                              <Phone className="h-3 w-3" />
                            </Button>
                          </div>
                        )}
                        {event.comment && (
                          <p className="text-sm text-foreground">
                            <strong>Description:</strong> {event.comment}
                          </p>
                        )}
                        <p className="text-sm text-muted-foreground">
                          <strong>Participants:</strong> {event.participantCount} participants
                        </p>
                      </div>
                    )}
                  </div>
                );
              })
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

