import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Edit, Archive, Trash2, MapPin, Users, ArchiveX, Phone, Navigation, ChevronDown, ChevronUp, Calendar, Camera } from "lucide-react";
import { ExportButton } from "@/components/ExportButton";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useEffect } from "react";
import Modal from "@/components/modal";
import EventForm from "@/components/event-form";
import { apiRequest } from "@/lib/queryClient";
import { ConfirmDialog } from "@/components/ConfirmDialog";

export default function Events() {
  const { toast } = useToast();
  const { user, isAuthenticated, isLoading } = useAuth();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingEvent, setEditingEvent] = useState<any>(null);
  const [deletingEvent, setDeletingEvent] = useState<any>(null);
  const [archivingEvent, setArchivingEvent] = useState<any>(null);
  const [expandedEvents, setExpandedEvents] = useState<Set<string>>(new Set());
  const [expandedIslands, setExpandedIslands] = useState<Set<string>>(new Set());
  // Initialize filters from URL parameters (for map links)
  const urlParams = new URLSearchParams(window.location.search);
  const [filters, setFilters] = useState({
    atoll: urlParams.get('atoll') || "",
    island: urlParams.get('island') || "",
    status: "",
  });

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: events = [] } = useQuery({
    queryKey: ["/api/events"],
    enabled: isAuthenticated,
  });

  const { data: islandsData = [] } = useQuery({
    queryKey: ["/api/islands"],
    enabled: isAuthenticated,
  });

  // Get unique atolls and islands for filters
  const atolls = Array.from(new Set((islandsData as any[]).map((island: any) => island.atoll))).sort();

  // Filter islands based on selected atoll
  const availableIslands = filters.atoll && filters.atoll !== 'all' 
    ? (islandsData as any[]).filter((island: any) => island.atoll === filters.atoll)
    : (islandsData as any[]);

  const deleteEventMutation = useMutation({
    mutationFn: async (eventId: string) => {
      await apiRequest("DELETE", `/api/events/${eventId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      toast({
        title: "Success",
        description: "Event deleted successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete event",
        variant: "destructive",
      });
    },
  });

  const archiveEventMutation = useMutation({
    mutationFn: async (eventId: string) => {
      // First mark as completed, then archive
      await apiRequest("PATCH", `/api/events/${eventId}`, { status: "completed" });
      await apiRequest("PATCH", `/api/events/${eventId}/archive`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      setArchivingEvent(null);
      toast({
        title: "Success",
        description: "Event completed and archived successfully",
      });
    },
    onError: (error: Error) => {
      setArchivingEvent(null);
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to archive event",
        variant: "destructive",
      });
    },
  });

  const handleArchiveEvent = (event: any) => {
    setArchivingEvent(event);
  };

  const confirmArchiveEvent = () => {
    if (archivingEvent) {
      archiveEventMutation.mutate(archivingEvent.id);
    }
  };

  const filteredEvents = (events as any[]).filter((event: any) => {
    if (filters.atoll && filters.atoll !== 'all' && event.atoll !== filters.atoll) return false;
    if (filters.island && filters.island !== 'all' && event.island !== filters.island) return false;
    if (filters.status && filters.status !== 'all' && event.status !== filters.status) return false;
    return true;
  });

  // Group events by atoll and then by island
  const eventsByAtoll = filteredEvents.reduce((acc, event) => {
    if (!acc[event.atoll]) {
      acc[event.atoll] = {};
    }
    const island = event.island || 'Unknown Island';
    if (!acc[event.atoll][island]) {
      acc[event.atoll][island] = [];
    }
    acc[event.atoll][island].push(event);
    return acc;
  }, {} as Record<string, Record<string, any[]>>);

  const canCreateEvents = (user as any)?.role === 'admin' || (user as any)?.role === 'coordinator';

  const toggleEventExpanded = (eventId: string) => {
    setExpandedEvents(prev => {
      const newSet = new Set(prev);
      if (newSet.has(eventId)) {
        newSet.delete(eventId);
      } else {
        newSet.add(eventId);
      }
      return newSet;
    });
  };

  const toggleIslandExpanded = (islandKey: string) => {
    setExpandedIslands(prev => {
      const newSet = new Set(prev);
      if (newSet.has(islandKey)) {
        newSet.delete(islandKey);
      } else {
        newSet.add(islandKey);
      }
      return newSet;
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="p-2 sm:p-4 lg:p-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground mb-2">Events Management</h1>
          <p className="text-sm sm:text-base text-muted-foreground">Organize and track events across Maldives</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-2 mt-4 sm:mt-0">
          {((user as any)?.role === 'coordinator' || (user as any)?.role === 'admin') && (
            <ExportButton 
              dataType="events"
              label="Events"
              className="w-full sm:w-auto"
            />
          )}
          {canCreateEvents && (
            <Button 
              onClick={() => setShowCreateModal(true)}
              data-testid="button-create-event"
            >
              <Plus className="h-4 w-4 mr-2" />
              Create Event
            </Button>
          )}
        </div>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-foreground mb-2">Filter by Atoll</label>
              <Select value={filters.atoll} onValueChange={(value) => setFilters({...filters, atoll: value, island: "all"})}>
                <SelectTrigger data-testid="select-filter-atoll">
                  <SelectValue placeholder="All Atolls" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Atolls</SelectItem>
                  {atolls.map((atoll, index) => (
                    <SelectItem key={`atoll-${atoll}-${index}`} value={atoll}>
                      {atoll}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="block text-sm font-medium text-foreground mb-2">Filter by Island</label>
              <Select value={filters.island} onValueChange={(value) => setFilters({...filters, island: value})}>
                <SelectTrigger data-testid="select-filter-island">
                  <SelectValue placeholder="All Islands" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Islands</SelectItem>
                  {availableIslands.map((island: any) => (
                    <SelectItem key={island.id} value={island.name}>
                      {island.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="block text-sm font-medium text-foreground mb-2">Filter by Status</label>
              <Select value={filters.status} onValueChange={(value) => setFilters({...filters, status: value})}>
                <SelectTrigger data-testid="select-filter-status">
                  <SelectValue placeholder="All Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="scheduled">Scheduled</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Events List */}
      <div className="space-y-6">
        {filteredEvents.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <Calendar className="h-16 w-16 mx-auto text-muted-foreground/40 mb-4" />
              <h3 className="text-lg font-semibold text-foreground mb-2">No Events Found</h3>
              <p className="text-muted-foreground max-w-md mx-auto" data-testid="text-no-events">
                {canCreateEvents ? "Get started by creating your first event to organize activities across the Maldives." : "Contact an admin to create events for your organization."}
              </p>
              {canCreateEvents && (
                <Button 
                  onClick={() => setShowCreateModal(true)}
                  className="mt-4"
                  data-testid="button-create-first-event"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Create Your First Event
                </Button>
              )}
            </CardContent>
          </Card>
        ) : (
          Object.entries(eventsByAtoll)
            .sort(([a], [b]) => a.localeCompare(b))
            .map(([atoll, islandGroups]) => {
              const totalEvents = Object.values(islandGroups).flat().length;
              return (
                <Card key={atoll}>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <MapPin className="h-5 w-5" />
                      {atoll} Atoll ({totalEvents} events)
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {Object.entries(islandGroups)
                      .sort(([a], [b]) => a.localeCompare(b))
                      .map(([island, islandEvents]) => {
                        const islandKey = `${atoll}-${island}`;
                        const isIslandExpanded = expandedIslands.has(islandKey);
                        return (
                          <div key={islandKey} className="space-y-2">
                            <div 
                              className="flex items-center justify-between border-b pb-2 cursor-pointer hover:bg-gray-50 transition-colors p-2 rounded"
                              onClick={() => toggleIslandExpanded(islandKey)}
                              data-testid={`button-toggle-island-${islandKey}`}
                            >
                              <div className="flex items-center gap-2">
                                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                                <h4 className="font-medium text-sm text-gray-700">
                                  {island} ({islandEvents.length} events)
                                </h4>
                              </div>
                              {isIslandExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                            </div>
                            {isIslandExpanded && (
                              <div className="space-y-4 pl-4">
                                {islandEvents.map((event: any) => {
                      const isExpanded = expandedEvents.has(event.id);
                      return (
                        <Card key={event.id} data-testid={`card-event-${event.id}`} className="border-l-4 border-l-primary/20">
                          <CardContent className="p-3">
                            {/* Compact header - always visible */}
                            <div 
                              className="flex items-center justify-between cursor-pointer"
                              onClick={() => toggleEventExpanded(event.id)}
                              data-testid={`button-expand-${event.id}`}
                            >
                              <div className="flex items-center space-x-3 flex-1">
                                {/* Photo thumbnail */}
                                {event.photoUrl && (
                                  <div className="flex-shrink-0">
                                    <img
                                      src={event.photoUrl}
                                      alt={event.title}
                                      className="w-12 h-12 object-cover rounded-lg border"
                                      data-testid={`img-event-thumbnail-${event.id}`}
                                    />
                                  </div>
                                )}
                                <div className="flex flex-col sm:flex-row sm:items-center sm:space-x-3 space-y-1 sm:space-y-0 flex-1">
                                  <h3 className="text-base font-semibold text-foreground" data-testid={`text-event-title-${event.id}`}>
                                    {event.title}
                                  </h3>
                                  <div className="flex items-center space-x-3">
                                    <span 
                                      className={`px-2 py-1 text-xs rounded-full ${
                                        event.status === 'active' ? 'bg-green-100 text-green-800' :
                                        event.status === 'scheduled' ? 'bg-blue-100 text-blue-800' :
                                        event.status === 'completed' ? 'bg-gray-100 text-gray-800' :
                                        'bg-yellow-100 text-yellow-800'
                                      }`}
                                      data-testid={`text-event-status-${event.id}`}
                                    >
                                      {event.status}
                                    </span>
                                    <div className="flex items-center text-sm text-muted-foreground">
                                      <Users className="h-4 w-4 mr-1" />
                                      <span data-testid={`text-event-participants-${event.id}`}>{event.participantCount}</span>
                                    </div>
                                    {event.photoUrl && (
                                      <div title="Has photo">
                                        <Camera className="h-4 w-4 text-muted-foreground" />
                                      </div>
                                    )}
                                  </div>
                                </div>
                              </div>
                              <div className="flex items-center space-x-2">
                                {/* Quick actions - always visible */}
                                <Button 
                                  variant="ghost" 
                                  size="sm"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    setLocation(`/map?event=${event.id}`);
                                  }}
                                  title="Get Directions"
                                  data-testid={`button-directions-${event.id}`}
                                >
                                  <Navigation className="h-4 w-4" />
                                </Button>
                                {event.contact && (
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      window.location.href = `tel:${event.contact}`;
                                    }}
                                    title={`Call ${event.contact}`}
                                    data-testid={`button-call-${event.id}`}
                                  >
                                    <Phone className="h-4 w-4" />
                                  </Button>
                                )}
                                {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                              </div>
                            </div>

                            {/* Expanded details */}
                            {isExpanded && (
                              <div className="mt-4 pt-4 border-t border-gray-200">
                                {event.eventLocation && (
                                  <p className="mt-2 text-sm text-muted-foreground">
                                    <strong>Event Location:</strong> {event.eventLocation}
                                  </p>
                                )}
                                {event.waitingLocation && (
                                  <p className="mt-1 text-sm text-muted-foreground">
                                    <strong>Waiting Location:</strong> {event.waitingLocation}
                                  </p>
                                )}
                                {event.contact && (
                                  <p className="mt-1 text-sm text-muted-foreground">
                                    <strong>Contact:</strong> {event.contact}
                                  </p>
                                )}
                                {event.comment && (
                                  <p className="mt-3 text-foreground" data-testid={`text-event-comment-${event.id}`}>
                                    {event.comment}
                                  </p>
                                )}

                                {/* Event Photo - Full Size */}
                                {event.photoUrl && (
                                  <div className="mt-4">
                                    <strong className="text-sm text-muted-foreground">Event Photo:</strong>
                                    <Dialog>
                                      <DialogTrigger asChild>
                                        <div className="mt-2 cursor-pointer">
                                          <img
                                            src={event.photoUrl}
                                            alt={event.title}
                                            className="max-w-sm w-full h-48 object-cover rounded-lg border hover:shadow-md transition-shadow"
                                            data-testid={`img-event-photo-${event.id}`}
                                          />
                                          <p className="text-xs text-muted-foreground mt-1">Click to view full size</p>
                                        </div>
                                      </DialogTrigger>
                                      <DialogContent className="max-w-4xl w-full">
                                        <div className="flex flex-col items-center space-y-4">
                                          <h3 className="text-lg font-semibold">{event.title}</h3>
                                          <img
                                            src={event.photoUrl}
                                            alt={event.title}
                                            className="max-w-full max-h-96 object-contain rounded-lg"
                                          />
                                        </div>
                                      </DialogContent>
                                    </Dialog>
                                  </div>
                                )}

                                {/* Admin/Coordinator actions */}
                                {((user as any)?.role === 'admin' || ((user as any)?.role === 'coordinator' && event.createdBy === (user as any)?.id)) && (
                                  <div className="flex space-x-2 mt-4 pt-3 border-t border-gray-100">
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      onClick={() => setEditingEvent(event)}
                                      data-testid={`button-edit-${event.id}`}
                                    >
                                      <Edit className="h-4 w-4 mr-1" />
                                      Edit
                                    </Button>
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      onClick={() => handleArchiveEvent(event)}
                                      disabled={archiveEventMutation.isPending}
                                      title="Complete and Archive Event"
                                      data-testid={`button-archive-${event.id}`}
                                    >
                                      <ArchiveX className="h-4 w-4 mr-1" />
                                      Archive
                                    </Button>
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      onClick={() => setDeletingEvent(event)}
                                      data-testid={`button-delete-${event.id}`}
                                    >
                                      <Trash2 className="h-4 w-4 mr-1" />
                                      Delete
                                    </Button>
                                  </div>
                                )}
                              </div>
                            )}
                          </CardContent>
                        </Card>
                      );
                                })}
                              </div>
                            )}
                          </div>
                        );
                      })}
                  </CardContent>
                </Card>
              );
            })
        )}
      </div>

      {/* Delete Confirmation Dialog */}
      <ConfirmDialog
        isOpen={!!deletingEvent}
        onClose={() => setDeletingEvent(null)}
        onConfirm={() => {
          if (deletingEvent) {
            deleteEventMutation.mutate(deletingEvent.id);
            setDeletingEvent(null);
          }
        }}
        title="Delete Event"
        description={`Are you sure you want to delete "${deletingEvent?.title}"? This action cannot be undone.`}
        confirmText="Delete Event"
        isLoading={deleteEventMutation.isPending}
      />

      {/* Archive Confirmation Dialog */}
      <ConfirmDialog
        isOpen={!!archivingEvent}
        onClose={() => setArchivingEvent(null)}
        onConfirm={confirmArchiveEvent}
        title="Archive Event"
        description={`Mark "${archivingEvent?.title}" as completed and move it to the archive? This will change the event status to completed.`}
        confirmText="Complete & Archive"
        isLoading={archiveEventMutation.isPending}
      />

      {/* Create Event Modal */}
      <Modal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        title="Create New Event"
      >
        <EventForm
          onSuccess={() => {
            setShowCreateModal(false);
            queryClient.invalidateQueries({ queryKey: ["/api/events"] });
          }}
        />
      </Modal>

      {/* Edit Event Modal */}
      <Modal
        isOpen={!!editingEvent}
        onClose={() => setEditingEvent(null)}
        title="Edit Event"
      >
        {editingEvent && (
          <EventForm
            event={editingEvent}
            onSuccess={() => {
              setEditingEvent(null);
              queryClient.invalidateQueries({ queryKey: ["/api/events"] });
            }}
          />
        )}
      </Modal>
    </div>
  );
}

