import { useAuth } from "@/hooks/useAuth";
import { SimpleMap } from "@/components/simple-map";
import { useEffect, useState } from "react";
import { useToast } from "@/hooks/use-toast";

export default function MapPage() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  // Check URL parameters for auto-selection and directions
  const urlParams = new URLSearchParams(window.location.search);
  const eventFromUrl = urlParams.get('event');

  const [selectedEventId, setSelectedEventId] = useState<string | null>(eventFromUrl);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-teal-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 font-medium">Loading Maldives Event Manager...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="h-[calc(100vh-5rem)] lg:h-[calc(100vh-2rem)] overflow-hidden">
      <SimpleMap 
        className="h-full" 
        selectedEventId={selectedEventId}
        onEventSelect={setSelectedEventId}
        autoDirections={!!eventFromUrl}
      />
    </div>
  );
}

client/src/pages/members.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Download, Plus, Edit, MoreVertical, Phone, Mail, Calendar, IdCard, Trash2, ChevronDown, ChevronUp, Users } from "lucide-react";
import { ExportButton } from "@/components/ExportButton";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger, DropdownMenuSeparator } from "@/components/ui/dropdown-menu";
import { ConfirmDialog } from "@/components/ConfirmDialog";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useEffect } from "react";
import { apiRequest } from "@/lib/queryClient";
import { getUserDisplayInfo } from "@/lib/userDisplayUtils";

export default function Members() {
  const { toast } = useToast();
  const { user, isAuthenticated, isLoading } = useAuth();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState("");
  const [roleFilter, setRoleFilter] = useState("");
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [memberToDelete, setMemberToDelete] = useState<any>(null);
  const [expandedMembers, setExpandedMembers] = useState<Set<string>>(new Set());

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: members = [] } = useQuery({
    queryKey: ["/api/users"],
    enabled: isAuthenticated,
  });

  const updateRoleMutation = useMutation({
    mutationFn: async ({ userId, role }: { userId: string; role: string }) => {
      await apiRequest("PATCH", `/api/users/${userId}/role`, { role });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "User role updated successfully",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update user role",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (userId: string) => {
      await apiRequest("DELETE", `/api/users/${userId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User deleted successfully",
      });
      setDeleteDialogOpen(false);
      setMemberToDelete(null);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const handleDeleteUser = (member: any) => {
    setMemberToDelete(member);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = () => {
    if (memberToDelete) {
      deleteMutation.mutate(memberToDelete.id);
    }
  };

  const filteredMembers = (members as any[]).filter((member: any) => {
    const matchesSearch = !searchQuery || 
      member.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      member.lastName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      member.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      member.serviceNumber?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesRole = !roleFilter || roleFilter === 'all' || member.role === roleFilter;

    return matchesSearch && matchesRole;
  });

  const canManageRoles = (user as any)?.role === 'admin';

  const toggleMemberExpanded = (memberId: string) => {
    setExpandedMembers(prev => {
      const newSet = new Set(prev);
      if (newSet.has(memberId)) {
        newSet.delete(memberId);
      } else {
        newSet.add(memberId);
      }
      return newSet;
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="p-2 sm:p-4 lg:p-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground mb-2">Members Directory</h1>
          <p className="text-sm sm:text-base text-muted-foreground">Manage team members and their contact information</p>
        </div>
        <div className="flex space-x-2 mt-4 sm:mt-0">
          {(user as any)?.role === 'admin' && (
            <ExportButton 
              dataType="users"
              label="Members"
              className="w-full sm:w-auto"
            />
          )}
        </div>
      </div>

      {/* Search and Filter */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Input
                placeholder="Search members..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                data-testid="input-search-members"
              />
            </div>
            <div>
              <Select value={roleFilter} onValueChange={setRoleFilter}>
                <SelectTrigger data-testid="select-filter-role">
                  <SelectValue placeholder="All Roles" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Roles</SelectItem>
                  <SelectItem value="admin">Admin</SelectItem>
                  <SelectItem value="coordinator">Coordinator</SelectItem>
                  <SelectItem value="agent">Agent</SelectItem>
                  <SelectItem value="attachment">Attachment</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Members Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6">
        {filteredMembers.length === 0 ? (
          <div className="col-span-full">
            <Card>
              <CardContent className="p-12 text-center">
                <Users className="h-16 w-16 mx-auto text-muted-foreground/40 mb-4" />
                <h3 className="text-lg font-semibold text-foreground mb-2">No Members Found</h3>
                <p className="text-muted-foreground max-w-md mx-auto" data-testid="text-no-members">
                  {searchQuery || roleFilter !== 'all' 
                    ? "Try adjusting your search criteria or filters to find members."
                    : "Your team directory is empty. Members will appear here once they join your organization."
                  }
                </p>
              </CardContent>
            </Card>
          </div>
        ) : (
          filteredMembers.map((member: any) => {
            const isExpanded = expandedMembers.has(member.id);
            const { displayName, initials } = getUserDisplayInfo(member);
            return (
              <Card key={member.id} data-testid={`card-member-${member.id}`}>
                <CardContent className="p-3">
                  {/* Compact header - always visible */}
                  <div 
                    className="flex items-center justify-between cursor-pointer"
                    onClick={() => toggleMemberExpanded(member.id)}
                    data-testid={`button-expand-member-${member.id}`}
                  >
                    <div className="flex items-center space-x-3 flex-1">
                      {member.profileImageUrl ? (
                        <div className="w-10 h-10 rounded-full overflow-hidden border-2 border-background shadow-sm">
                          <img 
                            src={member.profileImageUrl} 
                            alt={displayName}
                            className="w-full h-full object-cover"
                            data-testid={`img-member-avatar-${member.id}`}
                          />
                        </div>
                      ) : (
                        <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center text-primary-foreground font-semibold text-sm">
                          {initials}
                        </div>
                      )}
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <h3 className="font-semibold text-foreground text-base" data-testid={`text-member-name-${member.id}`}>
                            {displayName}
                          </h3>
                          <span 
                            className={`text-xs px-2 py-1 rounded ${
                              member.role === 'admin' ? 'bg-primary text-primary-foreground' :
                              member.role === 'coordinator' ? 'bg-secondary text-secondary-foreground' :
                              'bg-muted text-muted-foreground'
                            }`}
                            data-testid={`text-member-role-${member.id}`}
                          >
                            {member.role}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      {/* Quick actions - always visible */}
                      {member.mobile && (
                        <>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation();
                              window.open(`tel:${member.mobile}`, '_self');
                            }}
                            data-testid={`button-call-${member.id}`}
                          >
                            <Phone className="h-4 w-4" />
                          </Button>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation();
                              window.open(`sms:${member.mobile}`, '_self');
                            }}
                            data-testid={`button-sms-compact-${member.id}`}
                          >
                            ðŸ’¬
                          </Button>
                        </>
                      )}
                      {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                    </div>
                  </div>

                  {/* Expanded details */}
                  {isExpanded && (
                    <div className="mt-4 pt-4 border-t border-gray-200">
                      <div className="space-y-2 text-sm mb-4">
                        {member.serviceNumber && (
                          <div className="flex items-center text-muted-foreground">
                            <IdCard className="w-4 h-4 mr-3" />
                            <span data-testid={`text-member-service-${member.id}`}>{member.serviceNumber}</span>
                          </div>
                        )}
                        {member.mobile && (
                          <div className="flex items-center text-muted-foreground">
                            <Phone className="w-4 h-4 mr-3" />
                            <div className="flex space-x-2">
                              <a 
                                href={`tel:${member.mobile}`}
                                className="text-primary hover:underline"
                                data-testid={`link-call-${member.id}`}
                              >
                                {member.mobile}
                              </a>
                              <span className="text-muted-foreground">|</span>
                              <a 
                                href={`sms:${member.mobile}`}
                                className="text-secondary hover:underline text-sm"
                                data-testid={`link-message-${member.id}`}
                              >
                                Message
                              </a>
                            </div>
                          </div>
                        )}
                        {member.email && (
                          <div className="flex items-center text-muted-foreground">
                            <Mail className="w-4 h-4 mr-3" />
                            <span data-testid={`text-member-email-${member.id}`}>{member.email}</span>
                          </div>
                        )}
                        <div className="flex items-center text-muted-foreground">
                          <Calendar className="w-4 h-4 mr-3" />
                          <span data-testid={`text-member-joined-${member.id}`}>
                            Joined {new Date(member.createdAt).toLocaleDateString()}
                          </span>
                        </div>
                      </div>

                      <div className="flex space-x-2 pt-3 border-t border-gray-100">
                        {canManageRoles && (
                          <Select
                            value={member.role}
                            onValueChange={(role) => updateRoleMutation.mutate({ userId: member.id, role })}
                          >
                            <SelectTrigger className="flex-1" data-testid={`select-role-${member.id}`}>
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="agent">Agent</SelectItem>
                  <SelectItem value="attachment">Attachment</SelectItem>
                              <SelectItem value="coordinator">Coordinator</SelectItem>
                              <SelectItem value="admin">Admin</SelectItem>
                            </SelectContent>
                          </Select>
                        )}
                        {member.mobile && (
                          <Button 
                            variant="outline" 
                            size="sm"
                            onClick={() => window.open(`sms:${member.mobile}`, '_self')}
                            data-testid={`button-sms-${member.id}`}
                          >
                            ðŸ’¬
                          </Button>
                        )}
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm" data-testid={`button-more-${member.id}`}>
                              <MoreVertical className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            {canManageRoles && member.id !== (user as any)?.id && (
                              <DropdownMenuItem 
                                onClick={() => handleDeleteUser(member)}
                                className="text-red-600 hover:text-red-700 hover:bg-red-50"
                                data-testid={`menu-delete-${member.id}`}
                              >
                                <Trash2 className="w-4 h-4 mr-2" />
                                Delete User
                              </DropdownMenuItem>
                            )}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    </div>
                  )}
                </CardContent>
              </Card>
            );
          })
        )}
      </div>

      <ConfirmDialog
        isOpen={deleteDialogOpen}
        onClose={() => {
          setDeleteDialogOpen(false);
          setMemberToDelete(null);
        }}
        onConfirm={confirmDelete}
        title="Delete User"
        description={`Are you sure you want to delete ${getUserDisplayInfo(memberToDelete).displayName}? This action cannot be undone.`}
        confirmText="Delete User"
        cancelText="Cancel"
        variant="destructive"
        isLoading={deleteMutation.isPending}
      />
    </div>
  );
}

