const equipmentTypes = [
  "tools",
  "electronics",
  "visual",
  "network",
  "others"
];

const equipmentStatuses = [
  "available",
  "in_use", 
  "maintenance",
  "damaged"
];

const equipmentConditions = [
  "excellent",
  "good",
  "fair", 
  "poor"
];

export default function EquipmentForm({ onSuccess, editingEquipment }: EquipmentFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch islands data for dynamic dropdown
  const { data: islandsData = [] } = useQuery({
    queryKey: ["/api/islands"],
  });

  const form = useForm<InsertEquipment>({
    resolver: zodResolver(insertEquipmentSchema),
    defaultValues: editingEquipment ? {
      name: editingEquipment.name,
      type: editingEquipment.type,
      quantity: editingEquipment.quantity,
      status: editingEquipment.status,
      atoll: editingEquipment.atoll,
      island: editingEquipment.island || "",
      description: editingEquipment.description || "",
      condition: editingEquipment.condition,
    } : {
      name: "",
      type: "tools",
      quantity: 1,
      status: "available",
      atoll: "Kaafu",
      island: "",
      description: "",
      condition: "good",
    }
  });

  const createEquipmentMutation = useMutation({
    mutationFn: (data: InsertEquipment) => fetch('/api/equipment', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });
      toast({
        title: "Equipment Added",
        description: "Equipment has been successfully added to the inventory",
      });
      form.reset();
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Add Equipment",
        description: error.message || "Failed to add equipment",
        variant: "destructive",
      });
    }
  });

  const updateEquipmentMutation = useMutation({
    mutationFn: (data: InsertEquipment) => fetch(`/api/equipment/${editingEquipment?.id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });
      toast({
        title: "Equipment Updated",
        description: "Equipment details have been successfully updated",
      });
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Update Equipment",
        description: error.message || "Failed to update equipment",
        variant: "destructive",
      });
    }
  });

  const onSubmit = (data: InsertEquipment) => {
    if (editingEquipment) {
      updateEquipmentMutation.mutate(data);
    } else {
      createEquipmentMutation.mutate(data);
    }
  };

  const isLoading = createEquipmentMutation.isPending || updateEquipmentMutation.isPending;

  // Get unique atolls from islands data
  const atolls = Array.from(new Set((islandsData as any[]).map((island: any) => island.atoll))).sort();

  // Filter islands by selected atoll
  const availableIslands = form.watch("atoll") 
    ? (islandsData as any[]).filter((island: any) => island.atoll === form.watch("atoll"))
    : (islandsData as any[]);

  return (
    <Card className="w-full max-w-2xl">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Package className="h-5 w-5" />
          {editingEquipment ? "Edit Equipment" : "Add New Equipment"}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Equipment Name</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="e.g., Diving Tank, First Aid Kit"
                        {...field}
                        data-testid="input-equipment-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Equipment Type</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-equipment-type">
                          <SelectValue placeholder="Select equipment type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {equipmentTypes.map((type) => (
                          <SelectItem key={type} value={type}>
                            {type.charAt(0).toUpperCase() + type.slice(1)}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="quantity"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Quantity</FormLabel>
                    <FormControl>
                      <Input 
                        type="number"
                        min="1"
                        placeholder="1"
                        {...field}
                        onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                        data-testid="input-equipment-quantity"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Status</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-equipment-status">
                          <SelectValue placeholder="Select status" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {equipmentStatuses.map((status) => (
                          <SelectItem key={status} value={status}>
                            {status.replace('_', ' ').charAt(0).toUpperCase() + status.replace('_', ' ').slice(1)}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="atoll"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Atoll</FormLabel>
                    <Select 
                      onValueChange={(value) => {
                        field.onChange(value);
                        // Clear island selection when atoll changes
                        form.setValue("island", "");
                      }} 
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger data-testid="select-equipment-atoll">
                          <SelectValue placeholder="Select atoll" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {atolls.map((atoll, index) => (
                          <SelectItem key={`equipment-atoll-${index}`} value={atoll}>
                            {atoll}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="island"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Island</FormLabel>
                    <Select 
                      onValueChange={field.onChange}
                      value={field.value || ""}
                      disabled={!form.watch("atoll")}
                    >
                      <FormControl>
                        <SelectTrigger data-testid="select-equipment-island">
                          <SelectValue placeholder={form.watch("atoll") ? "Select Island" : "Select Atoll First"} />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {availableIslands.map((island: any) => (
                          <SelectItem key={island.id} value={island.name}>
                            {island.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="condition"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Condition</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-equipment-condition">
                          <SelectValue placeholder="Select condition" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {equipmentConditions.map((condition) => (
                          <SelectItem key={condition} value={condition}>
                            {condition.charAt(0).toUpperCase() + condition.slice(1)}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description (Optional)</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Additional details about the equipment..."
                      className="min-h-[80px]"
                      {...field}
                      value={field.value || ""}
                      data-testid="textarea-equipment-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-2 pt-4">
              <Button 
                type="submit" 
                disabled={isLoading}
                className="flex items-center gap-2"
                data-testid="button-submit-equipment"
              >
                <Plus className="h-4 w-4" />
                {isLoading ? "Saving..." : editingEquipment ? "Update Equipment" : "Add Equipment"}
              </Button>

              {form.formState.isDirty && (
                <Button 
                  type="button" 
                  variant="outline"
                  onClick={() => form.reset()}
                  data-testid="button-reset-equipment-form"
                >
                  Reset
                </Button>
              )}
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

