// Geocoding service using OpenStreetMap Nominatim (free)
// Allows searching for locations by name and converting to coordinates

interface GeocodingResult {
  display_name: string;
  lat: string;
  lon: string;
  place_id: string;
  type: string;
  importance: number;
  boundingbox: [string, string, string, string]; // [south, north, west, east]
}

interface SearchResult {
  name: string;
  coordinates: [number, number]; // [lat, lng]
  type: string;
  boundingBox?: {
    north: number;
    south: number; 
    east: number;
    west: number;
  };
}

class GeocodingService {
  private baseUrl = 'https://nominatim.openstreetmap.org';

  // Search for locations by name
  async searchLocation(query: string, limit: number = 5): Promise<SearchResult[]> {
    try {
      // Add bias towards Maldives for better local results
      const searchQuery = `${query}, Maldives`;

      const url = new URL(`${this.baseUrl}/search`);
      url.searchParams.set('q', searchQuery);
      url.searchParams.set('format', 'json');
      url.searchParams.set('limit', limit.toString());
      url.searchParams.set('countrycodes', 'mv'); // Restrict to Maldives
      url.searchParams.set('addressdetails', '1');
      url.searchParams.set('extratags', '1');

      const response = await fetch(url.toString(), {
        headers: {
          'User-Agent': 'MaldivesEventManager/1.0' // Required by Nominatim
        }
      });

      if (!response.ok) {
        throw new Error(`Geocoding failed: ${response.status}`);
      }

      const results: GeocodingResult[] = await response.json();

      return results.map(result => ({
        name: result.display_name,
        coordinates: [parseFloat(result.lat), parseFloat(result.lon)] as [number, number],
        type: result.type || 'unknown',
        boundingBox: {
          north: parseFloat(result.boundingbox[1]),
          south: parseFloat(result.boundingbox[0]),
          west: parseFloat(result.boundingbox[2]),
          east: parseFloat(result.boundingbox[3])
        }
      }));

    } catch (error) {
      return [];
    }
  }

  // Search specifically for Maldives islands and atolls
  async searchMaldivesLocations(query: string): Promise<SearchResult[]> {
    try {
      // Search for islands, resorts, and atolls in Maldives
      const searches = [
        `${query} island maldives`,
        `${query} atoll maldives`,
        `${query} resort maldives`
      ];

      const allResults: SearchResult[] = [];

      for (const searchTerm of searches) {
        const url = new URL(`${this.baseUrl}/search`);
        url.searchParams.set('q', searchTerm);
        url.searchParams.set('format', 'json');
        url.searchParams.set('limit', '3');
        url.searchParams.set('countrycodes', 'mv');
        url.searchParams.set('addressdetails', '1');

        const response = await fetch(url.toString(), {
          headers: {
            'User-Agent': 'MaldivesEventManager/1.0'
          }
        });

        if (response.ok) {
          const results: GeocodingResult[] = await response.json();

          const searchResults = results.map(result => ({
            name: result.display_name,
            coordinates: [parseFloat(result.lat), parseFloat(result.lon)] as [number, number],
            type: result.type || 'unknown',
            boundingBox: {
              north: parseFloat(result.boundingbox[1]),
              south: parseFloat(result.boundingbox[0]),
              west: parseFloat(result.boundingbox[2]),
              east: parseFloat(result.boundingbox[3])
            }
          }));

          allResults.push(...searchResults);
        }

        // Small delay between requests to be respectful
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      // Remove duplicates and sort by relevance
      const uniqueResults = allResults.filter((result, index, self) =>
        index === self.findIndex(r => 
          Math.abs(r.coordinates[0] - result.coordinates[0]) < 0.001 &&
          Math.abs(r.coordinates[1] - result.coordinates[1]) < 0.001
        )
      );

      return uniqueResults.slice(0, 5); // Return top 5 results

    } catch (error) {
      return [];
    }
  }

  // Reverse geocoding - get location name from coordinates
  async reverseGeocode(lat: number, lng: number): Promise<string | null> {
    try {
      const url = new URL(`${this.baseUrl}/reverse`);
      url.searchParams.set('lat', lat.toString());
      url.searchParams.set('lon', lng.toString());
      url.searchParams.set('format', 'json');
      url.searchParams.set('addressdetails', '1');

      const response = await fetch(url.toString(), {
        headers: {
          'User-Agent': 'MaldivesEventManager/1.0'
        }
      });

      if (!response.ok) {
        throw new Error(`Reverse geocoding failed: ${response.status}`);
      }

      const result = await response.json();
      return result.display_name || null;

    } catch (error) {
      return null;
    }
  }

  // Get popular Maldives destinations
  getPopularDestinations(): SearchResult[] {
    return [
      {
        name: "MalÃ©, Kaafu Atoll, Maldives",
        coordinates: [4.1755, 73.5093],
        type: "capital"
      },
      {
        name: "HulhumalÃ©, Kaafu Atoll, Maldives", 
        coordinates: [4.2167, 73.5333],
        type: "city"
      },
      {
        name: "Velana International Airport, HulhulÃ©, Maldives",
        coordinates: [4.1918, 73.5294],
        type: "airport"
      },
      {
        name: "Addu Atoll, Maldives",
        coordinates: [-0.6847, 73.1567],
        type: "atoll"
      },
      {
        name: "Baa Atoll, Maldives",
        coordinates: [5.3167, 72.9833],
        type: "atoll"
      }
    ];
  }
}

export const geocodingService = new GeocodingService();

