// Street routing service using OSRM (completely free, no API key required)
// Provides turn-by-turn directions and accurate route calculations
import * as polyline from 'polyline';

interface RouteStep {
  instruction: string;
  distance: number;
  duration: number;
  maneuver: {
    location: [number, number];
    type: string;
  };
}

interface RouteResult {
  coordinates: [number, number][];
  distance: number; // in meters
  duration: number; // in seconds
  steps: RouteStep[];
}

interface RoutingOptions {
  profile?: 'driving' | 'walking' | 'cycling';
  avoid_features?: string[];
}

class RoutingService {
  private baseUrl = 'https://router.project-osrm.org';

  async getRoute(
    start: [number, number], 
    end: [number, number], 
    options: RoutingOptions = {}
  ): Promise<RouteResult | null> {

    // Try multiple routing profiles in order of preference for street-level routing
    const profiles = ['foot', 'driving'];
    const requestedProfile = options.profile || 'walking';

    // Convert walking to foot for OSRM
    const osmProfile = requestedProfile === 'walking' ? 'foot' : requestedProfile;

    // Put requested profile first, then try others
    const tryProfiles = [osmProfile, ...profiles.filter(p => p !== osmProfile)];

    for (const profile of tryProfiles) {
      try {

        // OSRM API format: /route/v1/{profile}/{coordinates}?options
        const coordinatesString = `${start[1]},${start[0]};${end[1]},${end[0]}`; // OSRM uses lng,lat format
        const url = `${this.baseUrl}/route/v1/${profile}/${coordinatesString}?steps=true&geometries=polyline&overview=full&alternatives=false`;

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Accept': 'application/json'
          }
        });

        if (!response.ok) {
          continue; // Try next profile
        }

        const data = await response.json();

        if (!data.routes || data.routes.length === 0) {
          continue; // Try next profile
        }

        const route = data.routes[0];

        // Decode OSRM polyline geometry
        let routeCoordinates: [number, number][] = [];

        if (route.geometry && typeof route.geometry === 'string') {
          try {
            const decoded = polyline.decode(route.geometry);
            routeCoordinates = decoded.map((coord: number[]) => [coord[0] as number, coord[1] as number]);

            // Accept any route with coordinates (even if it's just 2 points)
            if (routeCoordinates.length >= 2) {

              // Process OSRM steps
              const steps: RouteStep[] = [];

              if (route.legs && route.legs[0] && route.legs[0].steps) {
                route.legs[0].steps.forEach((step: any) => {
                  if (step.maneuver && step.maneuver.location) {
                    steps.push({
                      instruction: step.maneuver.instruction || `Continue ${step.name || ''}`,
                      distance: step.distance || 0,
                      duration: step.duration || 0,
                      maneuver: {
                        location: [step.maneuver.location[1], step.maneuver.location[0]], // Convert to [lat, lng]
                        type: step.maneuver.type || 'continue'
                      }
                    });
                  }
                });
              }

              // Add arrival step if not present
              if (steps.length === 0 || !steps[steps.length - 1].instruction.includes('arrive')) {
                steps.push({
                  instruction: 'Arrive at destination',
                  distance: 0,
                  duration: 0,
                  maneuver: {
                    location: end,
                    type: 'arrive'
                  }
                });
              }

              return {
                coordinates: routeCoordinates,
                distance: route.distance || 0,
                duration: route.duration || 0,
                steps
              };
            } else {
            }
          } catch (error) {
            continue; // Try next profile
          }
        }
      } catch (error) {
        continue; // Try next profile
      }
    }

    // If all routing profiles failed, fall back to straight line
    return this.createStraightLineRoute(start, end);
  }

  // Enhanced fallback to straight line route with multiple waypoints for better visualization
  private createStraightLineRoute(start: [number, number], end: [number, number]): RouteResult {
    const distance = this.calculateDistance(start[0], start[1], end[0], end[1]) * 1000; // Convert to meters
    const bearing = this.calculateBearing(start[0], start[1], end[0], end[1]);

    // Create intermediate points for better visualization (every ~1km)
    const numSegments = Math.max(2, Math.floor(distance / 1000));
    const coordinates: [number, number][] = [];

    for (let i = 0; i <= numSegments; i++) {
      const ratio = i / numSegments;
      const lat = start[0] + (end[0] - start[0]) * ratio;
      const lng = start[1] + (end[1] - start[1]) * ratio;
      coordinates.push([lat, lng]);
    }

    return {
      coordinates,
      distance,
      duration: distance / 1.4, // Assume walking speed of 1.4 m/s (5 km/h)
      steps: [
        {
          instruction: `Head ${this.getCompassDirection(bearing)} towards destination`,
          distance,
          duration: distance / 1.4,
          maneuver: {
            location: start,
            type: 'start'
          }
        },
        {
          instruction: `Continue ${this.getCompassDirection(bearing)} for ${this.formatDistance(distance)}`,
          distance: distance * 0.8,
          duration: (distance * 0.8) / 1.4,
          maneuver: {
            location: [start[0] + (end[0] - start[0]) * 0.5, start[1] + (end[1] - start[1]) * 0.5],
            type: 'continue'
          }
        },
        {
          instruction: `Arrive at destination`,
          distance: 0,
          duration: 0,
          maneuver: {
            location: end,
            type: 'arrive'
          }
        }
      ]
    };
  }

  // Calculate distance between two points (Haversine formula)
  private calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  }

  // Calculate bearing between two points
  private calculateBearing(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const lat1Rad = lat1 * Math.PI / 180;
    const lat2Rad = lat2 * Math.PI / 180;
    const y = Math.sin(dLon) * Math.cos(lat2Rad);
    const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLon);
    let bearing = Math.atan2(y, x) * 180 / Math.PI;
    return (bearing + 360) % 360;
  }

  // Convert bearing to compass direction
  private getCompassDirection(bearing: number): string {
    const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
    const index = Math.round(bearing / 22.5) % 16;
    return directions[index];
  }

  // Format distance for display
  formatDistance(meters: number): string {
    if (meters < 1000) {
      return `${Math.round(meters)}m`;
    }
    return `${(meters / 1000).toFixed(1)}km`;
  }

  // Format duration for display
  formatDuration(seconds: number): string {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  }
}

export const routingService = new RoutingService();

