// Tile caching service for offline map functionality
// Uses IndexedDB for efficient storage of tile images
import L from 'leaflet';

interface TileData {
  key: string;
  blob: Blob;
  timestamp: number;
  x: number;
  y: number;
  z: number;
}

interface SyncStatus {
  lastSync: number;
  totalTiles: number;
  cachedTiles: number;
  isActive: boolean;
}

class TileCacheService {
  private dbName = 'MaldivesMapTiles';
  private dbVersion = 1;
  private storeName = 'tiles';
  private db: IDBDatabase | null = null;
  private syncInterval: number | null = null;

  // Maldives bounds for tile prefetching
  private maldivesBounds = {
    north: 7.091,
    south: -0.693,
    east: 73.754,
    west: 72.693
  };

  async init(): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.dbVersion);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        this.db = request.result;
        resolve();
      };

      request.onupgradeneeded = (event) => {
        const db = (event.target as IDBOpenDBRequest).result;
        if (!db.objectStoreNames.contains(this.storeName)) {
          const store = db.createObjectStore(this.storeName, { keyPath: 'key' });
          store.createIndex('timestamp', 'timestamp', { unique: false });
        }
      };
    });
  }

  // Generate tile URL for OpenStreetMap
  private getTileUrl(x: number, y: number, z: number): string {
    const servers = ['a', 'b', 'c'];
    const server = servers[Math.abs(x + y) % servers.length];
    return `https://${server}.tile.openstreetmap.org/${z}/${x}/${y}.png`;
  }

  // Generate unique key for tile
  private getTileKey(x: number, y: number, z: number): string {
    return `tile_${z}_${x}_${y}`;
  }

  // Get cached tile
  async getCachedTile(x: number, y: number, z: number): Promise<Blob | null> {
    if (!this.db) await this.init();

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction([this.storeName], 'readonly');
      const store = transaction.objectStore(this.storeName);
      const request = store.get(this.getTileKey(x, y, z));

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        const result = request.result;
        if (result && this.isTileValid(result.timestamp)) {
          resolve(result.blob);
        } else {
          resolve(null);
        }
      };
    });
  }

  // Cache a tile by coordinates
  async cacheTile(x: number, y: number, z: number, blob: Blob): Promise<void> {
    if (!this.db) await this.init();

    const tileData: TileData = {
      key: this.getTileKey(x, y, z),
      blob,
      timestamp: Date.now(),
      x,
      y,
      z
    };

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const request = store.put(tileData);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => resolve();
    });
  }

  // Cache a tile by URL (for download functions)
  async cacheTileByUrl(url: string, blob: Blob): Promise<void> {
    // Extract x, y, z from URL like https://tile.openstreetmap.org/12/2879/1970.png
    const match = url.match(/\/(\d+)\/(\d+)\/(\d+)\.png$/);
    if (!match) {
      throw new Error(`Invalid tile URL format: ${url}`);
    }

    const z = parseInt(match[1]);
    const x = parseInt(match[2]); 
    const y = parseInt(match[3]);

    return this.cacheTile(x, y, z, blob);
  }

  // Get cached tile by URL (returns data URL string for direct use in img src)
  async getCachedTileByUrl(url: string): Promise<string | null> {
    // Extract x, y, z from URL like https://a.tile.openstreetmap.org/12/2879/1970.png
    const match = url.match(/\/(\d+)\/(\d+)\/(\d+)\.png$/);
    if (!match) {
      return null;
    }

    const z = parseInt(match[1]);
    const x = parseInt(match[2]); 
    const y = parseInt(match[3]);

    const blob = await this.getCachedTile(x, y, z);
    if (blob) {
      return URL.createObjectURL(blob);
    }
    return null;
  }

  // Download and cache a tile
  async downloadAndCacheTile(x: number, y: number, z: number): Promise<boolean> {
    try {
      const url = this.getTileUrl(x, y, z);
      const response = await fetch(url);

      if (!response.ok) {
        console.warn(`Failed to download tile ${z}/${x}/${y}: ${response.status}`);
        return false;
      }

      const blob = await response.blob();
      await this.cacheTile(x, y, z, blob);
      return true;
    } catch (error) {
      console.error(`Error downloading tile ${z}/${x}/${y}:`, error);
      return false;
    }
  }

  // Check if a tile is still valid (not older than 7 days)
  private isTileValid(timestamp: number): boolean {
    const sevenDays = 7 * 24 * 60 * 60 * 1000;
    return Date.now() - timestamp < sevenDays;
  }

  // Convert lat/lng to tile coordinates
  private latLngToTile(lat: number, lng: number, zoom: number): { x: number; y: number } {
    const latRad = lat * Math.PI / 180;
    const n = Math.pow(2, zoom);
    const x = Math.floor((lng + 180) / 360 * n);
    const y = Math.floor((1 - Math.asinh(Math.tan(latRad)) / Math.PI) / 2 * n);
    return { x, y };
  }

  // Generate tile coordinates for a bounding box
  private getTilesInBounds(north: number, south: number, east: number, west: number, zoom: number): Array<{x: number, y: number, z: number}> {
    const nw = this.latLngToTile(north, west, zoom);
    const se = this.latLngToTile(south, east, zoom);

    const tiles = [];
    for (let x = nw.x; x <= se.x; x++) {
      for (let y = nw.y; y <= se.y; y++) {
        tiles.push({ x, y, z: zoom });
      }
    }
    return tiles;
  }

  // Sync tiles for Maldives region
  async syncMaldivesTiles(onProgress?: (progress: number, total: number) => void): Promise<void> {
    const zoomLevels = [7, 8, 9, 10, 11, 12]; // Cover from country view to detailed island view
    let allTiles: Array<{x: number, y: number, z: number}> = [];

    // Generate all tile coordinates
    for (const zoom of zoomLevels) {
      const tiles = this.getTilesInBounds(
        this.maldivesBounds.north,
        this.maldivesBounds.south,
        this.maldivesBounds.east,
        this.maldivesBounds.west,
        zoom
      );
      allTiles = allTiles.concat(tiles);
    }


    let downloaded = 0;
    const batchSize = 50; // Higher limit from our own server (not OSM)

    for (let i = 0; i < allTiles.length; i += batchSize) {
      const batch = allTiles.slice(i, i + batchSize);

      const promises = batch.map(async (tile) => {
        // Check if tile already exists and is valid
        const cached = await this.getCachedTile(tile.x, tile.y, tile.z);
        if (cached) {
          return true; // Skip if already cached and valid
        }

        return await this.downloadAndCacheTile(tile.x, tile.y, tile.z);
      });

      const results = await Promise.all(promises);
      downloaded += results.filter(Boolean).length;

      if (onProgress) {
        onProgress(downloaded, allTiles.length);
      }

      // Small delay between batches for our own server 
      if (i + batchSize < allTiles.length) {
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    }

    this.updateSyncStatus(allTiles.length, downloaded);
  }

  // Update sync status in localStorage
  private updateSyncStatus(totalTiles: number, cachedTiles: number): void {
    const status: SyncStatus = {
      lastSync: Date.now(),
      totalTiles,
      cachedTiles,
      isActive: false
    };
    localStorage.setItem('maldives_map_sync_status', JSON.stringify(status));
  }

  // Get sync status
  getSyncStatus(): SyncStatus | null {
    const stored = localStorage.getItem('maldives_map_sync_status');
    return stored ? JSON.parse(stored) : null;
  }

  // Setup daily sync (runs every 24 hours)
  setupDailySync(): void {
    // Clear existing interval
    if (this.syncInterval) {
      clearInterval(this.syncInterval);
    }

    // Check if we need to sync on startup
    const status = this.getSyncStatus();
    const oneDayMs = 24 * 60 * 60 * 1000;

    if (!status || Date.now() - status.lastSync > oneDayMs) {
      this.syncMaldivesTiles().catch(console.error);
    }

    // Set up daily interval
    this.syncInterval = window.setInterval(() => {
      this.syncMaldivesTiles().catch(console.error);
    }, oneDayMs);
  }

  // Stop daily sync
  stopDailySync(): void {
    if (this.syncInterval) {
      clearInterval(this.syncInterval);
      this.syncInterval = null;
    }
  }

  // Manual sync trigger
  async manualSync(onProgress?: (progress: number, total: number) => void): Promise<void> {
    const status = this.getSyncStatus();
    if (status?.isActive) {
      return;
    }

    // Mark sync as active
    if (status) {
      status.isActive = true;
      localStorage.setItem('maldives_map_sync_status', JSON.stringify(status));
    }

    try {
      await this.syncMaldivesTiles(onProgress);
    } finally {
      // Mark sync as inactive
      const updatedStatus = this.getSyncStatus();
      if (updatedStatus) {
        updatedStatus.isActive = false;
        localStorage.setItem('maldives_map_sync_status', JSON.stringify(updatedStatus));
      }
    }
  }

  // Clear all cached tiles
  async clearCache(): Promise<void> {
    if (!this.db) await this.init();

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const request = store.clear();

      request.onerror = () => reject(request.error);
      request.onsuccess = () => resolve();
    });
  }

  // Clean up old tiles (older than 30 days)
  async cleanupOldTiles(): Promise<number> {
    if (!this.db) await this.init();

    const thirtyDays = 30 * 24 * 60 * 60 * 1000;
    const cutoffTime = Date.now() - thirtyDays;
    let deletedCount = 0;

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const index = store.index('timestamp');
      const request = index.openCursor(IDBKeyRange.upperBound(cutoffTime));

      request.onerror = () => reject(request.error);
      request.onsuccess = (event) => {
        const cursor = (event.target as IDBRequest).result;
        if (cursor) {
          cursor.delete();
          deletedCount++;
          cursor.continue();
        } else {
          resolve(deletedCount);
        }
      };
    });
  }

  // Get cache statistics
  async getCacheStats(): Promise<{totalTiles: number, totalSize: number}> {
    if (!this.db) await this.init();

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction([this.storeName], 'readonly');
      const store = transaction.objectStore(this.storeName);
      const request = store.getAll();

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        const tiles = request.result;
        const totalSize = tiles.reduce((sum, tile) => sum + tile.blob.size, 0);
        resolve({
          totalTiles: tiles.length,
          totalSize
        });
      };
    });
  }
}

// Create singleton instance
export const tileCacheService = new TileCacheService();

// Helper function to create object URL from cached tile
export function createTileUrl(blob: Blob): string {
  return URL.createObjectURL(blob);
}

// Custom tile layer that uses cached tiles
export class OfflineTileLayer extends L.TileLayer {
  createTile(coords: L.Coords, done: L.DoneCallback): HTMLImageElement {
    const tile = document.createElement('img');

    L.DomEvent.on(tile, 'load', L.Util.bind(this._tileOnLoad, this, done, tile));
    L.DomEvent.on(tile, 'error', L.Util.bind(this._tileOnError, this, done, tile));

    if (this.options.crossOrigin || this.options.crossOrigin === '') {
      tile.crossOrigin = this.options.crossOrigin === true ? '' : this.options.crossOrigin;
    }

    // Try to load from cache first
    this.loadTileFromCache(coords, tile, done);

    return tile;
  }

  private async loadTileFromCache(coords: L.Coords, tile: HTMLImageElement, done: L.DoneCallback): Promise<void> {
    try {
      const cachedBlob = await tileCacheService.getCachedTile(coords.x, coords.y, coords.z);

      if (cachedBlob) {
        // Use cached tile
        tile.src = createTileUrl(cachedBlob);
      } else {
        // Fall back to network and cache the tile
        const url = this.getTileUrl(coords);
        tile.src = url;

        // Cache the tile when it loads
        tile.addEventListener('load', async () => {
          try {
            const response = await fetch(url);
            if (response.ok) {
              const blob = await response.blob();
              await tileCacheService.cacheTile(coords.x, coords.y, coords.z, blob);
            }
          } catch (error) {
            console.warn('Failed to cache tile:', error);
          }
        });
      }
    } catch (error) {
      console.error('Error loading tile from cache:', error);
      // Fall back to network
      tile.src = this.getTileUrl(coords);
    }
  }
}

