import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { usePermissions } from "@/hooks/usePermissions";
import { PermissionGuard } from "@/components/PermissionGuard";
import { Package, Plus, Search, Edit, Trash2, MapPin, Settings, ChevronDown, ChevronUp } from "lucide-react";
import { ExportButton } from "@/components/ExportButton";
import EquipmentForm from "@/components/equipment-form";
import EquipmentTransferForm from "@/components/equipment-transfer-form";
import EquipmentTransferHistory from "@/components/equipment-transfer-history";
import { type Equipment } from "../../../shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { ConfirmDialog } from "@/components/ConfirmDialog";

const maldivesAtolls = [
  "All Atolls", "Haa Alif", "Haa Dhaal", "Shaviyani", "Noonu", "Raa", "Baa", "Kaafu",
  "Alifu Alifu", "Alifu Dhaal", "Vaavu", "Meemu", "Faafu", "Dhaal",
  "Thaa", "Laamu", "Gaafu Alifu", "Gaafu Dhaal", "Gnaviyani", "Seenu"
];

const statusColors = {
  available: "bg-green-100 text-green-800",
  in_use: "bg-blue-100 text-blue-800", 
  maintenance: "bg-yellow-100 text-yellow-800",
  damaged: "bg-red-100 text-red-800"
};

const conditionColors = {
  excellent: "bg-green-100 text-green-800",
  good: "bg-blue-100 text-blue-800",
  fair: "bg-yellow-100 text-yellow-800", 
  poor: "bg-red-100 text-red-800"
};

export default function EquipmentPage() {
  const [showForm, setShowForm] = useState(false);
  const [editingEquipment, setEditingEquipment] = useState<Equipment | null>(null);
  const [selectedAtoll, setSelectedAtoll] = useState("All Atolls");
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("all");
  const [filterStatus, setFilterStatus] = useState("all");
  const [deletingEquipment, setDeletingEquipment] = useState<Equipment | null>(null);
  const [expandedEquipment, setExpandedEquipment] = useState<Set<string>>(new Set());
  const [expandedIslands, setExpandedIslands] = useState<Set<string>>(new Set());

  const { toast } = useToast();
  const { user } = useAuth();
  const { canViewEquipment, canEditEquipment } = usePermissions();
  const queryClient = useQueryClient();

  // Build query parameters
  const queryParams = new URLSearchParams();
  if (selectedAtoll !== "All Atolls") {
    queryParams.append('atoll', selectedAtoll);
  }
  if (filterType !== "all") {
    queryParams.append('type', filterType);
  }
  if (filterStatus !== "all") {
    queryParams.append('status', filterStatus);
  }

  const { data: equipment = [], isLoading } = useQuery<Equipment[]>({
    queryKey: ['/api/equipment', queryParams.toString()],
    queryFn: () => fetch(`/api/equipment?${queryParams.toString()}`).then(res => res.json()),
    staleTime: 30 * 1000, // 30 seconds
  });

  const deleteEquipmentMutation = useMutation({
    mutationFn: (id: string) => fetch(`/api/equipment/${id}`, {
      method: 'DELETE',
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });
      toast({
        title: "Equipment Deleted",
        description: "Equipment has been removed from inventory",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Delete Equipment",
        description: error.message || "Failed to delete equipment",
        variant: "destructive",
      });
    }
  });

  // Filter equipment based on search query and hide 0 quantity items
  const filteredEquipment = equipment.filter(item =>
    item.quantity > 0 && (
      !searchQuery || 
      item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.type.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.atoll.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (item.island && item.island.toLowerCase().includes(searchQuery.toLowerCase())) ||
      (item.description && item.description.toLowerCase().includes(searchQuery.toLowerCase()))
    )
  );

  // Group equipment by atoll and island for display
  const equipmentByAtoll = filteredEquipment.reduce((acc, item) => {
    if (!acc[item.atoll]) {
      acc[item.atoll] = {};
    }
    const island = item.island || 'Unknown Island';
    if (!acc[item.atoll][island]) {
      acc[item.atoll][island] = [];
    }
    acc[item.atoll][island].push(item);
    return acc;
  }, {} as Record<string, Record<string, Equipment[]>>);

  // Sort equipment within each island group by name
  Object.keys(equipmentByAtoll).forEach(atoll => {
    Object.keys(equipmentByAtoll[atoll]).forEach(island => {
      equipmentByAtoll[atoll][island].sort((a, b) => a.name.localeCompare(b.name));
    });
  });

  const handleEdit = (equipment: Equipment) => {
    setEditingEquipment(equipment);
    setShowForm(true);
  };

  const handleDelete = (id: string, name: string) => {
    const equipment = filteredEquipment.find(e => e.id === id);
    if (equipment) {
      setDeletingEquipment(equipment);
    }
  };

  const handleFormSuccess = () => {
    setShowForm(false);
    setEditingEquipment(null);
  };

  const toggleEquipmentExpanded = (equipmentId: string) => {
    setExpandedEquipment(prev => {
      const newSet = new Set(prev);
      if (newSet.has(equipmentId)) {
        newSet.delete(equipmentId);
      } else {
        newSet.add(equipmentId);
      }
      return newSet;
    });
  };

  const toggleIslandExpanded = (islandKey: string) => {
    setExpandedIslands(prev => {
      const newSet = new Set(prev);
      if (newSet.has(islandKey)) {
        newSet.delete(islandKey);
      } else {
        newSet.add(islandKey);
      }
      return newSet;
    });
  };

  // Check if user has permission to view equipment
  if (!canViewEquipment) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">
          <Package className="h-12 w-12 mx-auto mb-4 text-gray-400" />
          <h2 className="text-2xl font-semibold mb-2">Access Restricted</h2>
          <p className="text-gray-600">
            You don't have permission to view equipment. Contact your administrator if you need access.
          </p>
        </div>
      </div>
    );
  }

  if (showForm) {
    return (
      <PermissionGuard permission="editEquipment" fallback={
        <div className="container mx-auto p-6">
          <div className="text-center py-12">
            <Package className="h-12 w-12 mx-auto mb-4 text-gray-400" />
            <h2 className="text-2xl font-semibold mb-2">Permission Required</h2>
            <p className="text-gray-600">
              You don't have permission to add or edit equipment.
            </p>
          </div>
        </div>
      }>
        <div className="container mx-auto p-6">
          <div className="mb-4">
            <Button 
              onClick={() => {
                setShowForm(false);
                setEditingEquipment(null);
              }}
              variant="outline"
              data-testid="button-back-to-equipment"
            >
              â† Back to Equipment List
            </Button>
          </div>
          <EquipmentForm 
            onSuccess={handleFormSuccess}
            editingEquipment={editingEquipment}
          />
        </div>
      </PermissionGuard>
    );
  }

  return (
    <div className="container mx-auto p-2 sm:p-4 lg:p-6">
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 mb-6">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold flex items-center gap-2">
            <Package className="h-6 w-6 sm:h-8 sm:w-8" />
            Equipment Management
          </h1>
          <p className="text-sm sm:text-base text-muted-foreground mt-1">
            Track and manage equipment across all Maldives atolls
          </p>
        </div>
        <div className="flex flex-col sm:flex-row gap-2">
          <PermissionGuard permission="viewEquipment">
            <ExportButton 
              dataType="equipment"
              label="Equipment"
              className="w-full sm:w-auto"
            />
          </PermissionGuard>
          <PermissionGuard permission="editEquipment">
            <Button 
              onClick={() => setShowForm(true)}
              className="flex items-center gap-2"
              data-testid="button-add-equipment"
            >
              <Plus className="h-4 w-4" />
              Add Equipment
            </Button>
          </PermissionGuard>
        </div>
      </div>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          <Input
            placeholder="Search equipment..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-equipment-search"
          />
        </div>

        <Select value={selectedAtoll} onValueChange={setSelectedAtoll}>
          <SelectTrigger data-testid="select-filter-atoll">
            <SelectValue placeholder="Filter by atoll" />
          </SelectTrigger>
          <SelectContent>
            {maldivesAtolls.map((atoll) => (
              <SelectItem key={atoll} value={atoll}>
                {atoll}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <Select value={filterType} onValueChange={setFilterType}>
          <SelectTrigger data-testid="select-filter-type">
            <SelectValue placeholder="Filter by type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="tools">Tools</SelectItem>
            <SelectItem value="electronics">Electronics</SelectItem>
            <SelectItem value="visual">Visual</SelectItem>
            <SelectItem value="network">Network</SelectItem>
            <SelectItem value="others">Others</SelectItem>
          </SelectContent>
        </Select>

        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger data-testid="select-filter-status">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="available">Available</SelectItem>
            <SelectItem value="in_use">In Use</SelectItem>
            <SelectItem value="maintenance">Maintenance</SelectItem>
            <SelectItem value="damaged">Damaged</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Equipment Summary */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold">{filteredEquipment.length}</div>
            <div className="text-sm text-muted-foreground">Total Equipment</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-green-600">
              {filteredEquipment.filter(e => e.status === 'available').length}
            </div>
            <div className="text-sm text-muted-foreground">Available</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-blue-600">
              {filteredEquipment.filter(e => e.status === 'in_use').length}
            </div>
            <div className="text-sm text-muted-foreground">In Use</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-red-600">
              {filteredEquipment.filter(e => e.status === 'damaged').length}
            </div>
            <div className="text-sm text-muted-foreground">Damaged</div>
          </CardContent>
        </Card>
      </div>

      {/* Equipment List */}
      {isLoading ? (
        <div className="text-center py-8">Loading equipment...</div>
      ) : filteredEquipment.length === 0 ? (
        <Card>
          <CardContent className="p-12 text-center">
            <Package className="h-16 w-16 mx-auto text-muted-foreground/40 mb-4" />
            <h3 className="text-lg font-semibold text-foreground mb-2">No Equipment Found</h3>
            <p className="text-muted-foreground max-w-md mx-auto mb-4">
              Start building your inventory by adding equipment items that can be tracked across different atolls.
            </p>
            <Button 
              onClick={() => setShowForm(true)}
              data-testid="button-add-first-equipment"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Your First Equipment
            </Button>
          </CardContent>
        </Card>
      ) : selectedAtoll === "All Atolls" ? (
        // Group by atoll and island when showing all
        <div className="space-y-6">
          {Object.entries(equipmentByAtoll)
            .sort(([a], [b]) => a.localeCompare(b))
            .map(([atoll, islandGroups]) => {
              const totalItems = Object.values(islandGroups).flat().length;
              return (
                <Card key={atoll}>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <MapPin className="h-5 w-5" />
                      {atoll} Atoll ({totalItems} items)
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {Object.entries(islandGroups)
                      .sort(([a], [b]) => a.localeCompare(b))
                      .map(([island, items]) => {
                        const islandKey = `${atoll}-${island}`;
                        const isIslandExpanded = expandedIslands.has(islandKey);
                        return (
                          <div key={islandKey} className="space-y-2">
                            <div 
                              className="flex items-center justify-between border-b pb-2 cursor-pointer hover:bg-gray-50 transition-colors p-2 rounded"
                              onClick={() => toggleIslandExpanded(islandKey)}
                              data-testid={`button-toggle-island-${islandKey}`}
                            >
                              <div className="flex items-center gap-2">
                                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                                <h4 className="font-medium text-sm text-gray-700">
                                  {island} ({items.length} items)
                                </h4>
                              </div>
                              {isIslandExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                            </div>
                            {isIslandExpanded && (
                              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3 pl-4">
                                {items.map((item) => (
                                  <EquipmentCard
                                    key={item.id}
                                    equipment={item}
                                    onEdit={handleEdit}
                                    onDelete={handleDelete}
                                    isDeleting={deleteEquipmentMutation.isPending}
                                    isExpanded={expandedEquipment.has(item.id)}
                                    onToggleExpand={() => toggleEquipmentExpanded(item.id)}
                                  />
                                ))}
                              </div>
                            )}
                          </div>
                        );
                      })}
                  </CardContent>
                </Card>
              );
            })}
        </div>
      ) : (
        // Show grouped by island when filtered by specific atoll
        <div className="space-y-4">
          {Object.entries(equipmentByAtoll)
            .filter(([atoll]) => selectedAtoll === "All Atolls" || atoll === selectedAtoll)
            .sort(([a], [b]) => a.localeCompare(b))
            .map(([atoll, islandGroups]) => 
              Object.entries(islandGroups)
                .sort(([a], [b]) => a.localeCompare(b))
                .map(([island, items]) => (
                  <Card key={`${atoll}-${island}`}>
                    <CardHeader className="pb-3">
                      <CardTitle className="flex items-center gap-2 text-base">
                        <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                        {selectedAtoll === "All Atolls" ? `${island}, ${atoll} Atoll` : island}
                        <span className="text-sm font-normal text-gray-500">({items.length} items)</span>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {items.map((item) => (
                          <EquipmentCard
                            key={item.id}
                            equipment={item}
                            onEdit={handleEdit}
                            onDelete={handleDelete}
                            isDeleting={deleteEquipmentMutation.isPending}
                            isExpanded={expandedEquipment.has(item.id)}
                            onToggleExpand={() => toggleEquipmentExpanded(item.id)}
                          />
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                ))
            ).flat()}
        </div>
      )}

      {/* Delete Confirmation Dialog */}
      <ConfirmDialog
        isOpen={!!deletingEquipment}
        onClose={() => setDeletingEquipment(null)}
        onConfirm={() => {
          if (deletingEquipment) {
            deleteEquipmentMutation.mutate(deletingEquipment.id);
            setDeletingEquipment(null);
          }
        }}
        title="Delete Equipment"
        description={`Are you sure you want to delete "${deletingEquipment?.name}"? This will permanently remove this equipment from inventory.`}
        confirmText="Delete Equipment"
        isLoading={deleteEquipmentMutation.isPending}
      />
    </div>
  );
}

interface EquipmentCardProps {
  equipment: Equipment;
  onEdit: (equipment: Equipment) => void;
  onDelete: (id: string, name: string) => void;
  isDeleting: boolean;
  isExpanded: boolean;
  onToggleExpand: () => void;
}

function EquipmentCard({ equipment, onEdit, onDelete, isDeleting, isExpanded, onToggleExpand }: EquipmentCardProps) {
  const queryClient = useQueryClient();
  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardContent className="p-3">
        {/* Compact header - always visible */}
        <div 
          className="flex items-center justify-between cursor-pointer"
          onClick={onToggleExpand}
          data-testid={`button-expand-equipment-${equipment.id}`}
        >
          <div className="flex items-center space-x-3 flex-1">
            <div>
              <h3 className="font-semibold text-base">{equipment.name}</h3>
              <p className="text-sm text-muted-foreground capitalize">
                {equipment.type} â€¢ Qty: {equipment.quantity}
              </p>
            </div>
            <Badge className={statusColors[equipment.status as keyof typeof statusColors]}>
              {equipment.status.replace('_', ' ')}
            </Badge>
          </div>
          <div className="flex items-center space-x-2">
            {/* Quick actions - always visible */}
            <PermissionGuard permission="editEquipment">
              <Button
                size="sm"
                variant="ghost"
                onClick={(e) => {
                  e.stopPropagation();
                  onEdit(equipment);
                }}
                data-testid={`button-edit-equipment-${equipment.id}`}
              >
                <Edit className="h-4 w-4" />
              </Button>
            </PermissionGuard>
            <div onClick={(e) => e.stopPropagation()}>
              <EquipmentTransferForm
                equipment={equipment}
                onSuccess={() => {
                  // Refresh equipment list after transfer
                  queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });
                }}
              />
            </div>
            {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
          </div>
        </div>

        {/* Expanded details */}
        {isExpanded && (
          <div className="mt-4 pt-4 border-t border-gray-200 space-y-3">
            <div className="flex items-center gap-2">
              <MapPin className="h-4 w-4 text-gray-500" />
              <span className="text-sm">
                {equipment.atoll}
                {equipment.island && ` â€¢ ${equipment.island}`}
              </span>
            </div>

            {/* Equipment Transfer History - Moved to prominent position */}
            <EquipmentTransferHistory 
              equipmentId={equipment.id}
              equipmentName={equipment.name}
            />

            <div className="flex gap-2 flex-wrap">
              <Badge className={conditionColors[equipment.condition as keyof typeof conditionColors]}>
                {equipment.condition}
              </Badge>
            </div>

            {equipment.description && (
              <p className="text-sm text-gray-600 mt-2">
                {equipment.description}
              </p>
            )}

            <div className="text-xs text-gray-500 mt-2">
              Added {new Date(equipment.createdAt || Date.now()).toLocaleDateString()}
            </div>

            {/* Delete action in expanded view */}
            <div className="pt-3 border-t border-gray-100">
              <PermissionGuard permission="editEquipment">
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => onDelete(equipment.id, equipment.name)}
                  disabled={isDeleting}
                  data-testid={`button-delete-equipment-${equipment.id}`}
                >
                  <Trash2 className="h-4 w-4 mr-1" />
                  Delete
                </Button>
              </PermissionGuard>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

