import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Download, Database, Loader2, CheckCircle, Trash2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface PreloadStatus {
  isPreloading: boolean;
  completed: number;
  total: number;
  startTime?: number;
}

interface DatabaseStats {
  totalTiles: number;
  totalSize: number;
}

export function TilePreloadControl() {
  const [status, setStatus] = useState<PreloadStatus>({ isPreloading: false, completed: 0, total: 0 });
  const [databaseStats, setDatabaseStats] = useState<DatabaseStats>({ totalTiles: 0, totalSize: 0 });
  const [isLoading, setIsLoading] = useState(false);
  const [estimatedTimeRemaining, setEstimatedTimeRemaining] = useState<string>('');
  const { toast } = useToast();

  const fetchDatabaseStats = async () => {
    try {
      const response = await fetch('/api/cache/status');
      if (response.ok) {
        const data = await response.json();
        setDatabaseStats(data);
      }
    } catch (error) {
      console.warn('Failed to fetch database stats');
    }
  };

  const fetchStatus = async () => {
    try {
      const response = await fetch('/api/tiles/preload/status');
      if (response.ok) {
        const data = await response.json();
        setStatus(data);

        // Also fetch current database stats for accurate display
        await fetchDatabaseStats();

        // Calculate ETA if preloading and we have meaningful data
        if (data.isPreloading && data.completed > 100 && data.startTime) {
          const elapsed = (Date.now() - data.startTime) / 1000; // seconds
          const rate = data.completed / elapsed; // tiles per second
          const remaining = data.total - data.completed;
          const eta = remaining / rate; // seconds

          if (eta > 60) {
            const hours = Math.floor(eta / 3600);
            const minutes = Math.floor((eta % 3600) / 60);
            const seconds = Math.floor(eta % 60);

            if (hours > 0) {
              setEstimatedTimeRemaining(`${hours}h ${minutes}m ${seconds}s remaining`);
            } else {
              setEstimatedTimeRemaining(`${minutes}m ${seconds}s remaining`);
            }
          } else {
            setEstimatedTimeRemaining(`${Math.floor(eta)}s remaining`);
          }
        } else if (!data.isPreloading) {
          setEstimatedTimeRemaining('');
        }
      }
    } catch (error) {
      console.warn('Failed to fetch preload status');
    }
  };

  useEffect(() => {
    fetchStatus();
    fetchDatabaseStats();

    // Poll status every 3 seconds when preloading for more responsive updates
    const interval = setInterval(() => {
      if (status.isPreloading) {
        fetchStatus();
      } else {
        // When not preloading, still update database stats periodically
        fetchDatabaseStats();
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [status.isPreloading]);

  const startPreload = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/tiles/preload/start', { method: 'POST' });
      if (response.ok) {
        toast({
          title: "Preload Started",
          description: "The complete Maldives map is being cached to the server.",
        });
        fetchStatus();
      } else {
        const error = await response.json();
        toast({
          title: "Error",
          description: error.message || "Failed to start preload",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to start tile preload",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const clearServerCache = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/tiles/cache/clear-and-resync', { method: 'POST' });
      if (response.ok) {
        toast({
          title: "Server Cache Cleared",
          description: "All server map tiles cleared. New preload will start automatically.",
        });
        fetchStatus();
        fetchDatabaseStats();
      } else {
        const error = await response.json();
        toast({
          title: "Error",
          description: error.message || "Failed to clear server cache",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to clear server cache",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const progress = status.total > 0 ? (status.completed / status.total) * 100 : 0;

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Database className="h-5 w-5" />
          Server Map Preload
        </CardTitle>
        <CardDescription>
          Cache the complete Maldives map to the server for faster loading across all users
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {status.isPreloading ? (
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <div className="flex items-center gap-2 text-sm text-blue-600">
                <Loader2 className="h-4 w-4 animate-spin" />
                Preloading map tiles... {status.completed} / {status.total}
              </div>
              <span className="text-sm font-medium text-blue-600">
                {progress.toFixed(1)}%
              </span>
            </div>
            {estimatedTimeRemaining && (
              <div className="text-center">
                <span className="text-xs text-orange-600 font-medium">
                  â±ï¸ {estimatedTimeRemaining}
                </span>
              </div>
            )}
            <Progress value={progress} className="h-3" />
            <div className="text-xs text-gray-500 text-center">
              This process downloads all map tiles for the Maldives region
              {!estimatedTimeRemaining && status.completed > 10 && (
                <span className="block text-orange-600 mt-1">â±ï¸ Calculating time remaining...</span>
              )}
            </div>
          </div>
        ) : databaseStats.totalTiles > 0 ? (
          <div className="flex items-center gap-2 text-sm text-green-600">
            <CheckCircle className="h-4 w-4" />
            Map preload completed ({databaseStats.totalTiles.toLocaleString()} tiles cached)
          </div>
        ) : (
          <div className="text-sm text-gray-600">
            No preload has been run yet. Start preload to cache all Maldives map tiles.
            {databaseStats.totalTiles > 0 && (
              <div className="text-xs text-gray-500 mt-1">
                Current cache: {databaseStats.totalTiles.toLocaleString()} tiles
              </div>
            )}
          </div>
        )}

        <div className="flex flex-col gap-3">
          <Button 
            onClick={startPreload}
            disabled={isLoading || status.isPreloading}
            className="w-full"
            data-testid="button-start-preload"
          >
            {isLoading ? (
              <Loader2 className="h-4 w-4 animate-spin mr-2" />
            ) : (
              <Download className="h-4 w-4 mr-2" />
            )}
            {status.isPreloading ? 'Preload Running...' : 'Start Complete Map Preload'}
          </Button>

          {databaseStats.totalTiles > 0 && (
            <Button 
              onClick={clearServerCache}
              disabled={isLoading || status.isPreloading}
              variant="outline"
              className="w-full text-red-600 hover:text-red-700 border-red-200 hover:border-red-300"
              data-testid="button-clear-server-cache"
            >
              {isLoading ? (
                <Loader2 className="h-4 w-4 animate-spin mr-2" />
              ) : (
                <Trash2 className="h-4 w-4 mr-2" />
              )}
              Clear Server Cache & Restart
            </Button>
          )}
        </div>

        <div className="text-xs text-gray-500">
          âš ï¸ Admin only: This will download thousands of map tiles and may take several minutes
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState, useRef } from "react";
import type { ReactNode } from "react";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (result: any) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * Compresses an image file while maintaining reasonable quality
 * Targets around 100-200KB for optimal storage savings
 */
async function compressImage(file: File): Promise<Blob> {
  console.log('Starting image compression for file:', file.name, 'Size:', file.size);
  return new Promise((resolve, reject) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    if (!ctx) {
      console.error('Failed to get canvas context');
      reject(new Error('Canvas context not available'));
      return;
    }

    const img = new Image();

    img.onload = () => {
      console.log('Image loaded successfully. Original dimensions:', img.width, 'x', img.height);

      // Calculate new dimensions (max 800px width while maintaining aspect ratio)
      const maxWidth = 800;
      const ratio = Math.min(maxWidth / img.width, maxWidth / img.height);

      canvas.width = img.width * ratio;
      canvas.height = img.height * ratio;

      console.log('Compressed dimensions:', canvas.width, 'x', canvas.height);

      // Draw and compress the image
      ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

      // Convert to blob with 75% quality (good balance of size/quality)
      canvas.toBlob(
        (blob) => {
          if (blob) {
            console.log('Image compression successful. New size:', blob.size);
            resolve(blob);
          } else {
            console.error('Canvas toBlob returned null');
            reject(new Error('Image compression failed - toBlob returned null'));
          }
        },
        'image/jpeg',
        0.75
      );
    };

    img.onerror = (error) => {
      console.error('Failed to load image:', error);
      reject(new Error('Failed to load image for compression'));
    };

    img.src = URL.createObjectURL(file);
    console.log('Image object URL created, waiting for load...');
  });
}

/**
 * A simple file upload component using a standard file input with image compression
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      alert('Please select an image file');
      return;
    }

    setIsUploading(true);

    try {
      // Compress the image to save storage space
      const compressedFile = await compressImage(file);

      // Check compressed file size
      if (compressedFile.size > maxFileSize) {
        alert(`Compressed image is still too large (${Math.round(compressedFile.size / 1024)}KB). Please select a smaller image.`);
        setIsUploading(false);
        return;
      }

      const { method, url } = await onGetUploadParameters();

      const uploadResponse = await fetch(url, {
        method,
        body: compressedFile,
        headers: {
          'Content-Type': 'image/jpeg', // Always convert to JPEG for consistency
        },
      });

      if (uploadResponse.ok) {
        // Extract the object path from the presigned URL
        // URL format: https://storage.googleapis.com/bucket-name/object-path?signed-params
        const urlObj = new URL(url);
        const fullPath = urlObj.pathname; // This gives us /bucket-name/uploads/uuid

        // Extract just the object path part (after the bucket name)
        // Convert /bucket-name/.private/uploads/uuid to /objects/uploads/uuid
        const pathParts = fullPath.split('/').filter(part => part.length > 0);
        if (pathParts.length >= 3) {
          // Skip bucket name and .private, use uploads/uuid part
          const uploadsIndex = pathParts.findIndex(part => part === 'uploads');
          if (uploadsIndex >= 0) {
            const objectPath = `/objects/${pathParts.slice(uploadsIndex).join('/')}`;
            console.log('Extracted object path:', objectPath);

            onComplete?.({
              successful: [{ uploadURL: objectPath }]
            });
          } else {
            throw new Error('Invalid upload URL - uploads path not found');
          }
        } else {
          throw new Error('Invalid upload URL path format');
        }
      } else {
        const errorText = await uploadResponse.text();
        console.error('Upload failed:', errorText);
        throw new Error(`Upload failed: ${uploadResponse.status}`);
      }
    } catch (error: any) {
      console.error('Upload error:', error);
      console.error('Error details:', {
        message: error.message,
        name: error.name,
        stack: error.stack,
        toString: error.toString()
      });
      const errorMessage = error.message || error.toString() || 'Unknown error occurred';
      alert(`Failed to upload file: ${errorMessage}. Please try again.`);
    } finally {
      setIsUploading(false);
      // Reset file input to allow re-uploading the same file
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleButtonClick = () => {
    if (!isUploading && fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  return (
    <div>
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        disabled={isUploading}
        style={{ display: 'none' }}
      />
      <Button 
        type="button" 
        className={buttonClassName}
        disabled={isUploading}
        onClick={handleButtonClick}
      >
        {isUploading ? 'Uploading...' : children}
      </Button>
    </div>
  );
}

