import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { 
  UserCog, 
  Users, 
  Database, 
  ArchiveRestore, 
  BarChart3, 
  X, 
  Search,
  Download,
  Trash2,
  CheckCircle,
  AlertTriangle,
  Activity,
  Monitor,
  Settings,
  FileText,
  Globe
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { ConfirmDialog } from '@/components/ConfirmDialog';
import { getUserDisplayInfo } from '@/lib/userDisplayUtils';

interface AdminSectionProps {
  section: string;
  onClose: () => void;
}

export function AdminSection({ section, onClose }: AdminSectionProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedUsers, setSelectedUsers] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showDeactivateConfirm, setShowDeactivateConfirm] = useState(false);
  const [showActivateConfirm, setShowActivateConfirm] = useState(false);
  const [showApprovalDialog, setShowApprovalDialog] = useState(false);
  const [selectedPendingUser, setSelectedPendingUser] = useState<any>(null);
  const [approvalAction, setApprovalAction] = useState<'approve' | 'reject'>('approve');

  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
  });

  const { data: events = [] } = useQuery({
    queryKey: ["/api/events"],
  });

  const { data: islands = [] } = useQuery({
    queryKey: ["/api/islands"],
  });

  // Pending users query
  const { data: pendingUsers = [] } = useQuery({
    queryKey: ["/api/users/pending"],
    refetchInterval: 10000, // Refresh every 10 seconds
  });


  const bulkUpdateMutation = useMutation({
    mutationFn: async ({ userIds, updates }: { userIds: string[]; updates: any }) => {
      await Promise.all(
        userIds.map(userId => 
          apiRequest("PATCH", `/api/users/${userId}`, updates)
        )
      );
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "Users updated successfully",
      });
      setSelectedUsers([]);
    },
  });



  // User approval mutations
  const approveUserMutation = useMutation({
    mutationFn: async (userId: string) => {
      await apiRequest("PATCH", `/api/users/${userId}/approve`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/users/pending"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "User approved successfully",
      });
      setShowApprovalDialog(false);
      setSelectedPendingUser(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to approve user",
        variant: "destructive",
      });
    },
  });

  const rejectUserMutation = useMutation({
    mutationFn: async (userId: string) => {
      await apiRequest("PATCH", `/api/users/${userId}/reject`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/users/pending"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "User rejected successfully",
      });
      setShowApprovalDialog(false);
      setSelectedPendingUser(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to reject user",
        variant: "destructive",
      });
    },
  });



  const renderBulkOperationsSection = () => {
    const filteredUsers = (users as any[]).filter((user: any) =>
      user.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
      <>
        <Card className="w-full max-w-4xl">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Bulk User Operations</CardTitle>
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex gap-4">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search users..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-users"
                  />
                </div>
                <div className="flex gap-2">
                  <Button
                    onClick={() => setShowDeactivateConfirm(true)}
                    disabled={selectedUsers.length === 0}
                    variant="destructive"
                    data-testid="button-bulk-deactivate"
                  >
                    Deactivate Selected
                  </Button>
                  <Button
                    onClick={() => setShowActivateConfirm(true)}
                    disabled={selectedUsers.length === 0}
                    data-testid="button-bulk-activate"
                  >
                    Activate Selected
                  </Button>
                </div>
              </div>

              <div className="border rounded">
                <div className="max-h-96 overflow-y-auto">
                  {filteredUsers.map((user: any) => (
                    <div key={user.id} className="flex items-center space-x-3 p-3 border-b last:border-b-0">
                      <Checkbox
                        checked={selectedUsers.includes(user.id)}
                        onCheckedChange={(checked) => {
                          if (checked) {
                            setSelectedUsers([...selectedUsers, user.id]);
                          } else {
                            setSelectedUsers(selectedUsers.filter(id => id !== user.id));
                          }
                        }}
                        data-testid={`checkbox-user-${user.id}`}
                      />
                      <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center text-primary-foreground text-sm font-medium">
                        {getUserDisplayInfo(user).initials}
                      </div>
                      <div className="flex-1">
                        <p className="font-medium">{getUserDisplayInfo(user).displayName}</p>
                        <p className="text-sm text-muted-foreground">{user.email}</p>
                      </div>
                      <div className="text-sm">
                        <span className={`px-2 py-1 rounded text-xs ${
                          user.role === 'admin' ? 'bg-red-100 text-red-800' :
                          user.role === 'coordinator' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {user.role}
                        </span>
                      </div>
                      <div className="text-sm">
                        {user.isActive ? (
                          <CheckCircle className="h-4 w-4 text-green-500" />
                        ) : (
                          <AlertTriangle className="h-4 w-4 text-red-500" />
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              <p className="text-sm text-muted-foreground">
                {selectedUsers.length} user(s) selected
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Confirmation Dialogs for User Operations */}
        <ConfirmDialog
          isOpen={showDeactivateConfirm}
          onClose={() => setShowDeactivateConfirm(false)}
          onConfirm={() => {
            bulkUpdateMutation.mutate({ 
              userIds: selectedUsers, 
              updates: { isActive: false } 
            });
            setShowDeactivateConfirm(false);
          }}
          title="Deactivate Users"
          description={`Are you sure you want to deactivate ${selectedUsers.length} selected user${selectedUsers.length > 1 ? 's' : ''}? This will prevent them from accessing the application.`}
          confirmText="Deactivate Users"
          isLoading={bulkUpdateMutation.isPending}
        />

        <ConfirmDialog
          isOpen={showActivateConfirm}
          onClose={() => setShowActivateConfirm(false)}
          onConfirm={() => {
            bulkUpdateMutation.mutate({ 
              userIds: selectedUsers, 
              updates: { isActive: true } 
            });
            setShowActivateConfirm(false);
          }}
          title="Activate Users"
          description={`Are you sure you want to activate ${selectedUsers.length} selected user${selectedUsers.length > 1 ? 's' : ''}? This will allow them to access the application.`}
          confirmText="Activate Users"
          variant="default"
          isLoading={bulkUpdateMutation.isPending}
        />
      </>
    );
  };

  const renderSuspendedAccountsSection = () => {
    const suspendedUsers = (users as any[]).filter((user: any) => !user.isActive);

    return (
      <Card className="w-full max-w-4xl">
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>Suspended Accounts</CardTitle>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {suspendedUsers.length === 0 ? (
              <p className="text-center text-muted-foreground py-8">
                No suspended accounts found.
              </p>
            ) : (
              <div className="space-y-3">
                {suspendedUsers.map((user: any) => (
                  <div key={user.id} className="flex items-center justify-between p-3 border rounded">
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-muted rounded-full flex items-center justify-center text-muted-foreground text-sm font-medium">
                        {getUserDisplayInfo(user).initials}
                      </div>
                      <div>
                        <p className="font-medium">{getUserDisplayInfo(user).displayName}</p>
                        <p className="text-sm text-muted-foreground">{user.email}</p>
                      </div>
                    </div>
                    <Button
                      onClick={() => bulkUpdateMutation.mutate({ 
                        userIds: [user.id], 
                        updates: { isActive: true } 
                      })}
                      size="sm"
                      data-testid={`button-reactivate-${user.id}`}
                    >
                      Reactivate
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };


  const renderSystemAnalyticsSection = () => {
    const activeUsers = (users as any[]).filter((user: any) => user.isActive).length;
    const activeEvents = (events as any[]).filter((event: any) => event.status === 'active').length;
    const completedEvents = (events as any[]).filter((event: any) => event.status === 'completed').length;

    return (
      <Card className="w-full max-w-4xl">
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>System Analytics</CardTitle>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="border rounded p-4 text-center">
                <Users className="h-6 w-6 mx-auto mb-2 text-green-500" />
                <p className="text-2xl font-bold">{activeUsers}</p>
                <p className="text-xs text-muted-foreground">Active Users</p>
              </div>
              <div className="border rounded p-4 text-center">
                <Activity className="h-6 w-6 mx-auto mb-2 text-blue-500" />
                <p className="text-2xl font-bold">{activeEvents}</p>
                <p className="text-xs text-muted-foreground">Active Events</p>
              </div>
              <div className="border rounded p-4 text-center">
                <CheckCircle className="h-6 w-6 mx-auto mb-2 text-purple-500" />
                <p className="text-2xl font-bold">{completedEvents}</p>
                <p className="text-xs text-muted-foreground">Completed Events</p>
              </div>
              <div className="border rounded p-4 text-center">
                <Database className="h-6 w-6 mx-auto mb-2 text-orange-500" />
                <p className="text-2xl font-bold">{(islands as any[]).filter((i: any) => i.hasVisited).length}</p>
                <p className="text-xs text-muted-foreground">Visited Islands</p>
              </div>
            </div>

            <div className="space-y-4">
              <h3 className="font-semibold">User Distribution</h3>
              <div className="space-y-2">
                {['admin', 'coordinator', 'agent', 'attachment'].map(role => {
                  const count = (users as any[]).filter((user: any) => user.role === role).length;
                  const percentage = (users as any[]).length > 0 ? (count / (users as any[]).length * 100).toFixed(1) : 0;
                  return (
                    <div key={role} className="flex items-center justify-between p-2 border rounded">
                      <span className="capitalize font-medium">{role}s</span>
                      <div className="flex items-center gap-2">
                        <span className="text-sm">{count} users ({percentage}%)</span>
                        <div className="w-20 h-2 bg-muted rounded">
                          <div 
                            className="h-full bg-primary rounded" 
                            style={{ width: `${percentage}%` }}
                          />
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  };

  const renderBackupRestoreSection = () => (
    <Card className="w-full max-w-4xl">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle>Archive & Restore</CardTitle>
        <Button variant="ghost" size="sm" onClick={onClose}>
          <X className="h-4 w-4" />
        </Button>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <div className="space-y-4">
            <h3 className="font-semibold">Archived Events Management</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Button variant="outline" className="justify-start h-auto p-4">
                <div className="text-left">
                  <div className="flex items-center gap-2 mb-1">
                    <ArchiveRestore className="h-4 w-4" />
                    <span className="font-medium">Archive Old Events</span>
                  </div>
                  <p className="text-xs text-muted-foreground">Archive completed events older than 6 months</p>
                </div>
              </Button>
              <Button variant="outline" className="justify-start h-auto p-4">
                <div className="text-left">
                  <div className="flex items-center gap-2 mb-1">
                    <Download className="h-4 w-4" />
                    <span className="font-medium">Export Archives</span>
                  </div>
                  <p className="text-xs text-muted-foreground">Download archived events as CSV</p>
                </div>
              </Button>
            </div>
          </div>

          <div className="space-y-4">
            <h3 className="font-semibold">System Backup</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Button variant="outline" className="justify-start h-auto p-4">
                <div className="text-left">
                  <div className="flex items-center gap-2 mb-1">
                    <Database className="h-4 w-4" />
                    <span className="font-medium">Create Full Backup</span>
                  </div>
                  <p className="text-xs text-muted-foreground">Backup all data including users and events</p>
                </div>
              </Button>
              <Button variant="outline" className="justify-start h-auto p-4">
                <div className="text-left">
                  <div className="flex items-center gap-2 mb-1">
                    <ArchiveRestore className="h-4 w-4" />
                    <span className="font-medium">Restore from Backup</span>
                  </div>
                  <p className="text-xs text-muted-foreground">Restore system from previous backup</p>
                </div>
              </Button>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );


  const renderSystemMonitorSection = () => (
    <Card className="w-full max-w-4xl">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="flex items-center gap-2">
          <Settings className="h-5 w-5" />
          System Monitor
        </CardTitle>
        <Button variant="ghost" size="sm" onClick={onClose}>
          <X className="h-4 w-4" />
        </Button>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="border rounded p-4 text-center">
              <Users className="h-6 w-6 mx-auto mb-2 text-green-500" />
              <p className="text-xl font-bold">{(users as any[]).filter((u: any) => u.isActive).length}</p>
              <p className="text-xs text-muted-foreground">Active Users</p>
            </div>
            <div className="border rounded p-4 text-center">
              <Activity className="h-6 w-6 mx-auto mb-2 text-blue-500" />
              <p className="text-xl font-bold">{(events as any[]).filter((e: any) => e.status === 'active').length}</p>
              <p className="text-xs text-muted-foreground">Active Events</p>
            </div>
            <div className="border rounded p-4 text-center">
              <Globe className="h-6 w-6 mx-auto mb-2 text-orange-500" />
              <p className="text-xl font-bold">{(islands as any[]).filter((i: any) => i.hasVisited).length}</p>
              <p className="text-xs text-muted-foreground">Visited Islands</p>
            </div>
          </div>

          <div className="space-y-4">
            <h3 className="font-semibold">Environment</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="border rounded p-3">
                <p className="font-medium">Mode</p>
                <p className="text-sm text-muted-foreground">Development</p>
              </div>
              <div className="border rounded p-3">
                <p className="font-medium">Protocol</p>
                <p className="text-sm text-muted-foreground">{window.location.protocol}</p>
              </div>
              <div className="border rounded p-3">
                <p className="font-medium">Host</p>
                <p className="text-sm text-muted-foreground">{window.location.host}</p>
              </div>
              <div className="border rounded p-3">
                <p className="font-medium">User Agent</p>
                <p className="text-sm text-muted-foreground truncate">{navigator.userAgent.slice(0, 30)}...</p>
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );


  const renderUserApprovalSection = () => (
    <Card className="w-full max-w-4xl mx-auto max-h-[80vh] overflow-hidden flex flex-col">
      <CardHeader className="flex-shrink-0">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center">
            <UserCog className="h-5 w-5 mr-2" />
            User Approvals
          </CardTitle>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </div>
      </CardHeader>
      <CardContent className="flex-1 overflow-auto">
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Approve or reject pending user registrations
            </p>
            <div className="text-sm font-medium">
              {(pendingUsers as any[]).length} pending approval{(pendingUsers as any[]).length !== 1 ? 's' : ''}
            </div>
          </div>

          {(pendingUsers as any[]).length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <UserCog className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No users pending approval</p>
            </div>
          ) : (
            <div className="space-y-4">
              {(pendingUsers as any[]).map((user: any) => (
                <Card key={user.id} className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      {user.profileImageUrl ? (
                        <div className="w-10 h-10 rounded-full overflow-hidden border-2 border-background shadow-sm">
                          <img 
                            src={user.profileImageUrl} 
                            alt={getUserDisplayInfo(user).displayName}
                            className="w-full h-full object-cover"
                          />
                        </div>
                      ) : (
                        <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center text-primary-foreground font-semibold">
                          {getUserDisplayInfo(user).initials}
                        </div>
                      )}
                      <div>
                        <div className="font-semibold">
                          {getUserDisplayInfo(user).displayName}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {user.email}
                        </div>
                        <div className="text-xs text-muted-foreground">
                          Registered: {new Date(user.createdAt).toLocaleDateString()}
                        </div>
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        variant="outline"
                        className="text-green-600 hover:text-green-700 hover:bg-green-50"
                        onClick={() => {
                          setSelectedPendingUser(user);
                          setApprovalAction('approve');
                          setShowApprovalDialog(true);
                        }}
                        disabled={approveUserMutation.isPending || rejectUserMutation.isPending}
                        data-testid={`button-approve-${user.id}`}
                      >
                        <CheckCircle className="h-4 w-4 mr-1" />
                        Approve
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        className="text-red-600 hover:text-red-700 hover:bg-red-50"
                        onClick={() => {
                          setSelectedPendingUser(user);
                          setApprovalAction('reject');
                          setShowApprovalDialog(true);
                        }}
                        disabled={approveUserMutation.isPending || rejectUserMutation.isPending}
                        data-testid={`button-reject-${user.id}`}
                      >
                        <X className="h-4 w-4 mr-1" />
                        Reject
                      </Button>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );

  const renderDatabaseManagementSection = () => {
    const [isClearing, setIsClearing] = useState(false);
    const [isRefreshing, setIsRefreshing] = useState(false);

    const clearAllMilestonesMutation = useMutation({
      mutationFn: () => apiRequest("POST", "/api/admin/milestones/clear-all"),
      onSuccess: (data: any) => {
        toast({
          title: "Success",
          description: `All user milestones cleared successfully. Deleted ${data.deletedCount} milestones.`,
        });
        queryClient.invalidateQueries({ queryKey: ["/api/milestones"] });
        setIsClearing(false);
      },
      onError: (error) => {
        toast({
          title: "Error",
          description: "Failed to clear milestones. Please try again.",
          variant: "destructive",
        });
        setIsClearing(false);
      },
    });

    const refreshAllMilestonesMutation = useMutation({
      mutationFn: () => apiRequest("POST", "/api/admin/milestones/refresh-all"),
      onSuccess: (data: any) => {
        toast({
          title: "Success",
          description: `Milestone refresh completed for all users. ${data.results.length} users processed.`,
        });
        queryClient.invalidateQueries({ queryKey: ["/api/milestones"] });
        setIsRefreshing(false);
      },
      onError: (error) => {
        toast({
          title: "Error",
          description: "Failed to refresh milestones. Please try again.",
          variant: "destructive",
        });
        setIsRefreshing(false);
      },
    });

    return (
      <Card className="w-full max-w-4xl bg-white">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <div className="flex items-center gap-2">
            <Database className="h-5 w-5 text-orange-500" />
            <CardTitle className="text-lg font-semibold">Database Management</CardTitle>
          </div>
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            data-testid="button-close-database-management"
          >
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Milestone Management */}
          <div>
            <h3 className="text-md font-semibold mb-4 flex items-center gap-2">
              <Activity className="h-4 w-4 text-blue-500" />
              Milestone Management
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Clear All Milestones */}
              <div className="p-4 border rounded-lg">
                <div className="flex items-start justify-between mb-3">
                  <div>
                    <h4 className="font-medium text-red-600">Clear All Milestones</h4>
                    <p className="text-sm text-gray-600 mt-1">
                      Remove all milestone data for all users. This cannot be undone.
                    </p>
                  </div>
                </div>
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={() => {
                    setIsClearing(true);
                    clearAllMilestonesMutation.mutate();
                  }}
                  disabled={clearAllMilestonesMutation.isPending || isClearing}
                  data-testid="button-clear-all-milestones"
                >
                  {(clearAllMilestonesMutation.isPending || isClearing) ? (
                    "Clearing..."
                  ) : (
                    <>
                      <Trash2 className="h-4 w-4 mr-1" />
                      Clear All Milestones
                    </>
                  )}
                </Button>
              </div>

              {/* Refresh All Milestones */}
              <div className="p-4 border rounded-lg">
                <div className="flex items-start justify-between mb-3">
                  <div>
                    <h4 className="font-medium text-blue-600">Refresh All Milestones</h4>
                    <p className="text-sm text-gray-600 mt-1">
                      Recalculate and update milestone progress for all users based on current data.
                    </p>
                  </div>
                </div>
                <Button
                  variant="default"
                  size="sm"
                  onClick={() => {
                    setIsRefreshing(true);
                    refreshAllMilestonesMutation.mutate();
                  }}
                  disabled={refreshAllMilestonesMutation.isPending || isRefreshing}
                  data-testid="button-refresh-all-milestones"
                >
                  {(refreshAllMilestonesMutation.isPending || isRefreshing) ? (
                    "Refreshing..."
                  ) : (
                    <>
                      <ArchiveRestore className="h-4 w-4 mr-1" />
                      Refresh All Milestones
                    </>
                  )}
                </Button>
              </div>
            </div>
          </div>

          {/* Cache Management */}
          <div>
            <h3 className="text-md font-semibold mb-4 flex items-center gap-2">
              <Monitor className="h-4 w-4 text-green-500" />
              Cache Management
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Clear Frontend Cache */}
              <div className="p-4 border rounded-lg">
                <div className="flex items-start justify-between mb-3">
                  <div>
                    <h4 className="font-medium text-green-600">Clear Frontend Cache</h4>
                    <p className="text-sm text-gray-600 mt-1">
                      Clear all cached data in the browser. Forces fresh data fetch.
                    </p>
                  </div>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    // Clear React Query cache
                    queryClient.clear();
                    toast({
                      title: "Success",
                      description: "Frontend cache cleared successfully. All data will be fetched fresh.",
                    });
                  }}
                  data-testid="button-clear-frontend-cache"
                >
                  <Settings className="h-4 w-4 mr-1" />
                  Clear Frontend Cache
                </Button>
              </div>

              {/* Reset Client Cache & Reload */}
              <div className="p-4 border rounded-lg">
                <div className="flex items-start justify-between mb-3">
                  <div>
                    <h4 className="font-medium text-blue-600">Reset & Force Reload</h4>
                    <p className="text-sm text-gray-600 mt-1">
                      Clear cache and immediately refetch milestone data to resolve duplicates.
                    </p>
                  </div>
                </div>
                <Button
                  variant="default"
                  size="sm"
                  onClick={() => {
                    // Clear cache and refetch milestone data
                    queryClient.removeQueries({ queryKey: ['/api/milestones'] });
                    queryClient.removeQueries({ queryKey: ['/api/milestones/stats'] });
                    queryClient.invalidateQueries({ queryKey: ['/api/milestones'] });
                    queryClient.invalidateQueries({ queryKey: ['/api/milestones/stats'] });
                    toast({
                      title: "Success",
                      description: "Client cache reset complete. Milestone data refreshed.",
                    });
                  }}
                  data-testid="button-reset-client-cache"
                >
                  <Globe className="h-4 w-4 mr-1" />
                  Reset & Force Reload
                </Button>
              </div>
            </div>
          </div>

          <div className="text-xs text-gray-500 bg-yellow-50 p-3 rounded-lg border-l-4 border-yellow-400">
            <AlertTriangle className="h-4 w-4 inline mr-1" />
            <strong>Warning:</strong> Use these tools carefully. Clear all milestones will permanently delete milestone data. 
            Always ensure users refresh their browsers after clearing cache.
          </div>
        </CardContent>
      </Card>
    );
  };

  const sections: Record<string, () => JSX.Element> = {
    'user-approvals': renderUserApprovalSection,
    'bulk-operations': renderBulkOperationsSection,
    'suspended-accounts': renderSuspendedAccountsSection,
    'system-analytics': renderSystemAnalyticsSection,
    'backup-restore': renderBackupRestoreSection,
    'system-monitor': renderSystemMonitorSection,
    'database-management': renderDatabaseManagementSection,
  };

  const renderSection = sections[section];
  if (!renderSection) return null;

  const handleApprovalAction = () => {
    if (!selectedPendingUser) return;

    if (approvalAction === 'approve') {
      approveUserMutation.mutate(selectedPendingUser.id);
    } else {
      rejectUserMutation.mutate(selectedPendingUser.id);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      {renderSection()}

      <ConfirmDialog
        isOpen={showApprovalDialog}
        onClose={() => {
          setShowApprovalDialog(false);
          setSelectedPendingUser(null);
        }}
        onConfirm={handleApprovalAction}
        title={approvalAction === 'approve' ? 'Approve User' : 'Reject User'}
        description={
          approvalAction === 'approve'
            ? `Are you sure you want to approve ${getUserDisplayInfo(selectedPendingUser).displayName}? They will gain access to the system.`
            : `Are you sure you want to reject ${getUserDisplayInfo(selectedPendingUser).displayName}? They will not be able to access the system.`
        }
        confirmText={approvalAction === 'approve' ? 'Approve User' : 'Reject User'}
        cancelText="Cancel"
        variant={approvalAction === 'approve' ? 'default' : 'destructive'}
        isLoading={approveUserMutation.isPending || rejectUserMutation.isPending}
      />
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { User, Edit3, Save, X, Camera, Upload } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { getUserDisplayInfo } from "@/lib/userDisplayUtils";

export default function Profile() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobile: "",
    serviceNumber: "",
    profileImageUrl: ""
  });

  // Initialize form data when user data is available
  useEffect(() => {
    if (user) {
      setFormData({
        firstName: (user as any)?.firstName || "",
        lastName: (user as any)?.lastName || "",
        email: (user as any)?.email || "",
        mobile: (user as any)?.mobile || "",
        serviceNumber: (user as any)?.serviceNumber || "",
        profileImageUrl: (user as any)?.profileImageUrl || ""
      });
    }
  }, [user]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("PATCH", "/api/profile", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      setIsEditing(false);
      toast({
        title: "Success",
        description: "Profile updated successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const handleGetUploadParameters = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      const uploadURL = data.uploadURL;
      if (!uploadURL) {
        throw new Error('No upload URL received from server');
      }
      return {
        method: 'PUT' as const,
        url: uploadURL,
      };
    } catch (error) {
      console.error('Failed to get upload parameters:', error);
      throw error;
    }
  };

  const handleUploadComplete = async (result: any) => {
    if (result.successful && result.successful[0]) {
      const uploadURL = result.successful[0].uploadURL;
      try {
        const updateResponse = await apiRequest("PUT", "/api/profile/image", {
          profileImageURL: uploadURL,
        });
        const updateData = await updateResponse.json();
        queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
        toast({
          title: "Profile picture updated",
          description: "Your profile picture has been successfully updated.",
        });
      } catch (error: any) {
        console.error('Profile update failed:', error);
        toast({
          title: "Upload failed", 
          description: error.message || "Failed to update profile picture",
          variant: "destructive",
        });
      }
    } else {
      toast({
        title: "Upload failed",
        description: "No successful upload result received",
        variant: "destructive",
      });
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSave = () => {
    updateProfileMutation.mutate(formData);
  };

  const handleCancel = () => {
    // Reset form data to original values
    if (user) {
      setFormData({
        firstName: (user as any)?.firstName || "",
        lastName: (user as any)?.lastName || "",
        email: (user as any)?.email || "",
        mobile: (user as any)?.mobile || "",
        serviceNumber: (user as any)?.serviceNumber || "",
        profileImageUrl: (user as any)?.profileImageUrl || ""
      });
    }
    setIsEditing(false);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="p-4 lg:p-6">
        <Card>
          <CardContent className="p-8 text-center">
            <p className="text-muted-foreground">
              Please log in to view your profile.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-4 lg:p-6">
      <div className="max-w-2xl mx-auto">
        <div className="mb-6">
          <h1 className="text-3xl font-bold text-foreground mb-2">My Profile</h1>
          <p className="text-muted-foreground">Manage your account information and preferences</p>
        </div>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <User className="h-5 w-5" />
              Profile Information
            </CardTitle>
            <div className="flex gap-2">
              {isEditing ? (
                <>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleCancel}
                    disabled={updateProfileMutation.isPending}
                    data-testid="button-cancel-edit"
                  >
                    <X className="h-4 w-4 mr-2" />
                    Cancel
                  </Button>
                  <Button
                    size="sm"
                    onClick={handleSave}
                    disabled={updateProfileMutation.isPending}
                    data-testid="button-save-profile"
                  >
                    <Save className="h-4 w-4 mr-2" />
                    {updateProfileMutation.isPending ? "Saving..." : "Save"}
                  </Button>
                </>
              ) : (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setIsEditing(true)}
                  data-testid="button-edit-profile"
                >
                  <Edit3 className="h-4 w-4 mr-2" />
                  Edit Profile
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Profile Picture */}
            <div className="flex items-center space-x-4">
              <div className="relative">
                {(user as any)?.profileImageUrl ? (
                  <div className="w-20 h-20 rounded-full overflow-hidden border-4 border-background shadow-lg">
                    <img 
                      src={(user as any).profileImageUrl} 
                      alt="Profile"
                      className="w-full h-full object-cover"
                      data-testid="img-profile-picture"
                    />
                  </div>
                ) : (
                  <div className="w-20 h-20 bg-primary rounded-full flex items-center justify-center text-primary-foreground text-2xl font-bold border-4 border-background shadow-lg">
                    {getUserDisplayInfo(user as any).initials}
                  </div>
                )}

                {/* Upload Button */}
                <div className="absolute -bottom-1 -right-1">
                  <ObjectUploader
                    maxNumberOfFiles={1}
                    maxFileSize={5242880} // 5MB
                    onGetUploadParameters={handleGetUploadParameters}
                    onComplete={handleUploadComplete}
                    buttonClassName="h-8 w-8 rounded-full p-0 shadow-lg bg-primary hover:bg-primary/90"
                  >
                    <Camera className="h-4 w-4 text-primary-foreground" />
                  </ObjectUploader>
                </div>
              </div>

              <div>
                <p className="text-lg font-semibold" data-testid="text-profile-name">
                  {getUserDisplayInfo(user as any).displayName}
                </p>
                <p className="text-sm text-muted-foreground" data-testid="text-profile-role">
                  {(user as any)?.role?.charAt(0).toUpperCase() + (user as any)?.role?.slice(1)}
                </p>
                <p className="text-xs text-muted-foreground mt-1">
                  Click the camera icon to upload a profile picture
                </p>
              </div>
            </div>

            {/* Profile Form */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="firstName">First Name</Label>
                <Input
                  id="firstName"
                  value={formData.firstName}
                  onChange={(e) => handleInputChange('firstName', e.target.value)}
                  disabled={!isEditing}
                  data-testid="input-first-name"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="lastName">Last Name</Label>
                <Input
                  id="lastName"
                  value={formData.lastName}
                  onChange={(e) => handleInputChange('lastName', e.target.value)}
                  disabled={!isEditing}
                  data-testid="input-last-name"
                />
              </div>

              <div className="space-y-2 md:col-span-2">
                <Label htmlFor="email">Email Address</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  disabled={!isEditing}
                  data-testid="input-email"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="mobile">Mobile Number</Label>
                <Input
                  id="mobile"
                  value={formData.mobile}
                  onChange={(e) => handleInputChange('mobile', e.target.value)}
                  disabled={!isEditing}
                  placeholder="+960 XXX-XXXX"
                  data-testid="input-mobile"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="serviceNumber">Service Number</Label>
                <Input
                  id="serviceNumber"
                  value={formData.serviceNumber}
                  onChange={(e) => handleInputChange('serviceNumber', e.target.value)}
                  disabled={!isEditing}
                  data-testid="input-service-number"
                />
              </div>
            </div>

            {/* Account Information */}
            <div className="border-t pt-6">
              <h3 className="text-lg font-semibold mb-4">Account Information</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label>User ID</Label>
                  <div className="p-2 bg-muted rounded text-sm" data-testid="text-user-id">
                    {(user as any)?.id}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label>Account Status</Label>
                  <div className="p-2 bg-muted rounded text-sm" data-testid="text-account-status">
                    {(user as any)?.isActive ? (
                      <span className="text-green-600">Active</span>
                    ) : (
                      <span className="text-red-600">Suspended</span>
                    )}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label>Member Since</Label>
                  <div className="p-2 bg-muted rounded text-sm" data-testid="text-member-since">
                    {(user as any)?.createdAt ? 
                      new Date((user as any).createdAt).toLocaleDateString() : 
                      "Unknown"
                    }
                  </div>
                </div>
                <div className="space-y-2">
                  <Label>Last Updated</Label>
                  <div className="p-2 bg-muted rounded text-sm" data-testid="text-last-updated">
                    {(user as any)?.updatedAt ? 
                      new Date((user as any).updatedAt).toLocaleDateString() : 
                      "Never"
                    }
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

