import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl.js";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err: any) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

client/src/components/WalkieTalkie.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Mic, MicOff, Radio, Users, Volume2 } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";

interface WalkieTalkieProps {
  isOpen: boolean;
  onClose: () => void;
}

interface ConnectedUser {
  userId: string;
  userName: string;
}

export function WalkieTalkie({ isOpen, onClose }: WalkieTalkieProps) {
  const { user } = useAuth();
  const [isConnected, setIsConnected] = useState(false);
  const [isTalking, setIsTalking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [connectedUsers, setConnectedUsers] = useState<ConnectedUser[]>([]);
  const [currentSpeaker, setCurrentSpeaker] = useState<string | null>(null);

  const wsRef = useRef<WebSocket | null>(null);
  const mediaStreamRef = useRef<MediaStream | null>(null);
  const peerConnectionsRef = useRef<Map<string, RTCPeerConnection>>(new Map());
  const clientIdRef = useRef<string>(`client_${Date.now()}_${Math.random()}`);
  const audioContextRef = useRef<AudioContext | null>(null);
  const audioElementsRef = useRef<Map<string, HTMLAudioElement>>(new Map());

  // Initialize WebSocket connection
  useEffect(() => {
    if (!isOpen || !user) return;

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/walkie-talkie`;

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => {
      console.log('Connected to walkie talkie');
      setIsConnected(true);

      // Join the walkie talkie channel
      ws.send(JSON.stringify({
        type: 'join',
        clientId: clientIdRef.current,
        userId: (user as any).id,
        userName: `${(user as any).firstName} ${(user as any).lastName}`
      }));
    };

    ws.onmessage = async (event) => {
      const data = JSON.parse(event.data);

      switch (data.type) {
        case 'users-list':
          setConnectedUsers(data.users);
          break;

        case 'user-joined':
          setConnectedUsers(prev => [...prev.filter(u => u.userId !== data.userId), {
            userId: data.userId,
            userName: data.userName
          }]);
          break;

        case 'user-left':
          setConnectedUsers(prev => prev.filter(u => u.userId !== data.userId));
          // Clean up peer connection
          const pc = peerConnectionsRef.current.get(data.userId);
          if (pc) {
            pc.close();
            peerConnectionsRef.current.delete(data.userId);
          }
          break;

        case 'audio-start':
          setCurrentSpeaker(data.userName);
          break;

        case 'audio-end':
          setCurrentSpeaker(null);
          break;

        case 'signal':
          await handleSignalingMessage(data);
          break;
      }
    };

    ws.onclose = () => {
      setIsConnected(false);
      setCurrentSpeaker(null);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setIsConnected(false);
    };

    return () => {
      ws.close();
      cleanupMedia();
    };
  }, [isOpen, user]);

  const cleanupMedia = () => {
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach(track => track.stop());
      mediaStreamRef.current = null;
    }

    peerConnectionsRef.current.forEach(pc => pc.close());
    peerConnectionsRef.current.clear();

    if (audioContextRef.current) {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    audioElementsRef.current.forEach(audio => {
      audio.pause();
      audio.srcObject = null;
    });
    audioElementsRef.current.clear();
  };

  const createPeerConnection = (userId: string): RTCPeerConnection => {
    const pc = new RTCPeerConnection({
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' },
        { urls: 'stun:stun1.l.google.com:19302' }
      ]
    });

    pc.onicecandidate = (event) => {
      if (event.candidate && wsRef.current) {
        wsRef.current.send(JSON.stringify({
          type: 'signal',
          from: clientIdRef.current,
          to: userId,
          signal: {
            type: 'ice-candidate',
            candidate: event.candidate
          }
        }));
      }
    };

    pc.ontrack = (event) => {
      console.log('Received remote stream from:', userId);
      const audio = new Audio();
      audio.srcObject = event.streams[0];
      audio.play().catch(console.error);
      audioElementsRef.current.set(userId, audio);
    };

    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach(track => {
        pc.addTrack(track, mediaStreamRef.current!);
      });
    }

    peerConnectionsRef.current.set(userId, pc);
    return pc;
  };

  const handleSignalingMessage = async (data: any) => {
    const { from, signal } = data;

    let pc = peerConnectionsRef.current.get(from);
    if (!pc) {
      pc = createPeerConnection(from);
    }

    switch (signal.type) {
      case 'offer':
        await pc.setRemoteDescription(signal.offer);
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);

        if (wsRef.current) {
          wsRef.current.send(JSON.stringify({
            type: 'signal',
            from: clientIdRef.current,
            to: from,
            signal: {
              type: 'answer',
              answer: answer
            }
          }));
        }
        break;

      case 'answer':
        await pc.setRemoteDescription(signal.answer);
        break;

      case 'ice-candidate':
        await pc.addIceCandidate(signal.candidate);
        break;
    }
  };

  const startTalking = async () => {
    try {
      if (!mediaStreamRef.current) {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            autoGainControl: true
          }
        });
        mediaStreamRef.current = stream;
      }

      setIsTalking(true);
      setIsListening(false);

      // Create peer connections for all connected users
      for (const user of connectedUsers) {
        if (user.userId !== (user as any).id) {
          let pc = peerConnectionsRef.current.get(user.userId);
          if (!pc) {
            pc = createPeerConnection(user.userId);
          }

          // Add audio track if not already added
          const tracks = pc.getSenders().map(sender => sender.track);
          const audioTrack = mediaStreamRef.current.getAudioTracks()[0];
          if (!tracks.includes(audioTrack)) {
            pc.addTrack(audioTrack, mediaStreamRef.current);
          }

          // Create offer
          const offer = await pc.createOffer();
          await pc.setLocalDescription(offer);

          if (wsRef.current) {
            wsRef.current.send(JSON.stringify({
              type: 'signal',
              from: clientIdRef.current,
              to: user.userId,
              signal: {
                type: 'offer',
                offer: offer
              }
            }));
          }
        }
      }

      // Notify others that we're speaking
      if (wsRef.current) {
        wsRef.current.send(JSON.stringify({
          type: 'audio-start',
          clientId: clientIdRef.current,
          userId: (user as any).id,
          userName: `${(user as any).firstName} ${(user as any).lastName}`
        }));
      }

    } catch (error) {
      console.error('Error starting to talk:', error);
      alert('Could not access microphone. Please check permissions.');
    }
  };

  const stopTalking = () => {
    setIsTalking(false);
    setIsListening(true);

    // Notify others that we stopped speaking
    if (wsRef.current) {
      wsRef.current.send(JSON.stringify({
        type: 'audio-end',
        clientId: clientIdRef.current,
        userId: (user as any).id
      }));
    }
  };

  // Handle mouse/touch events for push-to-talk
  const handleMouseDown = () => {
    if (!isTalking && isConnected) {
      startTalking();
    }
  };

  const handleMouseUp = () => {
    if (isTalking) {
      stopTalking();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50" data-testid="walkie-talkie-overlay">
      <Card className="w-96 max-w-[90vw]">
        <CardContent className="p-6">
          {/* Header */}
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <Radio className="w-5 h-5" />
              <h3 className="font-semibold">Walkie Talkie</h3>
            </div>
            <div className="flex items-center space-x-2">
              <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
              <span className="text-sm text-muted-foreground">
                {isConnected ? 'Connected' : 'Disconnected'}
              </span>
            </div>
          </div>

          {/* Connected Users */}
          <div className="mb-4">
            <div className="flex items-center space-x-2 mb-2">
              <Users className="w-4 h-4" />
              <span className="text-sm font-medium">Connected ({connectedUsers.length})</span>
            </div>
            <div className="space-y-1 max-h-24 overflow-y-auto">
              {connectedUsers.map(connectedUser => (
                <div key={connectedUser.userId} className="flex items-center space-x-2 text-sm">
                  <div className={`w-2 h-2 rounded-full ${
                    currentSpeaker === connectedUser.userName ? 'bg-green-500 animate-pulse' : 'bg-gray-400'
                  }`} />
                  <span className={currentSpeaker === connectedUser.userName ? 'text-green-600 font-medium' : ''}>
                    {connectedUser.userName}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Current Speaker */}
          {currentSpeaker && (
            <div className="mb-4 p-2 bg-green-50 rounded-lg border border-green-200">
              <div className="flex items-center space-x-2 text-green-700">
                <Volume2 className="w-4 h-4" />
                <span className="text-sm font-medium">{currentSpeaker} is speaking</span>
              </div>
            </div>
          )}

          {/* Push to Talk Button */}
          <div className="text-center mb-4">
            <Button
              className={`w-32 h-32 rounded-full text-lg font-semibold transition-all ${
                isTalking 
                  ? 'bg-red-500 hover:bg-red-600 text-white scale-110' 
                  : 'bg-primary hover:bg-primary/90'
              }`}
              onMouseDown={handleMouseDown}
              onMouseUp={handleMouseUp}
              onTouchStart={handleMouseDown}
              onTouchEnd={handleMouseUp}
              disabled={!isConnected}
              data-testid="push-to-talk-button"
            >
              {isTalking ? <MicOff className="w-8 h-8" /> : <Mic className="w-8 h-8" />}
            </Button>
            <p className="mt-2 text-sm text-muted-foreground">
              {isTalking ? 'Release to stop' : 'Hold to talk'}
            </p>
          </div>

          {/* Status */}
          <div className="text-center mb-4">
            {!isConnected && (
              <p className="text-sm text-red-600">Connecting...</p>
            )}
            {isConnected && connectedUsers.length === 0 && (
              <p className="text-sm text-muted-foreground">No other users connected</p>
            )}
          </div>

          {/* Close Button */}
          <div className="flex justify-center">
            <Button variant="outline" onClick={onClose} data-testid="button-close-walkie-talkie">
              Close
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

