import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { MessageCircle, Send, Mic, MicOff, Play, Clock, Upload, Edit3, Check, X, Trash2, UserX } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";

interface ChatProps {
  isOpen: boolean;
  onClose: () => void;
  onMarkAllRead?: (lastMessageId?: string) => void;
}

interface ChatMessage {
  id: string;
  userId: string;
  userName: string;
  message?: string;
  audioUrl?: string;
  isAudio: boolean;
  timestamp: Date;
  isUploading?: boolean;
}

interface ConnectedUser {
  userId: string;
  userName: string;
}

export function Chat({ isOpen, onClose, onMarkAllRead }: ChatProps) {
  const { user } = useAuth();

  // Helper function to get proper user name
  const getUserName = (userObj: any) => {
    if (!userObj) return "Unknown User";
    const firstName = userObj.firstName?.trim() || "";
    const lastName = userObj.lastName?.trim() || "";
    if (firstName && lastName) {
      return `${firstName} ${lastName}`;
    }
    return firstName || lastName || userObj.email?.split('@')[0] || "Unknown User";
  };

  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const [connectedUsers, setConnectedUsers] = useState<ConnectedUser[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [isUploading, setIsUploading] = useState(false);

  // Username selection states
  const [chatUsername, setChatUsername] = useState("");
  const [isEditingUsername, setIsEditingUsername] = useState(false);
  const [tempUsername, setTempUsername] = useState("");

  const wsRef = useRef<WebSocket | null>(null);
  const mediaStreamRef = useRef<MediaStream | null>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const recordedChunksRef = useRef<Blob[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const clientIdRef = useRef<string>(`client_${Date.now()}_${Math.random()}`);

  // Scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  // Load saved username from localStorage
  useEffect(() => {
    if (!user) return;

    const savedUsername = localStorage.getItem(`chat_username_${(user as any).id}`);
    if (savedUsername) {
      setChatUsername(savedUsername);
    } else {
      // Default to real name
      setChatUsername(getUserName(user));
    }
  }, [user]);

  // Initialize WebSocket connection
  useEffect(() => {
    if (!isOpen || !user) return;

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/chat`;

    wsRef.current = new WebSocket(wsUrl);

    wsRef.current.onopen = () => {
      setIsConnected(true);

      // Join the chat
      wsRef.current?.send(JSON.stringify({
        type: 'join',
        clientId: clientIdRef.current,
        userId: (user as any).id,
        userName: chatUsername || getUserName(user)
      }));
    };

    wsRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      handleWebSocketMessage(data);
    };

    wsRef.current.onclose = () => {
      setIsConnected(false);
      setConnectedUsers([]);
    };

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      if (mediaStreamRef.current) {
        mediaStreamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, [isOpen, user]);

  const handleWebSocketMessage = (data: any) => {
    switch (data.type) {
      case 'user-list':
        setConnectedUsers(data.users || []);
        break;
      case 'text-message':
        setMessages(prev => {
          const newMessages = [...prev, {
            id: data.messageId,
            userId: data.userId,
            userName: data.userName,
            message: data.message,
            isAudio: false,
            timestamp: new Date(data.timestamp)
          }];

          // Mark all messages as read when chat is open and viewing messages
          if (isOpen && onMarkAllRead) {
            onMarkAllRead(data.messageId);
          }

          return newMessages;
        });
        break;
      case 'audio-message':
        setMessages(prev => {
          const newMessages = [...prev, {
            id: data.messageId,
            userId: data.userId,
            userName: data.userName,
            audioUrl: data.audioUrl,
            isAudio: true,
            timestamp: new Date(data.timestamp)
          }];

          // Mark all messages as read when chat is open and viewing messages
          if (isOpen && onMarkAllRead) {
            onMarkAllRead(data.messageId);
          }

          return newMessages;
        });
        break;
    }
  };

  const sendTextMessage = () => {
    if (!newMessage.trim() || !wsRef.current || !isConnected) return;

    const messageId = `${(user as any).id}_${Date.now()}`;

    wsRef.current.send(JSON.stringify({
      type: 'text-message',
      messageId,
      clientId: clientIdRef.current,
      userId: (user as any).id,
      userName: chatUsername || getUserName(user),
      message: newMessage.trim(),
      timestamp: new Date().toISOString()
    }));

    setNewMessage("");
  };

  const startAudioRecording = async () => {
    if (isRecording || isUploading) return;

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });
      mediaStreamRef.current = stream;

      setIsRecording(true);
      recordedChunksRef.current = [];

      if (MediaRecorder.isTypeSupported('audio/webm')) {
        mediaRecorderRef.current = new MediaRecorder(stream, {
          mimeType: 'audio/webm'
        });
      } else {
        mediaRecorderRef.current = new MediaRecorder(stream);
      }

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          recordedChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.start();
    } catch (error) {
      console.error('Could not access microphone:', error);
      alert('Could not access microphone. Please check permissions.');
    }
  };

  const stopAudioRecording = async () => {
    if (!isRecording || !mediaRecorderRef.current) return;

    setIsRecording(false);
    setIsUploading(true);

    mediaRecorderRef.current.addEventListener('stop', async () => {
      await handleAudioUpload();
    }, { once: true });

    mediaRecorderRef.current.stop();

    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach(track => track.stop());
    }
  };

  const handleAudioUpload = async () => {
    if (recordedChunksRef.current.length === 0) {
      setIsUploading(false);
      return;
    }

    try {
      // Create audio blob
      const audioBlob = new Blob(recordedChunksRef.current, { 
        type: mediaRecorderRef.current?.mimeType || 'audio/webm' 
      });

      // Get upload URL
      const uploadResponse = await apiRequest("POST", "/api/chat/upload");
      const uploadData = await uploadResponse.json();

      // Upload audio file
      const uploadResult = await fetch(uploadData.uploadURL, {
        method: 'PUT',
        body: audioBlob,
        headers: {
          'Content-Type': audioBlob.type,
        },
      });

      if (uploadResult.ok) {
        // Update server with audio metadata
        const metadataResponse = await apiRequest("PUT", "/api/chat/audio", {
          audioURL: uploadData.uploadURL,
          userId: (user as any).id,
          userName: chatUsername || getUserName(user)
        });

        const metadata = await metadataResponse.json();

        // Send audio message via WebSocket
        const messageId = `${(user as any).id}_${Date.now()}`;
        wsRef.current?.send(JSON.stringify({
          type: 'audio-message',
          messageId,
          clientId: clientIdRef.current,
          userId: (user as any).id,
          userName: chatUsername || getUserName(user),
          audioUrl: metadata.audioPath,
          timestamp: new Date().toISOString()
        }));
      }
    } catch (error) {
      console.error('Failed to upload audio:', error);
    } finally {
      setIsUploading(false);
    }
  };

  const playAudio = (audioUrl: string) => {
    const audio = new Audio(audioUrl);
    audio.play().catch(console.error);
  };

  // Username management functions
  const saveUsername = (newUsername: string) => {
    if (!user || !newUsername.trim()) return;

    const trimmedUsername = newUsername.trim();
    setChatUsername(trimmedUsername);
    localStorage.setItem(`chat_username_${(user as any).id}`, trimmedUsername);

    // If connected, rejoin with new username
    if (wsRef.current && isConnected) {
      wsRef.current.send(JSON.stringify({
        type: 'join',
        clientId: clientIdRef.current,
        userId: (user as any).id,
        userName: trimmedUsername
      }));
    }
  };

  const startEditingUsername = () => {
    setTempUsername(chatUsername);
    setIsEditingUsername(true);
  };

  const cancelEditingUsername = () => {
    setTempUsername("");
    setIsEditingUsername(false);
  };

  const confirmUsernameEdit = () => {
    if (tempUsername.trim()) {
      saveUsername(tempUsername);
    }
    setIsEditingUsername(false);
    setTempUsername("");
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit'
    });
  };

  // Admin functions for deleting messages
  const isAdmin = (user as any)?.role === 'admin';

  const deleteMessage = async (messageId: string) => {
    try {
      await apiRequest("DELETE", `/api/chat/messages/${messageId}`);
      // Remove message from local state
      setMessages(prev => prev.filter(msg => msg.id !== messageId));
    } catch (error) {
      console.error('Error deleting message:', error);
      alert('Failed to delete message');
    }
  };

  const deleteAllMessagesFromUser = async (userId: string, userName: string) => {
    if (!confirm(`Are you sure you want to delete all messages from ${userName}?`)) {
      return;
    }

    try {
      await apiRequest("DELETE", `/api/chat/messages/user/${userId}`);
      // Remove all messages from this user from local state
      setMessages(prev => prev.filter(msg => msg.userId !== userId));
    } catch (error) {
      console.error('Error deleting user messages:', error);
      alert('Failed to delete user messages');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50" data-testid="chat-overlay">
      <Card className="w-[600px] max-w-[95vw] h-[600px] max-h-[90vh] flex flex-col">
        <CardContent className="p-6 flex-1 flex flex-col min-h-0">
          {/* Header */}
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <MessageCircle className="w-5 h-5" />
              <h2 className="text-lg font-semibold">Team Chat</h2>
              <span className={`text-xs px-2 py-1 rounded ${
                isConnected ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
              }`}>
                {isConnected ? `Connected (${connectedUsers.length})` : 'Connecting...'}
              </span>
            </div>
          </div>

          {/* Username Section */}
          <div className="mb-4 p-3 bg-muted/20 rounded-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <span className="text-sm text-muted-foreground">Your username:</span>
                {isEditingUsername ? (
                  <div className="flex items-center space-x-2">
                    <Input
                      value={tempUsername}
                      onChange={(e) => setTempUsername(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && confirmUsernameEdit()}
                      placeholder="Enter username"
                      className="h-8 text-sm"
                      maxLength={30}
                      data-testid="input-username"
                    />
                    <Button
                      size="sm"
                      onClick={confirmUsernameEdit}
                      disabled={!tempUsername.trim()}
                      className="h-8 w-8 p-0"
                      data-testid="button-confirm-username"
                    >
                      <Check className="w-3 h-3" />
                    </Button>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={cancelEditingUsername}
                      className="h-8 w-8 p-0"
                      data-testid="button-cancel-username"
                    >
                      <X className="w-3 h-3" />
                    </Button>
                  </div>
                ) : (
                  <>
                    <span className="font-medium text-sm" data-testid="text-current-username">
                      {chatUsername || getUserName(user)}
                    </span>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={startEditingUsername}
                      className="h-6 w-6 p-0"
                      title="Edit username"
                      data-testid="button-edit-username"
                    >
                      <Edit3 className="w-3 h-3" />
                    </Button>
                  </>
                )}
              </div>
            </div>
          </div>

          {/* Messages */}
          <div className="flex-1 border rounded-lg p-4 bg-muted/10 overflow-y-auto min-h-0 space-y-3">
            {messages.length === 0 ? (
              <p className="text-sm text-muted-foreground text-center py-8">
                No messages yet. Start the conversation!
              </p>
            ) : (
              messages.map((msg) => (
                <div key={msg.id} className="flex flex-col space-y-1 group">
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-medium">{msg.userName}</span>
                    <div className="flex items-center space-x-1 text-xs text-muted-foreground">
                      <Clock className="w-3 h-3" />
                      <span>{formatTime(msg.timestamp)}</span>
                    </div>
                    {/* Admin controls */}
                    {isAdmin && (
                      <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity">
                        <Button
                          variant="ghost"
                          size="sm"
                          className="h-6 w-6 p-0 text-red-500 hover:text-red-700"
                          onClick={() => deleteMessage(msg.id)}
                          title="Delete message"
                          data-testid={`button-delete-message-${msg.id}`}
                        >
                          <Trash2 className="w-3 h-3" />
                        </Button>
                        {msg.userId !== (user as any)?.id && (
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-6 w-6 p-0 text-red-500 hover:text-red-700"
                            onClick={() => deleteAllMessagesFromUser(msg.userId, msg.userName)}
                            title={`Delete all messages from ${msg.userName}`}
                            data-testid={`button-delete-user-messages-${msg.userId}`}
                          >
                            <UserX className="w-3 h-3" />
                          </Button>
                        )}
                      </div>
                    )}
                  </div>
                  {msg.isAudio ? (
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-muted-foreground">ðŸŽµ Audio message</span>
                      {msg.isUploading && (
                        <Upload className="w-3 h-3 text-blue-500 animate-spin" />
                      )}
                      {msg.audioUrl && (
                        <Button
                          variant="ghost"
                          size="sm"
                          className="h-6 w-6 p-0"
                          onClick={() => playAudio(msg.audioUrl!)}
                          title="Play audio"
                          data-testid={`button-play-audio-${msg.id}`}
                        >
                          <Play className="w-3 h-3" />
                        </Button>
                      )}
                    </div>
                  ) : (
                    <p className="text-sm bg-background rounded-lg px-3 py-2 max-w-fit" data-testid={`message-text-${msg.id}`}>{msg.message}</p>
                  )}
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="flex items-center space-x-2 mt-4">
            <Input
              placeholder="Type a message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendTextMessage()}
              disabled={!isConnected}
              data-testid="input-message"
            />
            <Button
              onClick={sendTextMessage}
              disabled={!newMessage.trim() || !isConnected}
              size="sm"
              data-testid="button-send-message"
            >
              <Send className="w-4 h-4" />
            </Button>
            <Button
              onClick={isRecording ? stopAudioRecording : startAudioRecording}
              disabled={!isConnected || isUploading}
              size="sm"
              variant={isRecording ? "destructive" : "default"}
              data-testid="button-audio-record"
              title={isRecording ? "Stop recording" : "Record audio message"}
            >
              {isUploading ? (
                <Upload className="w-4 h-4 animate-spin" />
              ) : isRecording ? (
                <MicOff className="w-4 h-4" />
              ) : (
                <Mic className="w-4 h-4" />
              )}
            </Button>
          </div>

          {/* Close Button */}
          <div className="flex justify-center pt-4 border-t">
            <Button variant="outline" onClick={onClose} data-testid="button-close-chat">
              Close
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

