import { useState, useEffect } from 'react';
import { MapContainer, TileLayer, useMapEvents, Marker, Popup, useMap } from 'react-leaflet';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { MapPin, Navigation, Target } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers in Leaflet with React
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

interface MapLocationPickerProps {
  isOpen: boolean;
  onClose: () => void;
  onLocationSelect: (location: { latitude: number; longitude: number; address: string }) => void;
  initialLocation?: { latitude: number; longitude: number; address: string };
  title?: string;
}

// Direct OpenStreetMap tile layer for reliable loading
function DirectTileLayer() {
  return (
    <TileLayer
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      attribution="Â© OpenStreetMap contributors"
      maxZoom={18}
      minZoom={1}
      subdomains={['a', 'b', 'c']}
    />
  );
}

// Component to automatically center and zoom map when location changes
function MapController({ center, zoom }: { center: [number, number]; zoom: number }) {
  const map = useMap();

  useEffect(() => {
    map.setView(center, zoom, {
      animate: true,
      duration: 1.5
    });
  }, [map, center, zoom]);

  return null;
}

// Component to handle map clicks and update marker position
function LocationSelector({ onLocationChange, selectedLocation }: {
  onLocationChange: (lat: number, lng: number) => void;
  selectedLocation: [number, number] | null;
}) {
  useMapEvents({
    click: (e) => {
      onLocationChange(e.latlng.lat, e.latlng.lng);
    },
  });

  return selectedLocation ? (
    <Marker position={selectedLocation}>
      <Popup>
        <div className="p-2">
          <strong className="text-sm">Selected Location</strong>
          <br />
          <span className="text-xs text-gray-600">
            {selectedLocation[0].toFixed(6)}, {selectedLocation[1].toFixed(6)}
          </span>
        </div>
      </Popup>
    </Marker>
  ) : null;
}

export function MapLocationPicker({ 
  isOpen, 
  onClose, 
  onLocationSelect, 
  initialLocation,
  title = "Select Location on Map"
}: MapLocationPickerProps) {
  const { toast } = useToast();
  const [selectedLocation, setSelectedLocation] = useState<[number, number] | null>(
    initialLocation ? [initialLocation.latitude, initialLocation.longitude] : null
  );
  const [address, setAddress] = useState(initialLocation?.address || '');
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [locationAccuracy, setLocationAccuracy] = useState<number | null>(null);
  const [shouldZoomIn, setShouldZoomIn] = useState(!!initialLocation);

  // Default to center of Maldives
  const maldivesCenter: [number, number] = [3.2028, 73.2207];
  const mapCenter = selectedLocation || maldivesCenter;

  const handleLocationChange = (lat: number, lng: number) => {
    setSelectedLocation([lat, lng]);
    setShouldZoomIn(true); // Always zoom when manually selecting on map
    setLocationAccuracy(null); // Clear GPS accuracy for manual selection
    // Auto-generate a simple address description
    setAddress(`Location: ${lat.toFixed(6)}, ${lng.toFixed(6)}`);

    toast({
      title: "Location Selected",
      description: `Coordinates: ${lat.toFixed(4)}, ${lng.toFixed(4)}`,
    });
  };

  const handleGetCurrentLocation = () => {
    setIsGettingLocation(true);
    if (navigator.geolocation) {
      const timeoutId = setTimeout(() => {
        setIsGettingLocation(false);
        toast({
          title: "Location Timeout",
          description: "Location request timed out. Please try again or select manually on the map.",
          variant: "destructive",
        });
      }, 10000); // 10 second timeout

      navigator.geolocation.getCurrentPosition(
        (position) => {
          clearTimeout(timeoutId);
          const { latitude, longitude, accuracy } = position.coords;

          setSelectedLocation([latitude, longitude]);
          setLocationAccuracy(accuracy);
          setAddress(`Current Location: ${latitude.toFixed(6)}, ${longitude.toFixed(6)}`);
          setIsGettingLocation(false);

          // Only zoom in if GPS accuracy is good (â‰¤50 meters)
          const isAccurate = accuracy <= 50;
          setShouldZoomIn(isAccurate);

          if (isAccurate) {
            toast({
              title: "Accurate Location Found",
              description: `GPS accuracy: ${accuracy.toFixed(0)}m - Zooming in for precise view`,
            });
          } else {
            toast({
              title: "Location Found (Low Accuracy)",
              description: `GPS accuracy: ${accuracy.toFixed(0)}m - Location marked but not zoomed. Try again for better accuracy.`,
              variant: "default",
            });
          }
        },
        (error) => {
          clearTimeout(timeoutId);
          console.error('Error getting current location:', error);
          setIsGettingLocation(false);

          let errorMessage = "Could not get your current location";
          switch(error.code) {
            case error.PERMISSION_DENIED:
              errorMessage = "Location access denied. Please grant location permissions and try again.";
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage = "Location information unavailable. Please select manually on the map.";
              break;
            case error.TIMEOUT:
              errorMessage = "Location request timed out. Please try again.";
              break;
          }

          toast({
            title: "Location Error",
            description: errorMessage,
            variant: "destructive",
          });
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 30000 // 30 seconds cache for fresher location
        }
      );
    } else {
      setIsGettingLocation(false);
      toast({
        title: "Not Supported",
        description: "Geolocation is not supported by this browser",
        variant: "destructive",
      });
    }
  };

  const handleConfirm = () => {
    if (selectedLocation) {
      const locationData = {
        latitude: selectedLocation[0],
        longitude: selectedLocation[1],
        address: address || `Location: ${selectedLocation[0].toFixed(6)}, ${selectedLocation[1].toFixed(6)}`
      };

      // Call the callback function
      onLocationSelect(locationData);

      toast({
        title: "Location Confirmed",
        description: "Location has been set for your event",
      });

      onClose();
    }
  };

  const handleCancel = () => {
    // Reset to initial values
    if (initialLocation) {
      setSelectedLocation([initialLocation.latitude, initialLocation.longitude]);
      setAddress(initialLocation.address);
    } else {
      setSelectedLocation(null);
      setAddress('');
    }
    onClose();
  };

  return (
    <div className="dialog-overlay-modal">
      <Dialog open={isOpen} onOpenChange={handleCancel}>
        <DialogContent 
          className="max-w-4xl h-[80vh] flex flex-col p-0"
        >
        <DialogHeader className="p-6 pb-0 flex-shrink-0">
          <DialogTitle className="flex items-center gap-2">
            <MapPin className="h-5 w-5" />
            {title}
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1 overflow-y-auto p-6 pt-4 space-y-4">
          {/* Controls */}
          <div className="flex flex-col sm:flex-row gap-2">
            <div className="flex-1">
              <Input
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter location description..."
                data-testid="input-location-address"
              />
            </div>
            <Button
              onClick={handleGetCurrentLocation}
              disabled={isGettingLocation}
              variant="outline"
              className="flex items-center gap-2"
              data-testid="button-current-location"
            >
              <Navigation className="h-4 w-4" />
              {isGettingLocation ? 'Getting...' : 'My Location'}
            </Button>
          </div>


          {/* Map */}
          <div className="h-[300px] w-full border rounded-md overflow-hidden">
            <MapContainer
              center={mapCenter}
              zoom={shouldZoomIn ? 15 : 7}
              style={{ height: '100%', width: '100%' }}
              className="cursor-crosshair"
            >
              <DirectTileLayer />
              <MapController 
                center={mapCenter} 
                zoom={shouldZoomIn ? 15 : 7} 
              />
              <LocationSelector 
                onLocationChange={handleLocationChange}
                selectedLocation={selectedLocation}
              />
            </MapContainer>
          </div>

          {/* Selected coordinates display */}
          {selectedLocation && (
            <div className="text-sm bg-blue-50 p-3 rounded-md">
              <div className="flex items-center gap-2">
                <MapPin className="h-4 w-4 text-blue-600" />
                <strong className="text-blue-800">Selected Coordinates:</strong>
              </div>
              <div className="ml-6 text-blue-700">
                Latitude: {selectedLocation[0].toFixed(6)} | Longitude: {selectedLocation[1].toFixed(6)}
                {locationAccuracy !== null && locationAccuracy !== undefined && (
                  <div className="mt-1">
                    GPS Accuracy: Â±{locationAccuracy.toFixed(0)}m 
                    {locationAccuracy <= 50 ? (
                      <span className="text-green-600 font-medium"> (High Accuracy)</span>
                    ) : (
                      <span className="text-orange-600 font-medium"> (Low Accuracy)</span>
                    )}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <DialogFooter className="p-6 pt-0 flex-shrink-0 border-t bg-gray-50">
          <Button
            variant="outline"
            onClick={handleCancel}
            data-testid="button-cancel-location"
          >
            Cancel
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={!selectedLocation}
            data-testid="button-confirm-location"
          >
            Confirm Location
          </Button>
        </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/event-share.tsx.content:
import { useState, useEffect } from 'react';
import { useRoute } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { CalendarDays, MapPin, Users, Contact, MessageSquare, Clock, Navigation, Share2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers in Leaflet with React
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Custom tile layer that uses the server's cached tiles
function CachedTileLayer() {
  const map = useRoute()[1]?.params;

  useEffect(() => {
    // This will be handled by the MapContainer's TileLayer component
  }, []);

  return (
    <TileLayer
      url="/api/tiles/{z}/{x}/{y}"
      attribution="Â© OpenStreetMap contributors"
      maxZoom={18}
      minZoom={1}
    />
  );
}

export default function EventShare() {
  const [match, params] = useRoute('/share/event/:id');
  const { toast } = useToast();
  const [currentLocation, setCurrentLocation] = useState<[number, number] | null>(null);

  const eventId = params?.id;

  // Fetch event data using public endpoint (no auth required)
  const { data: event, isLoading, error } = useQuery({
    queryKey: ['/api/public/events', eventId],
    queryFn: async () => {
      const response = await fetch(`/api/public/events/${eventId}`);
      if (!response.ok) {
        throw new Error('Event not found');
      }
      return response.json();
    },
    enabled: !!eventId,
  });

  // Get user's current location
  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCurrentLocation([position.coords.latitude, position.coords.longitude]);
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  };

  // Share functionality
  const handleShare = async () => {
    const shareUrl = window.location.href;

    if (navigator.share) {
      try {
        await navigator.share({
          title: `${event.title} - Maldives Event`,
          text: `Join us at ${event.title} in ${event.island}, ${event.atoll}`,
          url: shareUrl,
        });
      } catch (error) {
        // Fallback to clipboard copy
        copyToClipboard(shareUrl);
      }
    } else {
      copyToClipboard(shareUrl);
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text).then(() => {
      toast({
        title: "Link Copied",
        description: "Event link copied to clipboard",
      });
    });
  };

  const getDirections = () => {
    if (event?.id) {
      window.location.href = `/map?event=${event.id}`;
    }
  };

  useEffect(() => {
    // Set page title and meta tags for sharing
    if (event) {
      document.title = `${event.title} - Maldives Event`;

      // Update meta tags for better social sharing
      const updateMetaTag = (property: string, content: string) => {
        let meta = document.querySelector(`meta[property="${property}"]`) as HTMLMetaElement;
        if (!meta) {
          meta = document.createElement('meta');
          meta.setAttribute('property', property);
          document.head.appendChild(meta);
        }
        meta.content = content;
      };

      updateMetaTag('og:title', `${event.title} - Maldives Event`);
      updateMetaTag('og:description', `Join us at ${event.title} in ${event.island}, ${event.atoll}. ${event.comment || ''}`);
      updateMetaTag('og:type', 'website');
      updateMetaTag('og:url', window.location.href);

      // Add event-specific meta tags
      updateMetaTag('event:location', `${event.island}, ${event.atoll}`);
      if (event.latitude && event.longitude) {
        updateMetaTag('event:coordinates', `${event.latitude},${event.longitude}`);
      }
    }
  }, [event]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (error || !event) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <Card className="max-w-md w-full mx-4">
          <CardContent className="p-6 text-center">
            <h1 className="text-2xl font-bold text-gray-900 mb-2">Event Not Found</h1>
            <p className="text-gray-600 mb-4">The event you're looking for doesn't exist or has been removed.</p>
            <Button onClick={() => window.location.href = '/'}>
              Go to Homepage
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const eventDate = new Date(event.createdAt || Date.now());
  const hasLocation = event.latitude && event.longitude;
  const eventCoords: [number, number] | null = hasLocation 
    ? [parseFloat(event.latitude), parseFloat(event.longitude)]
    : null;

  // Default center on Maldives if no specific location
  const mapCenter: [number, number] = eventCoords || [3.2028, 73.2207];
  const mapZoom = eventCoords ? 14 : 7;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-blue-500 rounded-lg flex items-center justify-center">
                <CalendarDays className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">Maldives Events</h1>
                <p className="text-sm text-gray-600">Shared Event Details</p>
              </div>
            </div>
            <Button 
              onClick={handleShare}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
              data-testid="button-share-event"
            >
              <Share2 className="h-4 w-4" />
              Share
            </Button>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 py-6 space-y-6">
        {/* Event Details Card */}
        <Card>
          <CardHeader>
            <div className="flex items-start justify-between">
              <div className="space-y-2">
                <CardTitle className="text-2xl">{event.title}</CardTitle>
                <div className="flex items-center gap-4 text-sm text-gray-600">
                  <div className="flex items-center gap-1">
                    <MapPin className="h-4 w-4" />
                    <span>{event.island}, {event.atoll}</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <Clock className="h-4 w-4" />
                    <span>{eventDate.toLocaleDateString()}</span>
                  </div>
                </div>
              </div>
              <Badge 
                variant={event.status === 'active' ? 'default' : 
                       event.status === 'completed' ? 'secondary' : 'outline'}
              >
                {event.status}
              </Badge>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Event Location */}
            {event.eventLocation && (
              <div>
                <h3 className="font-semibold text-sm text-gray-700 mb-1">Event Location</h3>
                <p className="text-gray-900">{event.eventLocation}</p>
              </div>
            )}

            {/* Waiting Location */}
            {event.waitingLocation && (
              <div>
                <h3 className="font-semibold text-sm text-gray-700 mb-1">Waiting Location</h3>
                <p className="text-gray-900">{event.waitingLocation}</p>
              </div>
            )}

            {/* Contact */}
            {event.contact && (
              <div>
                <h3 className="font-semibold text-sm text-gray-700 mb-1 flex items-center gap-1">
                  <Contact className="h-4 w-4" />
                  Contact
                </h3>
                <p className="text-gray-900">{event.contact}</p>
              </div>
            )}

            {/* Comment */}
            {event.comment && (
              <div>
                <h3 className="font-semibold text-sm text-gray-700 mb-1 flex items-center gap-1">
                  <MessageSquare className="h-4 w-4" />
                  Details
                </h3>
                <p className="text-gray-900">{event.comment}</p>
              </div>
            )}

            {/* Action Buttons */}
            <div className="flex gap-3 pt-4">
              {hasLocation && (
                <Button 
                  onClick={getDirections}
                  className="flex items-center gap-2"
                  data-testid="button-get-directions"
                >
                  <Navigation className="h-4 w-4" />
                  Get Directions
                </Button>
              )}
              <Button 
                onClick={getCurrentLocation}
                variant="outline"
                className="flex items-center gap-2"
                data-testid="button-my-location"
              >
                <MapPin className="h-4 w-4" />
                My Location
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Map Card */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5" />
              Event Location Map
            </CardTitle>
          </CardHeader>
          <CardContent className="p-0">
            <div className="h-[400px] w-full">
              <MapContainer
                center={mapCenter}
                zoom={mapZoom}
                style={{ height: '100%', width: '100%' }}
              >
                <CachedTileLayer />

                {/* Event Location Marker */}
                {eventCoords && (
                  <Marker position={eventCoords}>
                    <Popup>
                      <div className="p-2">
                        <strong className="text-sm">{event.title}</strong>
                        <br />
                        <span className="text-xs text-gray-600">
                          {event.island}, {event.atoll}
                        </span>
                        <br />
                        <span className="text-xs text-gray-500">
                          {eventCoords[0].toFixed(4)}, {eventCoords[1].toFixed(4)}
                        </span>
                      </div>
                    </Popup>
                  </Marker>
                )}

                {/* User's Current Location */}
                {currentLocation && (
                  <Marker 
                    position={currentLocation}
                    icon={L.icon({
                      iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
                      shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
                      iconSize: [25, 41],
                      iconAnchor: [12, 41],
                      popupAnchor: [1, -34],
                      shadowSize: [41, 41]
                    })}
                  >
                    <Popup>
                      <div className="p-2">
                        <strong className="text-sm">Your Location</strong>
                        <br />
                        <span className="text-xs text-gray-600">
                          {currentLocation[0].toFixed(4)}, {currentLocation[1].toFixed(4)}
                        </span>
                      </div>
                    </Popup>
                  </Marker>
                )}
              </MapContainer>
            </div>
          </CardContent>
        </Card>

        {/* Footer */}
        <div className="text-center text-sm text-gray-500">
          <p>Powered by Maldives Event Manager</p>
        </div>
      </div>
    </div>
  );
}

client/src/components/ConfirmDialog.tsx.content:
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface ConfirmDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title: string;
  description: string;
  confirmText?: string;
  cancelText?: string;
  variant?: "default" | "destructive";
  isLoading?: boolean;
}

export function ConfirmDialog({
  isOpen,
  onClose,
  onConfirm,
  title,
  description,
  confirmText = "Continue",
  cancelText = "Cancel",
  variant = "destructive",
  isLoading = false,
}: ConfirmDialogProps) {
  return (
    <AlertDialog open={isOpen} onOpenChange={onClose}>
      <AlertDialogContent data-testid="confirm-dialog">
        <AlertDialogHeader>
          <AlertDialogTitle data-testid="confirm-dialog-title">
            {title}
          </AlertDialogTitle>
          <AlertDialogDescription data-testid="confirm-dialog-description">
            {description}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel 
            onClick={onClose}
            disabled={isLoading}
            data-testid="confirm-dialog-cancel"
          >
            {cancelText}
          </AlertDialogCancel>
          <AlertDialogAction 
            onClick={onConfirm}
            disabled={isLoading}
            className={variant === "destructive" ? "bg-red-600 hover:bg-red-700" : ""}
            data-testid="confirm-dialog-confirm"
          >
            {isLoading ? "Processing..." : confirmText}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

