client/src/components/equipment-transfer.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Equipment } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogHeader, 
  DialogTitle, 
  DialogTrigger 
} from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { usePermissions } from "@/hooks/usePermissions";
import { ArrowRight, Move } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

const transferSchema = z.object({
  quantityToMove: z.number().min(1, "Quantity must be at least 1"),
  destinationAtoll: z.string().min(1, "Destination atoll is required"),
  destinationIsland: z.string().optional(),
  notes: z.string().optional(),
});

type TransferForm = z.infer<typeof transferSchema>;

interface EquipmentTransferFormProps {
  equipment: Equipment;
  onSuccess?: () => void;
}

export default function EquipmentTransferForm({ equipment, onSuccess }: EquipmentTransferFormProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { canTransferEquipment } = usePermissions();

  // Fetch islands data for dynamic dropdown
  const { data: islandsData = [] } = useQuery({
    queryKey: ["/api/islands"],
  });

  const form = useForm<TransferForm>({
    resolver: zodResolver(transferSchema),
    defaultValues: {
      quantityToMove: 1,
      destinationAtoll: "",
      destinationIsland: "",
      notes: "",
    }
  });

  const transferMutation = useMutation({
    mutationFn: async (data: TransferForm) => {
      const response = await apiRequest('POST', `/api/equipment/${equipment.id}/transfer`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });
      queryClient.invalidateQueries({ queryKey: ['/api/equipment/transfers'] });
      toast({
        title: "Equipment Transferred",
        description: `Successfully moved ${form.getValues('quantityToMove')} ${equipment.name} to ${form.getValues('destinationAtoll')}`,
      });
      form.reset();
      setOpen(false);
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Transfer Failed",
        description: error.message || "Failed to transfer equipment",
        variant: "destructive",
      });
    }
  });

  const onSubmit = (data: TransferForm) => {
    if (data.quantityToMove > equipment.quantity) {
      toast({
        title: "Invalid Quantity",
        description: `Cannot move ${data.quantityToMove} items. Only ${equipment.quantity} available.`,
        variant: "destructive",
      });
      return;
    }

    transferMutation.mutate(data);
  };

  const isTransferDisabled = equipment.quantity <= 0 || equipment.status === "transferred" || !canTransferEquipment;

  // Get unique atolls from islands data
  const atolls = Array.from(new Set((islandsData as any[]).map((island: any) => island.atoll))).sort();

  // Filter islands by selected atoll
  const availableIslands = form.watch("destinationAtoll") 
    ? (islandsData as any[]).filter((island: any) => island.atoll === form.watch("destinationAtoll"))
    : (islandsData as any[]);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button 
          variant="outline" 
          size="sm" 
          disabled={isTransferDisabled}
          data-testid={`button-transfer-${equipment.id}`}
        >
          <Move className="w-4 h-4 mr-2" />
          Transfer
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle data-testid="title-transfer-equipment">Transfer Equipment</DialogTitle>
          <DialogDescription>
            Move {equipment.name} from {equipment.atoll} to another location
          </DialogDescription>
        </DialogHeader>

        <div className="bg-muted p-3 rounded-lg mb-4" data-testid="transfer-source-info">
          <div className="flex items-center gap-2 text-sm">
            <div className="font-medium">From:</div>
            <div>{equipment.atoll}{equipment.island ? ` - ${equipment.island}` : ""}</div>
          </div>
          <div className="flex items-center gap-2 text-sm">
            <div className="font-medium">Available:</div>
            <div data-testid="text-available-quantity">{equipment.quantity} units</div>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="quantityToMove"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Quantity to Move</FormLabel>
                  <FormControl>
                    <Input
                      type="number"
                      min={1}
                      max={equipment.quantity}
                      {...field}
                      value={field.value || ""}
                      onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : "")}
                      data-testid="input-transfer-quantity"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <ArrowRight className="w-4 h-4" />
              <span>Transfer to destination</span>
            </div>

            <FormField
              control={form.control}
              name="destinationAtoll"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Destination Atoll</FormLabel>
                  <Select 
                    onValueChange={(value) => {
                      field.onChange(value);
                      // Clear island selection when atoll changes
                      form.setValue("destinationIsland", "");
                    }} 
                    defaultValue={field.value}
                  >
                    <FormControl>
                      <SelectTrigger data-testid="select-destination-atoll">
                        <SelectValue placeholder="Select destination atoll" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {atolls.map((atoll, index) => (
                        <SelectItem key={`transfer-atoll-${index}`} value={atoll}>
                          {atoll}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="destinationIsland"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Destination Island</FormLabel>
                  <Select 
                    onValueChange={field.onChange}
                    value={field.value || ""}
                    disabled={!form.watch("destinationAtoll")}
                  >
                    <FormControl>
                      <SelectTrigger data-testid="select-destination-island">
                        <SelectValue placeholder={form.watch("destinationAtoll") ? "Select Island" : "Select Atoll First"} />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {availableIslands.map((island: any) => (
                        <SelectItem key={island.id} value={island.name}>
                          {island.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Transfer Notes (Optional)</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Reason for transfer, contact person, etc..."
                      rows={3}
                      {...field}
                      data-testid="input-transfer-notes"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-2 pt-2">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => setOpen(false)}
                data-testid="button-cancel-transfer"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={transferMutation.isPending}
                data-testid="button-confirm-transfer"
              >
                {transferMutation.isPending ? "Transferring..." : "Transfer Equipment"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

