import { useEffect, useRef, useState, useCallback } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import { Icon, LatLngTuple } from 'leaflet';
import L from 'leaflet';
import { useQuery } from '@tanstack/react-query';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { useLocationTracking } from '@/hooks/useLocationTracking';
import { Calendar, MapPin, Users, Clock, Search, Navigation, X, ChevronLeft, ChevronRight, MapIcon, Route, Target } from 'lucide-react';
import { format } from 'date-fns';
import * as polyline from 'polyline';
import { routingService } from '@/lib/routingService';
import ModernDirectionsPanel from './ModernDirectionsPanel';
import type { Event, Island, IslandWithUserVisitStatus } from '@shared/schema';

// Maldives coordinates (central position)
const MALDIVES_CENTER: LatLngTuple = [3.2028, 73.2207];
const DEFAULT_ZOOM = 7;
const MAX_ZOOM = 18;

// Maldives bounds to ensure proper fit
const MALDIVES_BOUNDS: [LatLngTuple, LatLngTuple] = [
  [-0.5, 72.5], // Southwest corner
  [7.5, 74.0]   // Northeast corner  
];

// Custom event marker icon
const eventIcon = new Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Dynamic location marker creator - now direction-aware
const createLocationIcon = (deviceHeading: number, isCompassEnabled: boolean) => {
  return new L.DivIcon({
    html: `
      <div class="current-location-marker">
        <div class="location-pulse"></div>
        <div class="location-marker ${isCompassEnabled ? 'directional-marker' : 'dot-marker'}" 
             style="transform: rotate(${isCompassEnabled ? deviceHeading : 0}deg)">
          ${isCompassEnabled ? 'â–²' : ''}
        </div>
      </div>
    `,
    className: 'custom-location-icon',
    iconSize: [40, 40],
    iconAnchor: [20, 20],
    popupAnchor: [0, -20]
  });
};

interface SimpleMapProps {
  selectedEventId?: string | null;
  onEventSelect?: (eventId: string | null) => void;
  className?: string;
  autoDirections?: boolean;
}

// Component to handle map center changes and ensure proper bounds
function MapController({ 
  eventId, 
  events, 
  islandCoords,
  userLocation,
  isZoomLocked
}: { 
  eventId?: string | null; 
  events: Event[];
  islandCoords: Record<string, { lat: number; lng: number; atoll: string }>;
  userLocation?: [number, number] | null;
  isZoomLocked: boolean;
}) {
  const map = useMap();

  useEffect(() => {
    // Set max bounds for Maldives region
    map.setMaxBounds(MALDIVES_BOUNDS);

    if (eventId && events.length > 0) {
      // Find the selected event
      const selectedEvent = events.find(event => event.id === eventId);

      if (selectedEvent) {
        let eventLat, eventLng;

        // Use event's actual coordinates if available, fallback to island coordinates
        if (selectedEvent.latitude && selectedEvent.longitude) {
          eventLat = parseFloat(selectedEvent.latitude);
          eventLng = parseFloat(selectedEvent.longitude);
        } else {
          const coords = islandCoords[`${selectedEvent.atoll}_${selectedEvent.island}`];
          if (coords) {
            eventLat = coords.lat;
            eventLng = coords.lng;
          }
        }

        if (eventLat && eventLng && !isNaN(eventLat) && !isNaN(eventLng)) {
          // Navigate to the event location - respect zoom lock
          if (isZoomLocked) {
            // Only change position, keep current zoom when locked
            map.panTo([eventLat, eventLng], { animate: true, duration: 1 });
          } else {
            // Normal behavior when not zoom locked
            map.setView([eventLat, eventLng], 12, { animate: true, duration: 1 });
          }
        }
      }
    } else if (userLocation && !isZoomLocked) {
      // Only set view to user location if not zoom locked (this should only happen on initial load)
      map.setView(userLocation, MAX_ZOOM, { animate: true, duration: 1 });
    } else if (!userLocation && !isZoomLocked) {
      // Final fallback to Maldives center only if not zoom locked
      map.setView(MALDIVES_CENTER, DEFAULT_ZOOM);
    }
  }, [eventId, events, islandCoords, userLocation, map, isZoomLocked]);

  return null;
}

export function SimpleMap({ selectedEventId, onEventSelect, className, autoDirections }: SimpleMapProps) {
  const mapRef = useRef<L.Map>(null);
  const [mapReady, setMapReady] = useState(false);
  const [userLocation, setUserLocation] = useState<[number, number] | null>(null);
  const [locationLoaded, setLocationLoaded] = useState(false);
  const [isZoomLocked, setIsZoomLocked] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredIslands, setFilteredIslands] = useState<Island[]>([]);
  const [showSearchResults, setShowSearchResults] = useState(false);

  // Direction-based map rotation state
  const [isDirectionMode, setIsDirectionMode] = useState(false);
  const [previousLocation, setPreviousLocation] = useState<[number, number] | null>(null);
  const [currentBearing, setCurrentBearing] = useState<number>(0);
  const [locationHistory, setLocationHistory] = useState<Array<{lat: number, lng: number, timestamp: number}>>([]);

  // Compass state
  const [isCompassEnabled, setIsCompassEnabled] = useState(false);
  const [deviceHeading, setDeviceHeading] = useState<number>(0);
  const [isWatchingLocation, setIsWatchingLocation] = useState(false);
  const [userInteracted, setUserInteracted] = useState(false);
  const watchId = useRef<number | null>(null);
  const isMoving = useRef<boolean>(false);
  const interactionTimeout = useRef<number | null>(null);

  // Directions state
  const [directionsMode, setDirectionsMode] = useState(false);
  const [routeCoordinates, setRouteCoordinates] = useState<[number, number][]>([]);
  const [routeInstructions, setRouteInstructions] = useState<string[]>([]);
  const [routeDistance, setRouteDistance] = useState<string>('');
  const [routeDuration, setRouteDuration] = useState<string>('');
  const [isLoadingDirections, setIsLoadingDirections] = useState(false);

  const { toast } = useToast();

  // Calculate bearing between two points
  const calculateBearing = useCallback((startLat: number, startLng: number, endLat: number, endLng: number): number => {
    const dLng = (endLng - startLng) * Math.PI / 180;
    const startLatRad = startLat * Math.PI / 180;
    const endLatRad = endLat * Math.PI / 180;

    const y = Math.sin(dLng) * Math.cos(endLatRad);
    const x = Math.cos(startLatRad) * Math.sin(endLatRad) - Math.sin(startLatRad) * Math.cos(endLatRad) * Math.cos(dLng);

    let bearing = Math.atan2(y, x) * 180 / Math.PI;
    return (bearing + 360) % 360; // Normalize to 0-360
  }, []);

  // Update map rotation based on bearing
  const updateMapRotation = useCallback((bearing: number) => {
    const map = mapRef.current;
    if (!map || !isDirectionMode) return;

    // Rotate the map so the bearing direction points up
    const rotation = -bearing; // Negative because we want to rotate the map, not the direction

    // Apply CSS transform to rotate the map container
    const mapContainer = map.getContainer();
    if (mapContainer) {
      mapContainer.style.transform = `rotate(${rotation}deg)`;
      mapContainer.style.transformOrigin = 'center center';
    }
  }, [isDirectionMode]);

  // Location tracking for automatic island visit detection
  const { 
    isTracking, 
    nearbyIslands,
    checkLocationForIslandVisits 
  } = useLocationTracking({
    enabled: true,
    checkInterval: 45000, // 45 seconds for map context
    showToasts: true
  });

  // Fetch events data
  const { data: events = [], isLoading: eventsLoading } = useQuery<Event[]>({
    queryKey: ['/api/events'],
    enabled: mapReady
  });

  // Fetch islands data
  const { data: islands = [], isLoading: islandsLoading } = useQuery<IslandWithUserVisitStatus[]>({
    queryKey: ['/api/islands'],
    enabled: mapReady
  });

  // Create a map of island keys (atoll_island) to coordinates to handle same-named islands
  const islandCoords = islands.reduce((acc, island) => {
    if (island.latitude && island.longitude) {
      const key = `${island.atoll}_${island.name}`;
      acc[key] = {
        lat: parseFloat(island.latitude),
        lng: parseFloat(island.longitude),
        atoll: island.atoll
      };
    }
    return acc;
  }, {} as Record<string, { lat: number; lng: number; atoll: string }>);

  const handleMapReady = () => {
    setMapReady(true);
    // Automatically get user location when map is ready
    if (!locationLoaded) {
      getUserLocationOnLoad();
    }
  };

  // Get user's current location automatically on load
  const getUserLocationOnLoad = () => {
    if (!navigator.geolocation) {
      setLocationLoaded(true);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        const newLocation: [number, number] = [latitude, longitude];
        setUserLocation(newLocation);

        // Add to location history for direction calculation
        const now = Date.now();
        setLocationHistory(prev => {
          const updated = [...prev, { lat: latitude, lng: longitude, timestamp: now }];
          // Keep only last 5 positions for bearing calculation
          return updated.slice(-5);
        });
        setLocationLoaded(true);
        setIsZoomLocked(true); // Lock zoom when user location is acquired
        if (mapRef.current) {
          mapRef.current.setView([latitude, longitude], MAX_ZOOM);
        }

        // Check for nearby islands and mark visited
        try {
          await checkLocationForIslandVisits(latitude, longitude);
        } catch (error) {
          // Silent fail for island checking on auto-load
        }
      },
      (error) => {
        // Silently fail on auto-load, user can manually trigger location later
        setLocationLoaded(true);
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 60000 }
    );
  };

  // Continuous location watching for direction mode
  useEffect(() => {
    if (!isDirectionMode || !navigator.geolocation) return;

    let watchId: number;

    const watchOptions = {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 5000
    };

    const handlePositionUpdate = (position: GeolocationPosition) => {
      const { latitude, longitude } = position.coords;
      const newLocation: [number, number] = [latitude, longitude];

      setUserLocation(newLocation);

      // Calculate bearing if we have previous location
      if (previousLocation) {
        const bearing = calculateBearing(
          previousLocation[0], previousLocation[1],
          latitude, longitude
        );

        // Only update if movement is significant (> 5 meters approx)
        const distance = calculateDistance(
          previousLocation[0], previousLocation[1],
          latitude, longitude
        );

        if (distance > 0.00005) { // ~5 meters in degrees
          setCurrentBearing(bearing);
          updateMapRotation(bearing);
        }
      }

      setPreviousLocation(newLocation);
    };

    watchId = navigator.geolocation.watchPosition(
      handlePositionUpdate,
      (error) => {
        console.error('Direction tracking error:', error);
      },
      watchOptions
    );

    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [isDirectionMode, previousLocation, calculateBearing, updateMapRotation]);

  // Calculate distance between two points (simple approximation)
  const calculateDistance = useCallback((lat1: number, lng1: number, lat2: number, lng2: number): number => {
    const dlat = lat2 - lat1;
    const dlng = lng2 - lng1;
    return Math.sqrt(dlat * dlat + dlng * dlng);
  }, []);

  // Toggle direction mode
  const toggleDirectionMode = useCallback(() => {
    setIsDirectionMode(prev => {
      const newMode = !prev;

      if (!newMode) {
        // Reset map rotation when disabling direction mode
        const map = mapRef.current;
        if (map) {
          const mapContainer = map.getContainer();
          if (mapContainer) {
            mapContainer.style.transform = 'rotate(0deg)';
          }
        }
        setPreviousLocation(null);
        setCurrentBearing(0);
      } else {
        // Start direction tracking
        if (userLocation) {
          setPreviousLocation(userLocation);
        }
      }

      toast({
        title: newMode ? "Direction Mode Enabled" : "Direction Mode Disabled",
        description: newMode ? "Map will now rotate based on your movement direction" : "Map rotation reset to north-up",
      });

      return newMode;
    });
  }, [userLocation, toast]);

  // Compass functionality
  const requestCompassPermission = async () => {
    if ('DeviceOrientationEvent' in window) {
      if (typeof (DeviceOrientationEvent as any).requestPermission === 'function') {
        const permission = await (DeviceOrientationEvent as any).requestPermission();
        return permission === 'granted';
      }
      return true; // Android devices don't require permission
    }
    return false;
  };

  const toggleCompass = async () => {
    if (!isCompassEnabled) {
      const hasPermission = await requestCompassPermission();
      if (!hasPermission) {
        toast({
          title: "Compass Permission Denied",
          description: "Please allow device orientation access to use the compass.",
          variant: "destructive"
        });
        return;
      }

      setIsCompassEnabled(true);
      toast({
        title: "Compass Enabled",
        description: "Device compass is now active"
      });

      // Start continuous location tracking
      startContinuousLocationTracking();
    } else {
      setIsCompassEnabled(false);
      stopContinuousLocationTracking();
      toast({
        title: "Compass Disabled",
        description: "Device compass is now inactive"
      });
    }
  };

  // Enhanced location tracking with battery optimization
  const lastPanTime = useRef<number>(0);
  const lastLocation = useRef<[number, number] | null>(null);

  const resetUserInteraction = () => {
    if (interactionTimeout.current) {
      clearTimeout(interactionTimeout.current);
    }
    interactionTimeout.current = window.setTimeout(() => {
      setUserInteracted(false);
    }, 5000); // Reset after 5 seconds of no interaction
  };

  const startContinuousLocationTracking = () => {
    if (!navigator.geolocation || watchId.current !== null) return;

    setIsWatchingLocation(true);

    // Use dynamic accuracy based on movement
    const getWatchOptions = (isMoving: boolean) => ({
      enableHighAccuracy: isMoving,
      timeout: isMoving ? 10000 : 20000,
      maximumAge: isMoving ? 3000 : 10000
    });

    const locationHandler = (position: GeolocationPosition) => {
      const { latitude, longitude, speed } = position.coords;
      const newLocation: [number, number] = [latitude, longitude];

      // Movement detection for battery optimization
      const currentlyMoving = Boolean((speed !== null && speed > 0.5) || // 0.5 m/s threshold
                                      (lastLocation.current && 
                                       calculateDistance(lastLocation.current[0], lastLocation.current[1], latitude, longitude) > 0.00001));

      // Track movement state changes for dynamic accuracy
      if (currentlyMoving !== isMoving.current) {
        isMoving.current = currentlyMoving;

        // Restart tracking with appropriate accuracy
        if (watchId.current !== null) {
          navigator.geolocation.clearWatch(watchId.current);
          watchId.current = navigator.geolocation.watchPosition(
            locationHandler,
            (error) => console.error('Location tracking error:', error),
            getWatchOptions(currentlyMoving)
          );
        }
      }

      // Only update if significant movement or first location
      if (!lastLocation.current || currentlyMoving) {
        setUserLocation(newLocation);
        lastLocation.current = newLocation;

        // Smart map following - only if compass enabled and user hasn't interacted
        const now = Date.now();
        if (mapRef.current && isCompassEnabled && !userInteracted && (now - lastPanTime.current > 1000)) {
          mapRef.current.panTo([latitude, longitude]);
          lastPanTime.current = now;
        }

        // Check for nearby islands when moving
        if (currentlyMoving) {
          checkLocationForIslandVisits(latitude, longitude).catch(() => {
            // Silent error handling
          });
        }
      }
    };

    watchId.current = navigator.geolocation.watchPosition(
      locationHandler,
      (error) => {
        console.error('Continuous location error:', error);
      },
      getWatchOptions(false) // Start with low accuracy
    );
  };

  const stopContinuousLocationTracking = () => {
    if (watchId.current !== null) {
      navigator.geolocation.clearWatch(watchId.current);
      watchId.current = null;
    }
    setIsWatchingLocation(false);
    lastLocation.current = null;
    lastPanTime.current = 0;
  };

  // Device orientation handling with cross-platform support
  useEffect(() => {
    if (!isCompassEnabled) return;

    const handleOrientation = (event: DeviceOrientationEvent) => {
      let heading: number | null = null;

      // iOS Safari uses webkitCompassHeading (0-360, 0 = North)
      if ('webkitCompassHeading' in event && (event as any).webkitCompassHeading !== null) {
        heading = (event as any).webkitCompassHeading;
      }
      // Android and other browsers use alpha with screen orientation compensation
      else if (event.alpha !== null) {
        // Get screen orientation
        const screenOrientation = screen.orientation?.angle || 0;
        // Adjust for screen orientation and convert to compass heading
        heading = (360 - event.alpha - screenOrientation) % 360;
      }

      if (heading !== null) {
        // Smooth the heading changes to reduce jitter
        setDeviceHeading(prev => {
          const diff = ((heading! - prev + 540) % 360) - 180;
          return Math.abs(diff) < 2 ? prev : prev + diff * 0.3;
        });
      }
    };

    // Request permission on iOS
    if (typeof (DeviceOrientationEvent as any).requestPermission === 'function') {
      (DeviceOrientationEvent as any).requestPermission().then((permission: string) => {
        if (permission === 'granted') {
          window.addEventListener('deviceorientation', handleOrientation);
        }
      });
    } else {
      window.addEventListener('deviceorientation', handleOrientation);
    }

    return () => {
      window.removeEventListener('deviceorientation', handleOrientation);
    };
  }, [isCompassEnabled]);

  // Map interaction detection to suspend auto-follow
  useEffect(() => {
    const map = mapRef.current;
    if (!map || !isCompassEnabled) return;

    const handleMapInteraction = () => {
      setUserInteracted(true);
      resetUserInteraction();
    };

    // Listen for map interactions
    map.on('dragstart', handleMapInteraction);
    map.on('zoomstart', handleMapInteraction);
    map.on('mousedown', handleMapInteraction);
    map.on('touchstart', handleMapInteraction);

    return () => {
      map.off('dragstart', handleMapInteraction);
      map.off('zoomstart', handleMapInteraction);
      map.off('mousedown', handleMapInteraction);
      map.off('touchstart', handleMapInteraction);
    };
  }, [isCompassEnabled]);

  // Page visibility optimization for battery saving
  useEffect(() => {
    if (!isWatchingLocation) return;

    const handleVisibilityChange = () => {
      if (document.hidden) {
        // Pause location tracking when tab is hidden
        if (watchId.current !== null) {
          navigator.geolocation.clearWatch(watchId.current);
          watchId.current = null;
        }
      } else {
        // Resume location tracking when tab becomes visible
        startContinuousLocationTracking();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [isWatchingLocation]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopContinuousLocationTracking();
      if (interactionTimeout.current) {
        clearTimeout(interactionTimeout.current);
      }
    };
  }, []);

  // Get user's current location (manual trigger)
  const getUserLocation = async () => {
    if (!navigator.geolocation) {
      toast({
        title: "Geolocation not supported",
        description: "Your browser doesn't support geolocation.",
        variant: "destructive"
      });
      return;
    }

    // Request compass permission synchronously in user gesture (iOS requirement)
    let compassEnabled = isCompassEnabled;
    if (!compassEnabled) {
      const hasCompassPermission = await requestCompassPermission();
      if (hasCompassPermission) {
        setIsCompassEnabled(true);
        compassEnabled = true;
      }
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        const newLocation: [number, number] = [latitude, longitude];
        setUserLocation(newLocation);
        setPreviousLocation(newLocation);
        setIsZoomLocked(true); // Lock zoom when user location is acquired

        // Start continuous tracking if compass was enabled
        if (compassEnabled && !isWatchingLocation) {
          startContinuousLocationTracking();
        }

        if (mapRef.current) {
          mapRef.current.setView([latitude, longitude], MAX_ZOOM);
        }
        toast({
          title: "Location found",
          description: `Located at ${latitude.toFixed(6)}, ${longitude.toFixed(6)} with ${position.coords.accuracy}m accuracy`
        });

        // Check for nearby islands and mark visited
        try {
          await checkLocationForIslandVisits(latitude, longitude);
        } catch (error) {
          // Don't show error toast for island checking as it has its own error handling
        }
      },
      (error) => {
        let message = "Unable to retrieve your location.";
        if (error.code === error.PERMISSION_DENIED) {
          message = "Location access denied. Please enable location permissions.";
        }
        toast({
          title: "Location error",
          description: message,
          variant: "destructive"
        });
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  // Navigation functions for events
  const navigateToNextEvent = () => {
    if (events.length === 0) return;

    const currentIndex = selectedEventId 
      ? events.findIndex(event => event.id === selectedEventId)
      : -1;

    const nextIndex = currentIndex < events.length - 1 ? currentIndex + 1 : 0;
    const nextEvent = events[nextIndex];

    onEventSelect?.(nextEvent.id);
    toast({
      title: "Next Event",
      description: `Navigating to: ${nextEvent.title}`,
    });
  };

  const navigateToPreviousEvent = () => {
    if (events.length === 0) return;

    const currentIndex = selectedEventId 
      ? events.findIndex(event => event.id === selectedEventId)
      : -1;

    const prevIndex = currentIndex > 0 ? currentIndex - 1 : events.length - 1;
    const prevEvent = events[prevIndex];

    onEventSelect?.(prevEvent.id);
    toast({
      title: "Previous Event",
      description: `Navigating to: ${prevEvent.title}`,
    });
  };

  const showAllEvents = () => {
    onEventSelect?.(null);
    toast({
      title: "Show All Events",
      description: "Showing all events on the map",
    });
  };

  // Get current event details for display
  const getCurrentEvent = () => {
    if (!selectedEventId) return null;
    return events.find(event => event.id === selectedEventId);
  };

  // Update filtered islands when search query changes
  useEffect(() => {
    if (searchQuery.trim()) {
      const filtered = islands.filter(island => 
        island.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        island.atoll.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredIslands(filtered);
      setShowSearchResults(filtered.length > 0);
    } else {
      setFilteredIslands([]);
      setShowSearchResults(false);
    }
  }, [searchQuery]);

  const selectIsland = (island: Island) => {
    if (island.latitude && island.longitude && mapRef.current) {
      const lat = parseFloat(island.latitude);
      const lng = parseFloat(island.longitude);
      if (!isNaN(lat) && !isNaN(lng)) {
        if (isZoomLocked) {
          // Only change position, keep current zoom when locked
          mapRef.current.panTo([lat, lng]);
        } else {
          // Normal behavior when not zoom locked
          mapRef.current.setView([lat, lng], 12);
        }
        setSearchQuery(island.name);
        setShowSearchResults(false);
      }
    }
  };

  const clearSearch = () => {
    setSearchQuery('');
    setShowSearchResults(false);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-500';
      case 'active': return 'bg-blue-500';
      case 'planned': return 'bg-yellow-500';
      case 'cancelled': return 'bg-red-500';
      default: return 'bg-gray-500';
    }
  };

  const formatEventDate = (dateStr: string | Date | null) => {
    try {
      if (!dateStr) return 'No date';
      return format(new Date(dateStr), 'MMM dd, yyyy');
    } catch {
      return String(dateStr);
    }
  };

  // Directions functions
  const getDirections = async (startLat: number, startLng: number, endLat: number, endLng: number) => {
    setIsLoadingDirections(true);
    try {
      // Use the improved routing service
      const route = await routingService.getRoute([startLat, startLng], [endLat, endLng], { profile: 'walking' });

      if (route) {
        // Extract turn-by-turn instructions
        const instructions: string[] = route.steps.map(step => step.instruction);

        // Format distance and duration
        const distance = (route.distance / 1000).toFixed(1) + ' km';
        const duration = Math.round(route.duration / 60) + ' min';
        setRouteCoordinates(route.coordinates);
        setRouteInstructions(instructions);
        setRouteDistance(distance);
        setRouteDuration(duration);
        setDirectionsMode(true);

        toast({
          title: "Directions Found",
          description: `Route: ${distance}, ${duration}`,
        });
      } else {
        throw new Error('No route found from routing service');
      }
    } catch (error) {
      console.error('Error getting directions:', error);
      toast({
        title: "Directions Error", 
        description: "Could not get directions. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoadingDirections(false);
    }
  };

  const getDirectionsToEvent = () => {
    if (!userLocation) {
      toast({
        title: "Location Required",
        description: "Please enable location access to get directions.",
        variant: "destructive",
      });
      return;
    }

    if (!selectedEventId) {
      toast({
        title: "Event Required",
        description: "Please select an event to get directions.",
        variant: "destructive",
      });
      return;
    }

    const currentEvent = getCurrentEvent();
    if (!currentEvent) return;

    let eventLat, eventLng;
    if (currentEvent.latitude && currentEvent.longitude) {
      eventLat = parseFloat(currentEvent.latitude);
      eventLng = parseFloat(currentEvent.longitude);
    } else {
      const coords = islandCoords[currentEvent.island];
      if (coords) {
        eventLat = coords.lat;
        eventLng = coords.lng;
      }
    }

    if (eventLat && eventLng && !isNaN(eventLat) && !isNaN(eventLng)) {
      setDirectionsMode(true);
      getDirections(userLocation[0], userLocation[1], eventLat, eventLng);
    } else {
      toast({
        title: "Event Location Error",
        description: "Could not find coordinates for this event.",
        variant: "destructive",
      });
    }
  };

  const clearDirections = () => {
    setDirectionsMode(false);
    setRouteCoordinates([]);
    setRouteInstructions([]);
    setRouteDistance('');
    setRouteDuration('');
  };


  // Auto-trigger directions when autoDirections prop is true and conditions are met
  useEffect(() => {
    if (autoDirections && selectedEventId && events.length > 0 && !directionsMode) {
      // Get user location first, then trigger directions
      if (!navigator.geolocation) {
        toast({
          title: "Geolocation not supported",
          description: "Your browser doesn't support geolocation.",
          variant: "destructive"
        });
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation([latitude, longitude]);
          setIsZoomLocked(true); // Lock zoom when user location is acquired

          // Set map view to max zoom when location is acquired
          if (mapRef.current) {
            mapRef.current.setView([latitude, longitude], MAX_ZOOM);
          }

          // Delay to ensure events and location are ready
          setTimeout(() => {
            const currentEvent = events.find(event => event.id === selectedEventId);
            if (!currentEvent) return;

            let eventLat, eventLng;
            if (currentEvent.latitude && currentEvent.longitude) {
              eventLat = parseFloat(currentEvent.latitude);
              eventLng = parseFloat(currentEvent.longitude);
            } else {
              const coords = islandCoords[currentEvent.island];
              if (coords) {
                eventLat = coords.lat;
                eventLng = coords.lng;
              }
            }

            if (eventLat && eventLng && !isNaN(eventLat) && !isNaN(eventLng)) {
              setDirectionsMode(true);
              getDirections(latitude, longitude, eventLat, eventLng);
            }
          }, 1000);
        },
        (error) => {
          let message = "Unable to retrieve your location for directions.";
          if (error.code === error.PERMISSION_DENIED) {
            message = "Location access denied. Please enable location permissions for directions.";
          }
          toast({
            title: "Location error",
            description: message,
            variant: "destructive",
          });
        },
        { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
      );
    }
  }, [autoDirections, selectedEventId, events, directionsMode, islandCoords]);

  if (eventsLoading || islandsLoading) {
    return (
      <div className={`${className} flex items-center justify-center bg-gray-50`}>
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-2"></div>
          <p className="text-gray-600">Loading map data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`${className} relative`}>
      {/* Compact mobile navigation */}
      <div className="absolute top-2 left-2 right-2 z-[1000] space-y-2">
        {/* Combined event navigation and directions - single compact row */}
        {events.length > 0 && (
          <div className="flex items-center gap-1 bg-white/95 backdrop-blur-sm border border-white/20 shadow-lg rounded-lg px-2 py-1">
            {/* Event navigation */}
            <Button
              variant="ghost"
              size="sm"
              onClick={navigateToPreviousEvent}
              disabled={events.length === 0}
              className="h-7 w-7 p-0 hover:bg-gray-100/80"
              data-testid="button-previous-event"
            >
              <ChevronLeft className="w-3 h-3" />
            </Button>

            <div className="flex-1 text-center px-1">
              {selectedEventId && getCurrentEvent() ? (
                <span className="text-xs font-medium text-blue-600 truncate block">
                  {getCurrentEvent()?.title}
                </span>
              ) : (
                <span className="text-xs text-gray-500">All Events</span>
              )}
            </div>

            <Button
              variant="ghost"
              size="sm"
              onClick={navigateToNextEvent}
              disabled={events.length === 0}
              className="h-7 w-7 p-0 hover:bg-gray-100/80"
              data-testid="button-next-event"
            >
              <ChevronRight className="w-3 h-3" />
            </Button>

            <Button
              variant="ghost"
              size="sm"
              onClick={showAllEvents}
              className="text-xs px-2 py-1 h-7 hover:bg-gray-100/80"
              data-testid="button-show-all-events"
            >
              All
            </Button>

            {/* Directions controls in same row */}
            {selectedEventId && (
              <>
                <div className="w-px h-4 bg-gray-300 mx-1"></div>
                {!directionsMode ? (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={getDirectionsToEvent}
                    disabled={!userLocation || isLoadingDirections}
                    className="text-xs px-2 py-1 h-7 hover:bg-gray-100/80 flex items-center gap-1"
                    data-testid="button-get-directions"
                  >
                    <Navigation className="w-3 h-3" />
                    {isLoadingDirections ? "..." : "Route"}
                  </Button>
                ) : (
                  <div className="flex items-center gap-1">
                    <span className="text-xs text-green-600 font-medium">
                      {routeDistance}
                    </span>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={clearDirections}
                      className="text-xs px-1 py-1 h-7 hover:bg-gray-100/80"
                      data-testid="button-clear-directions"
                    >
                      <X className="w-3 h-3" />
                    </Button>
                  </div>
                )}
              </>
            )}
          </div>
        )}
      </div>

      {/* Search bar and location controls - more compact */}
      <div className="absolute top-16 left-2 right-2 z-[1000] flex gap-2">
          <div className="relative flex-1">
            <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400 w-3 h-3" />
            <Input
              type="text"
              placeholder="Search islands..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-7 pr-8 py-1.5 text-sm bg-white/95 backdrop-blur-sm border-white/20 shadow-lg rounded-lg focus:bg-white transition-all h-8"
              data-testid="input-island-search"
            />
            {searchQuery && (
              <Button
                variant="ghost"
                size="sm"
                onClick={clearSearch}
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-5 w-5 p-0 hover:bg-gray-100/80"
              >
                <X className="w-2.5 h-2.5" />
              </Button>
            )}

            {/* Search results dropdown */}
            {showSearchResults && filteredIslands.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white/95 backdrop-blur-sm border border-white/20 rounded-lg shadow-lg max-h-32 overflow-y-auto z-50">
                {filteredIslands.map((island) => (
                  <button
                    key={island.id}
                    onClick={() => selectIsland(island)}
                    className="w-full text-left px-3 py-2 hover:bg-blue-50/80 border-b border-gray-100 last:border-b-0 transition-colors"
                    data-testid={`search-result-${island.id}`}
                  >
                    <div className="text-sm font-medium">{island.name}</div>
                    <div className="text-xs text-gray-600">{island.atoll}</div>
                  </button>
                ))}
              </div>
            )}
          </div>

          <Button
            onClick={getUserLocation}
            className="bg-blue-600 hover:bg-blue-700 text-white shadow-lg rounded-lg px-2 py-1.5 h-8 text-xs"
            data-testid="button-my-location"
          >
            <Navigation className="w-3 h-3 mr-1" />
            Location
          </Button>

          <Button
            onClick={toggleDirectionMode}
            className={`${
              isDirectionMode 
                ? 'bg-orange-600 hover:bg-orange-700' 
                : 'bg-gray-600 hover:bg-gray-700'
            } text-white shadow-lg rounded-lg px-2 py-1.5 h-8 text-xs transition-colors`}
            data-testid="button-direction-mode"
            title={isDirectionMode ? "Disable direction mode" : "Enable direction mode - map will rotate based on your movement"}
          >
            <Navigation className={`w-3 h-3 mr-1 transition-transform ${isDirectionMode ? 'rotate-45' : ''}`} />
            {isDirectionMode ? 'Direction' : 'North Up'}
          </Button>

        </div>

        {/* Custom zoom controls positioned below search */}
        <div className="absolute top-28 left-2 z-[1000] flex flex-col gap-1">
          <Button
            variant="outline"
            size="sm"
            onClick={() => mapRef.current?.zoomIn()}
            className="w-8 h-8 p-0 bg-white/95 backdrop-blur-sm border-white/20 shadow-lg hover:bg-white"
            data-testid="button-zoom-in"
          >
            +
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => mapRef.current?.zoomOut()}
            className="w-8 h-8 p-0 bg-white/95 backdrop-blur-sm border-white/20 shadow-lg hover:bg-white"
            data-testid="button-zoom-out"
          >
            âˆ’
          </Button>
        </div>


      <div className={`${className} w-full`}>
        <MapContainer
          center={MALDIVES_CENTER}
          zoom={DEFAULT_ZOOM}
          style={{ height: '100%', width: '100%' }}
          bounds={MALDIVES_BOUNDS}
          boundsOptions={{ padding: [20, 20] }}
          maxBounds={MALDIVES_BOUNDS}
          minZoom={6}
          maxZoom={18}
          zoomControl={false}
            ref={mapRef}
            whenReady={handleMapReady}
            data-testid="map-container"
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            <MapController 
              eventId={selectedEventId} 
              events={events} 
              islandCoords={islandCoords}
              userLocation={userLocation}
              isZoomLocked={isZoomLocked}
            />

            {/* Render user location marker */}
            {userLocation && (
              <Marker
                position={userLocation}
                icon={createLocationIcon(deviceHeading, isCompassEnabled)}
                data-testid="marker-user-location"
              >
                <Popup>
                  <div className="p-2">
                    <h4 className="font-semibold">Your Location</h4>
                    <p className="text-sm text-gray-600">
                      Lat: {userLocation[0].toFixed(6)}<br />
                      Lng: {userLocation[1].toFixed(6)}
                    </p>
                  </div>
                </Popup>
              </Marker>
            )}

            {/* Render event markers */}
            {events.map((event) => {
              // Use event's actual coordinates if available, fallback to island coordinates
              let eventLat, eventLng, eventAtoll;

              if (event.latitude && event.longitude) {
                eventLat = parseFloat(event.latitude);
                eventLng = parseFloat(event.longitude);
                eventAtoll = event.atoll; // Use event's stored atoll directly
              } else {
                const coords = islandCoords[`${event.atoll}_${event.island}`];
                if (!coords) return null;
                eventLat = coords.lat;
                eventLng = coords.lng;
                eventAtoll = coords.atoll;
              }

              if (isNaN(eventLat) || isNaN(eventLng)) return null;

              return (
                <Marker
                  key={`event-${event.id}`}
                  position={[eventLat, eventLng]}
                  icon={eventIcon}
                  eventHandlers={{
                    click: () => onEventSelect?.(event.id)
                  }}
                  data-testid={`marker-event-${event.id}`}
                >
                  <Popup>
                    <div className="p-3 min-w-[250px]">
                      <div className="flex items-start justify-between mb-2">
                        <h4 className="font-semibold text-lg">{event.title}</h4>
                        <Badge 
                          variant="outline" 
                          className={`ml-2 ${getStatusColor(event.status)} text-white`}
                        >
                          {event.status}
                        </Badge>
                      </div>

                      <div className="space-y-2 text-sm">
                        <div className="flex items-center text-gray-600">
                          <MapPin className="w-4 h-4 mr-1" />
                          {event.island}, {eventAtoll}
                        </div>

                        <div className="flex items-center text-gray-600">
                          <Calendar className="w-4 h-4 mr-1" />
                          {formatEventDate(event.createdAt)}
                        </div>

                        <div className="flex items-center text-gray-600">
                          <Users className="w-4 h-4 mr-1" />
                          {event.participantCount} participants
                        </div>

                        {(event.latitude && event.longitude) && (
                          <div className="flex items-center text-gray-600">
                            <span className="text-xs">
                              Lat: {parseFloat(event.latitude).toFixed(4)}, Lng: {parseFloat(event.longitude).toFixed(4)}
                            </span>
                          </div>
                        )}
                      </div>

                      {event.comment && (
                        <p className="mt-2 text-sm text-gray-700 border-t pt-2">
                          {event.comment}
                        </p>
                      )}
                    </div>
                  </Popup>
                </Marker>
              );
            })}

            {/* Enhanced route visualization with gradients and arrows */}
            {directionsMode && routeCoordinates.length > 0 && (
              <>
                {/* Route shadow for depth - render first so gradient sits on top */}
                <Polyline
                  positions={routeCoordinates}
                  pathOptions={{
                    color: "#1E40AF",
                    weight: 8,
                    opacity: 0.3,
                    className: "route-line-shadow"
                  }}
                />

                {/* Render gradient route segments */}
                {routeCoordinates.slice(0, -1).map((coord, index) => {
                  const nextCoord = routeCoordinates[index + 1];
                  const progress = index / (routeCoordinates.length - 1);

                  // Interpolate color from blue to green based on progress
                  const startR = 59, startG = 130, startB = 246;  // #3B82F6 (blue)
                  const endR = 16, endG = 185, endB = 129;        // #10B981 (green)

                  const r = Math.round(startR + (endR - startR) * progress);
                  const g = Math.round(startG + (endG - startG) * progress);
                  const b = Math.round(startB + (endB - startB) * progress);
                  const segmentColor = `rgb(${r}, ${g}, ${b})`;

                  return (
                    <Polyline
                      key={`route-segment-${index}`}
                      positions={[coord, nextCoord]}
                      pathOptions={{
                        color: segmentColor,
                        weight: 6,
                        opacity: 0.9,
                        className: "route-line-gradient-segment"
                      }}
                    />
                  );
                })}

                {/* Direction arrows along the route */}
                {routeCoordinates.slice(0, -1).map((coord, index) => {
                  // Show arrows every 5th coordinate to avoid clutter
                  if (index % 5 !== 0) return null;

                  const nextCoord = routeCoordinates[index + 1];
                  if (!nextCoord) return null;

                  // Calculate bearing for arrow rotation
                  const bearing = calculateBearing(coord[0], coord[1], nextCoord[0], nextCoord[1]);

                  return (
                    <Marker
                      key={`route-arrow-${index}`}
                      position={coord}
                      icon={new L.DivIcon({
                        html: `<div class="route-direction-arrow" style="--rotation: ${bearing}deg;">
                          <span class="arrow-glyph">â–²</span>
                        </div>`,
                        className: 'route-arrow-icon',
                        iconSize: [16, 16],
                        iconAnchor: [8, 8]
                      })}
                    />
                  );
                })}

                {/* Route markers for turn points */}
                {routeInstructions.map((instruction, index) => {
                  if (index === 0 || index === routeInstructions.length - 1) return null;
                  const segmentIndex = Math.floor((index / routeInstructions.length) * routeCoordinates.length);
                  const position = routeCoordinates[segmentIndex];
                  if (!position) return null;

                  return (
                    <Marker
                      key={`route-step-${index}`}
                      position={position}
                      icon={new L.DivIcon({
                        html: `<div class="route-step-marker">
                          <div class="route-step-number">${index + 1}</div>
                        </div>`,
                        className: 'route-step-icon',
                        iconSize: [24, 24],
                        iconAnchor: [12, 12]
                      })}
                    >
                      <Popup>
                        <div className="p-2">
                          <div className="font-semibold text-sm">Step {index + 1}</div>
                          <div className="text-xs text-gray-600 mt-1">{instruction}</div>
                        </div>
                      </Popup>
                    </Marker>
                  );
                })}
              </>
            )}
        </MapContainer>
      </div>

      {/* Modern directions panel with slide-up animation */}
      {directionsMode && routeInstructions.length > 0 && (
        <ModernDirectionsPanel
          routeInstructions={routeInstructions}
          routeDistance={routeDistance}
          routeDuration={routeDuration}
          isLoadingDirections={isLoadingDirections}
          onClose={clearDirections}
        />
      )}
    </div>
  );
}

