import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { History, ChevronDown, ChevronUp, ArrowRight, User, Calendar, FileText } from "lucide-react";
import { type EquipmentTransferWithUser } from "../../../shared/schema";

interface EquipmentTransferHistoryProps {
  equipmentId: string;
  equipmentName: string;
}

export default function EquipmentTransferHistory({ equipmentId, equipmentName }: EquipmentTransferHistoryProps) {
  const [isExpanded, setIsExpanded] = useState(true); // Expanded by default

  const { data: transfers = [], isLoading, error } = useQuery<EquipmentTransferWithUser[]>({
    queryKey: ['/api/equipment/transfers/with-users', equipmentId],
    queryFn: () => fetch(`/api/equipment/transfers/with-users?equipmentId=${equipmentId}`).then(res => res.json()),
    // Always fetch data - removed enabled condition
    staleTime: 30 * 1000, // 30 seconds
  });

  const formatUserName = (user: EquipmentTransferWithUser['transferredByUser']) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    if (user.serviceNumber) {
      return `Service #${user.serviceNumber}`;
    }
    return user.email || "Unknown User";
  };

  const formatDate = (date: string | Date | null) => {
    if (!date) return "Unknown Date";
    const dateObj = date instanceof Date ? date : new Date(date);
    return dateObj.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="mt-4 border-l-4 border-blue-500 pl-3 bg-blue-50/30 p-3 rounded-r-md">
      <Button
        variant="ghost"
        size="sm"
        onClick={() => setIsExpanded(!isExpanded)}
        className="w-full justify-between hover:bg-blue-100/50 font-semibold text-blue-700"
        data-testid={`button-toggle-transfer-history-${equipmentId}`}
      >
        <div className="flex items-center gap-2">
          <History className="h-5 w-5 text-blue-600" />
          <span>Transfer History</span>
          {!isLoading && transfers.length > 0 && (
            <Badge variant="secondary" className="ml-2 bg-blue-100 text-blue-800">
              {transfers.length} movements
            </Badge>
          )}
          {!isLoading && transfers.length === 0 && (
            <Badge variant="outline" className="ml-2 text-gray-500">
              No history
            </Badge>
          )}
        </div>
        {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
      </Button>

      {isExpanded && (
        <div className="mt-3 space-y-3" data-testid={`transfer-history-${equipmentId}`}>
          {isLoading && (
            <div className="text-center py-4 text-sm text-muted-foreground">
              Loading transfer history...
            </div>
          )}

          {error && (
            <div className="text-center py-4 text-sm text-destructive">
              Failed to load transfer history
            </div>
          )}

          {!isLoading && !error && transfers.length === 0 && (
            <Card className="bg-muted/30">
              <CardContent className="p-4 text-center">
                <History className="h-8 w-8 mx-auto text-muted-foreground/50 mb-2" />
                <p className="text-sm text-muted-foreground">
                  No transfer history available for {equipmentName}
                </p>
              </CardContent>
            </Card>
          )}

          {transfers.length > 0 && (
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {transfers.map((transfer, index) => (
                <Card key={transfer.id} className="transition-all hover:shadow-sm">
                  <CardContent className="p-4">
                    <div className="space-y-3">
                      {/* Transfer header with user info */}
                      <div className="flex items-start justify-between">
                        <div className="flex items-center gap-2">
                          <User className="h-4 w-4 text-blue-600" />
                          <div>
                            <div className="font-medium text-sm" data-testid={`transfer-user-${transfer.id}`}>
                              {formatUserName(transfer.transferredByUser)}
                            </div>
                            {transfer.transferredByUser.serviceNumber && (
                              <div className="text-xs text-muted-foreground">
                                Service #{transfer.transferredByUser.serviceNumber}
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="flex items-center gap-1 text-xs text-muted-foreground">
                          <Calendar className="h-3 w-3" />
                          <span data-testid={`transfer-date-${transfer.id}`}>
                            {formatDate(transfer.transferredAt)}
                          </span>
                        </div>
                      </div>

                      <Separator />

                      {/* Transfer details */}
                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-sm">
                          <div className="flex items-center gap-2">
                            <span className="font-medium">Quantity Moved:</span>
                            <Badge variant="outline" data-testid={`transfer-quantity-${transfer.id}`}>
                              {transfer.quantityMoved} units
                            </Badge>
                          </div>
                        </div>

                        {/* From/To locations */}
                        <div className="flex items-center gap-3 text-sm">
                          <div className="flex-1">
                            <div className="font-medium text-muted-foreground mb-1">From:</div>
                            <div data-testid={`transfer-from-${transfer.id}`}>
                              {transfer.fromAtoll}
                              {transfer.fromIsland && ` - ${transfer.fromIsland}`}
                            </div>
                          </div>

                          <ArrowRight className="h-4 w-4 text-muted-foreground flex-shrink-0" />

                          <div className="flex-1">
                            <div className="font-medium text-muted-foreground mb-1">To:</div>
                            <div data-testid={`transfer-to-${transfer.id}`}>
                              {transfer.toAtoll}
                              {transfer.toIsland && ` - ${transfer.toIsland}`}
                            </div>
                          </div>
                        </div>

                        {/* Notes if available */}
                        {transfer.notes && (
                          <div className="mt-3 pt-2 border-t border-muted">
                            <div className="flex items-start gap-2 text-sm">
                              <FileText className="h-3 w-3 text-muted-foreground mt-0.5 flex-shrink-0" />
                              <div>
                                <div className="font-medium text-muted-foreground mb-1">Notes:</div>
                                <div className="text-muted-foreground" data-testid={`transfer-notes-${transfer.id}`}>
                                  {transfer.notes}
                                </div>
                              </div>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

shared/geolocation-utils.ts.content:
/**
 * Utility functions for geolocation calculations
 */

// Configuration constants
export const ISLAND_VISITING_DISTANCE_METERS = 500; // Default visiting distance threshold

/**
 * Calculate the distance between two geographic coordinates using the Haversine formula
 * @param lat1 Latitude of first point
 * @param lon1 Longitude of first point  
 * @param lat2 Latitude of second point
 * @param lon2 Longitude of second point
 * @returns Distance in meters
 */
export function calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
  const R = 6371000; // Earth's radius in meters
  const dLat = toRadians(lat2 - lat1);
  const dLon = toRadians(lon2 - lon1);

  const a = 
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);

  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c; // Distance in meters
}

/**
 * Convert degrees to radians
 */
function toRadians(degrees: number): number {
  return degrees * (Math.PI / 180);
}

/**
 * Check if a user location is within visiting distance of an island
 */
export function isWithinVisitingDistance(
  userLat: number, 
  userLon: number, 
  islandLat: number, 
  islandLon: number,
  visitingDistanceMeters: number = ISLAND_VISITING_DISTANCE_METERS
): boolean {
  const distance = calculateDistance(userLat, userLon, islandLat, islandLon);
  return distance <= visitingDistanceMeters;
}

/**
 * Find all islands within visiting distance of user location
 */
export function findNearbyIslands(
  userLat: number,
  userLon: number, 
  islands: Array<{id: string, latitude: string | null, longitude: string | null, name: string}>,
  visitingDistanceMeters: number = ISLAND_VISITING_DISTANCE_METERS
): Array<{id: string, name: string, distance: number}> {
  const nearbyIslands: Array<{id: string, name: string, distance: number}> = [];

  for (const island of islands) {
    if (!island.latitude || !island.longitude) continue;

    const islandLat = parseFloat(island.latitude);
    const islandLon = parseFloat(island.longitude);

    if (isNaN(islandLat) || isNaN(islandLon)) continue;

    const distance = calculateDistance(userLat, userLon, islandLat, islandLon);

    if (distance <= visitingDistanceMeters) {
      nearbyIslands.push({
        id: island.id,
        name: island.name,
        distance: Math.round(distance)
      });
    }
  }

  return nearbyIslands.sort((a, b) => a.distance - b.distance);
}

