import { useState, useEffect, useCallback } from 'react';
import { useToast } from './use-toast';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useAuth } from './useAuth';

interface LocationResult {
  nearbyIslands: Array<{id: string, name: string, distance: number}>;
  newlyVisited: Array<{id: string, name: string}>;
  message: string;
}

interface UseLocationTrackingOptions {
  enabled?: boolean;
  checkInterval?: number; // milliseconds
  trackingRadius?: number; // meters (not used in this hook, handled by backend)
  showToasts?: boolean;
}

export function useLocationTracking(options: UseLocationTrackingOptions = {}) {
  const { 
    enabled = true, 
    checkInterval = 30000, // 30 seconds
    showToasts = true 
  } = options;

  const { toast } = useToast();
  const { isAuthenticated } = useAuth();

  const [isTracking, setIsTracking] = useState(false);
  const [lastLocation, setLastLocation] = useState<{lat: number, lng: number} | null>(null);
  const [lastCheckTime, setLastCheckTime] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [nearbyIslands, setNearbyIslands] = useState<Array<{id: string, name: string, distance: number}>>([]);

  const checkLocationForIslandVisits = useCallback(async (latitude: number, longitude: number) => {
    try {
      setError(null);
      const response = await apiRequest('POST', '/api/islands/check-location', {
        latitude, 
        longitude
      });

      const result: LocationResult = await response.json();

      setNearbyIslands(result.nearbyIslands);

      // Show toast for newly visited islands
      if (showToasts && result.newlyVisited.length > 0) {
        toast({
          title: "ðŸï¸ Island Visited!",
          description: result.message,
          duration: 5000,
        });

        // Invalidate islands query to refresh the visited count in dashboard
        queryClient.invalidateQueries({ queryKey: ['/api/islands'] });
      }

      return result;
    } catch (error: any) {
      const errorMessage = error.message || 'Failed to check location for island visits';
      setError(errorMessage);

      if (showToasts) {
        toast({
          title: "Location Check Failed",
          description: errorMessage,
          variant: "destructive",
        });
      }

      throw error;
    }
  }, [toast, showToasts]);

  const getCurrentLocationAndCheck = useCallback(async () => {
    if (!navigator.geolocation || !isAuthenticated) {
      return;
    }

    return new Promise<LocationResult | null>((resolve) => {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          const newLocation = { lat: latitude, lng: longitude };

          try {
            const result = await checkLocationForIslandVisits(latitude, longitude);
            setLastLocation(newLocation);
            setLastCheckTime(Date.now());
            resolve(result);
          } catch (error) {
            resolve(null);
          }
        },
        (error) => {
          let message = "Unable to get your location";
          if (error.code === error.PERMISSION_DENIED) {
            message = "Location permission denied. Please enable location access to track island visits.";
          }
          setError(message);

          if (showToasts && error.code === error.PERMISSION_DENIED) {
            toast({
              title: "Location Permission Needed",
              description: message,
              variant: "destructive",
            });
          }
          resolve(null);
        },
        { 
          enableHighAccuracy: true, 
          timeout: 15000, 
          maximumAge: 30000 // Use cached location if it's less than 30 seconds old
        }
      );
    });
  }, [isAuthenticated, checkLocationForIslandVisits, showToasts, toast]);

  const startTracking = useCallback(() => {
    if (!enabled || !isAuthenticated) return;

    setIsTracking(true);
    setError(null);

    // Initial check
    getCurrentLocationAndCheck();

    // Set up periodic checking
    const interval = setInterval(() => {
      getCurrentLocationAndCheck();
    }, checkInterval);

    return () => {
      clearInterval(interval);
      setIsTracking(false);
    };
  }, [enabled, isAuthenticated, getCurrentLocationAndCheck, checkInterval]);

  const stopTracking = useCallback(() => {
    setIsTracking(false);
  }, []);

  // Manual check function for immediate location checking
  const checkNow = useCallback(() => {
    return getCurrentLocationAndCheck();
  }, [getCurrentLocationAndCheck]);

  // Auto-start tracking when component mounts and conditions are met
  useEffect(() => {
    if (!enabled || !isAuthenticated) return;

    const cleanup = startTracking();
    return cleanup;
  }, [enabled, isAuthenticated, startTracking]);

  return {
    isTracking,
    lastLocation,
    lastCheckTime,
    error,
    nearbyIslands,
    startTracking,
    stopTracking,
    checkNow,
    checkLocationForIslandVisits
  };
}

client/src/components/island-check-in.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { useToast } from "@/hooks/use-toast";
import { usePermissions } from "@/hooks/usePermissions";
import { MapPin, CheckCircle, ChevronDown, ChevronUp, Users, Package, Wrench, Search, History, Calendar, Clock } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { format } from "date-fns";
import type { IslandWithUserVisitStatus, Equipment, IslandVisit } from "@shared/schema";

interface IslandCheckInProps {
  className?: string;
}

export default function IslandCheckIn({ className }: IslandCheckInProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showEquipmentDialog, setShowEquipmentDialog] = useState(false);
  const [selectedIslandForCheckIn, setSelectedIslandForCheckIn] = useState<IslandWithUserVisitStatus | null>(null);
  const [selectedEquipment, setSelectedEquipment] = useState<Set<string>>(new Set());
  const [selectedAtoll, setSelectedAtoll] = useState<string>("");
  const [selectedIslandId, setSelectedIslandId] = useState<string>("");
  const [showVisitHistory, setShowVisitHistory] = useState(false);
  const { toast } = useToast();
  const { canCheckInWithEquipment } = usePermissions();
  const queryClient = useQueryClient();

  // Fetch islands data
  const { data: islands = [], isLoading } = useQuery<IslandWithUserVisitStatus[]>({
    queryKey: ["/api/islands"],
  });

  // Fetch equipment data - only available equipment with quantity > 0
  const { data: equipment = [] } = useQuery<Equipment[]>({
    queryKey: ["/api/equipment"],
    select: (data) => data.filter((eq: any) => eq.status === "available" && eq.quantity > 0),
  });

  // Fetch visit history for selected island
  const { data: visitHistory = [], isLoading: isLoadingHistory } = useQuery<Array<IslandVisit & { equipmentUsed: string[] }>>({
    queryKey: ["/api/island-visits", selectedIslandId],
    queryFn: async () => {
      if (!selectedIslandId) return [];
      const response = await fetch(`/api/island-visits?islandId=${selectedIslandId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch visit history');
      }
      return response.json();
    },
    enabled: !!selectedIslandId,
  });

  // Check-in mutation
  const checkInMutation = useMutation({
    mutationFn: async ({ islandId, equipmentIds }: { islandId: string; equipmentIds: string[] }) => {
      const response = await apiRequest('POST', `/api/islands/${islandId}/check-in`, { equipmentIds });
      return response.json();
    },
    onSuccess: (result: any) => {
      // Invalidate both islands and equipment caches to refresh the UI
      queryClient.invalidateQueries({ queryKey: ['/api/islands'] });
      queryClient.invalidateQueries({ queryKey: ['/api/equipment'] });

      // Invalidate milestone caches so badges update immediately
      queryClient.invalidateQueries({ queryKey: ['/api/milestones'] });
      queryClient.invalidateQueries({ queryKey: ['/api/milestones/stats'] });
      queryClient.invalidateQueries({ queryKey: ['/api/milestones/history'] });

      // Invalidate visit history for this island
      queryClient.invalidateQueries({ queryKey: ['/api/island-visits', selectedIslandForCheckIn?.id] });

      toast({
        title: result.alreadyVisited ? "Already Visited" : "Check-in Successful! ðŸï¸",
        description: result.message,
        variant: result.alreadyVisited ? "default" : "default",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Check-in Failed",
        description: error?.message || "Failed to check in to island",
        variant: "destructive",
      });
    },
  });

  const handleCheckIn = (island: IslandWithUserVisitStatus) => {
    setSelectedIslandForCheckIn(island);
    setSelectedEquipment(new Set());

    // If user can't check in with equipment (attachment role), proceed directly without equipment selection
    if (!canCheckInWithEquipment) {
      checkInMutation.mutate({
        islandId: island.id,
        equipmentIds: [] // Empty array for attachment users
      });
      setSelectedIslandForCheckIn(null);
    } else {
      // Show equipment selection dialog for users who can select equipment
      setShowEquipmentDialog(true);
    }
  };

  const handleConfirmCheckIn = () => {
    if (!selectedIslandForCheckIn) return;

    const equipmentIds = Array.from(selectedEquipment);
    checkInMutation.mutate(
      { 
        islandId: selectedIslandForCheckIn.id, 
        equipmentIds 
      }
    );

    setShowEquipmentDialog(false);
    setSelectedIslandForCheckIn(null);
    setSelectedEquipment(new Set());
  };

  const handleCancelCheckIn = () => {
    setShowEquipmentDialog(false);
    setSelectedIslandForCheckIn(null);
    setSelectedEquipment(new Set());
  };

  const toggleEquipmentSelection = (equipmentId: string) => {
    const newSelection = new Set(selectedEquipment);
    if (newSelection.has(equipmentId)) {
      newSelection.delete(equipmentId);
    } else {
      newSelection.add(equipmentId);
    }
    setSelectedEquipment(newSelection);
  };

  // Get unique atolls for dropdown
  const uniqueAtolls = Array.from(new Set(islands.map(island => island.atoll))).sort();

  // Get islands for selected atoll
  const islandsInSelectedAtoll = selectedAtoll 
    ? islands.filter(island => island.atoll === selectedAtoll).sort((a, b) => a.name.localeCompare(b.name))
    : [];

  // Get selected island object
  const selectedIsland = selectedIslandId 
    ? islands.find(island => island.id === selectedIslandId) 
    : null;

  // Handle atoll selection
  const handleAtollChange = (atoll: string) => {
    setSelectedAtoll(atoll);
    setSelectedIslandId(""); // Clear island selection when atoll changes
  };

  // Handle island selection and trigger check-in
  const handleIslandSelect = (islandId: string) => {
    setSelectedIslandId(islandId);
    const island = islands.find(i => i.id === islandId);
    if (island) {
      handleCheckIn(island);
    }
  };

  // Group equipment by atoll and island for better organization
  const groupEquipmentByLocation = (equipmentList: Equipment[]) => {
    const grouped = equipmentList.reduce((acc, eq) => {
      const key = eq.island ? `${eq.atoll} - ${eq.island}` : eq.atoll;
      if (!acc[key]) {
        acc[key] = [];
      }
      acc[key].push(eq);
      return acc;
    }, {} as Record<string, Equipment[]>);

    // Sort locations alphabetically
    return Object.keys(grouped)
      .sort()
      .map(location => ({
        location,
        equipment: grouped[location]
      }));
  };

  const equipmentByLocation = groupEquipmentByLocation(equipment);

  if (isLoading) {
    return (
      <Card className={className}>
        <CardContent className="p-4">
          <div className="animate-pulse">
            <div className="h-4 bg-muted rounded mb-2"></div>
            <div className="h-4 bg-muted rounded mb-2"></div>
            <div className="h-4 bg-muted rounded"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={className}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg flex items-center gap-2">
            <MapPin className="h-5 w-5 text-primary" />
            Island Check-in
          </CardTitle>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            data-testid="button-toggle-checkin"
          >
            {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
          </Button>
        </div>
        <div className="flex items-center gap-4 text-sm text-muted-foreground">
          <div className="flex items-center gap-1">
            <MapPin className="h-4 w-4 text-blue-600" />
            <span>{uniqueAtolls.length} atolls</span>
          </div>
          <div className="flex items-center gap-1">
            <Users className="h-4 w-4 text-green-600" />
            <span>{islands.length} islands</span>
          </div>
        </div>
      </CardHeader>

      {isExpanded && (
        <CardContent className="pt-0">
          {/* Dropdown Selection Interface */}
          <div className="space-y-4">
            {/* Atoll Selection */}
            <div className="space-y-2">
              <label htmlFor="atoll-select" className="text-sm font-medium text-muted-foreground">
                Select Atoll
              </label>
              <Select value={selectedAtoll} onValueChange={handleAtollChange}>
                <SelectTrigger id="atoll-select" data-testid="select-atoll">
                  <SelectValue placeholder="Choose an atoll..." />
                </SelectTrigger>
                <SelectContent>
                  {uniqueAtolls.map((atoll) => (
                    <SelectItem key={atoll} value={atoll}>
                      {atoll.replace(/ atoll$/i, '')}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Island Selection */}
            <div className="space-y-2">
              <label htmlFor="island-select" className="text-sm font-medium text-muted-foreground">
                Select Island
              </label>
              <Select 
                value={selectedIslandId} 
                onValueChange={handleIslandSelect}
                disabled={!selectedAtoll}
              >
                <SelectTrigger id="island-select" data-testid="select-island">
                  <SelectValue placeholder={selectedAtoll ? "Choose an island..." : "Select an atoll first"} />
                </SelectTrigger>
                <SelectContent>
                  {islandsInSelectedAtoll.map((island) => (
                    <SelectItem key={island.id} value={island.id}>
                      {island.name}
                      {island.hasVisited && (
                        <span className="ml-2 text-xs text-green-600">
                          âœ“ Visited
                        </span>
                      )}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Instructions */}
            <div className="p-3 bg-muted/50 rounded-lg">
              <p className="text-sm text-muted-foreground">
                <strong>Instructions:</strong> Select an atoll first, then choose an island to check in. 
                Islands can be checked into multiple times for repeat visits.
              </p>
            </div>

            {/* Selected Island Info */}
            {selectedIsland && (
              <div className="space-y-3">
                <div className="p-3 border rounded-lg bg-card">
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-medium" data-testid={`text-selected-island-${selectedIsland.id}`}>
                        {selectedIsland.name}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {selectedIsland.atoll}
                        {selectedIsland.lastVisited && (
                          <span className="ml-2">
                            â€¢ Last visited: {new Date(selectedIsland.lastVisited).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    </div>
                    {selectedIsland.hasVisited && (
                      <Badge variant="secondary" className="bg-green-100 text-green-700 dark:bg-green-900/50 dark:text-green-300">
                        <CheckCircle className="h-3 w-3 mr-1" />
                        Visited Before
                      </Badge>
                    )}
                  </div>
                </div>

                {/* Visit History Section */}
                {selectedIsland.hasVisited && visitHistory.length > 0 && (
                  <div className="border rounded-lg bg-card">
                    <Collapsible open={showVisitHistory} onOpenChange={setShowVisitHistory}>
                      <CollapsibleTrigger asChild>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="w-full p-3 h-auto justify-between text-left hover:bg-muted/50"
                          data-testid="button-toggle-visit-history"
                        >
                          <div className="flex items-center gap-2">
                            <History className="h-4 w-4 text-primary" />
                            <span className="font-medium">Visit History</span>
                            <Badge variant="outline" className="ml-1">
                              {visitHistory.length} {visitHistory.length === 1 ? 'visit' : 'visits'}
                            </Badge>
                          </div>
                          {showVisitHistory ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                        </Button>
                      </CollapsibleTrigger>
                      <CollapsibleContent className="px-3 pb-3">
                        <Separator className="mb-3" />
                        {isLoadingHistory ? (
                          <div className="text-center py-4">
                            <div className="animate-pulse">
                              <div className="h-4 bg-muted rounded mb-2"></div>
                              <div className="h-4 bg-muted rounded mb-2"></div>
                            </div>
                          </div>
                        ) : (
                          <ScrollArea className="max-h-48">
                            <div className="space-y-3">
                              {visitHistory.map((visit) => (
                                <div key={visit.id} className="p-3 bg-muted/30 rounded-lg" data-testid={`visit-${visit.id}`}>
                                  <div className="flex items-start justify-between">
                                    <div className="flex-1">
                                      <div className="flex items-center gap-2 mb-1">
                                        <Calendar className="h-3 w-3 text-muted-foreground" />
                                        <span className="text-sm font-medium">
                                          {visit.visitedAt ? format(new Date(visit.visitedAt), 'MMM dd, yyyy') : 'Unknown date'}
                                        </span>
                                        <Clock className="h-3 w-3 text-muted-foreground ml-2" />
                                        <span className="text-xs text-muted-foreground">
                                          {visit.visitedAt ? format(new Date(visit.visitedAt), 'h:mm a') : 'Unknown time'}
                                        </span>
                                      </div>
                                      {visit.equipmentUsed && visit.equipmentUsed.length > 0 && (
                                        <div className="mt-2">
                                          <div className="text-xs text-muted-foreground mb-1">Equipment brought:</div>
                                          <div className="flex flex-wrap gap-1">
                                            {visit.equipmentUsed.map((eq, index) => (
                                              <Badge key={index} variant="outline" className="text-xs">
                                                <Package className="h-2 w-2 mr-1" />
                                                {eq}
                                              </Badge>
                                            ))}
                                          </div>
                                        </div>
                                      )}
                                    </div>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </ScrollArea>
                        )}
                      </CollapsibleContent>
                    </Collapsible>
                  </div>
                )}
              </div>
            )}

            {islands.length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                <MapPin className="h-8 w-8 mx-auto mb-2 opacity-50" />
                <p className="text-sm">No islands available</p>
              </div>
            )}
          </div>
        </CardContent>
      )}

      {/* Equipment Selection Dialog */}
      <Dialog open={showEquipmentDialog} onOpenChange={setShowEquipmentDialog}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
