import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { useQuery } from '@tanstack/react-query';
import type { UserMilestone } from '@shared/schema';
import { Trophy, Target, MapPin, Waves } from 'lucide-react';

export function MilestoneBadges() {
  const { data: milestones = [], isLoading } = useQuery<UserMilestone[]>({
    queryKey: ['/api/milestones'],
    staleTime: 0, // Always fetch fresh data
    refetchOnMount: true,
    refetchOnWindowFocus: true,
  });

  const { data: stats } = useQuery<{ totalIslands: number; totalVisits: number; uniqueAtolls: number }>({
    queryKey: ['/api/milestones/stats'],
    staleTime: 0, // Always fetch fresh data
    refetchOnMount: true,
    refetchOnWindowFocus: true,
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Trophy className="w-5 h-5 text-yellow-500" />
            Milestones & Achievements
          </CardTitle>
          <CardDescription>Your exploration progress and badges</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="text-center text-gray-500">Loading achievements...</div>
        </CardContent>
      </Card>
    );
  }

  // Defensive deduplication: Remove duplicates by milestone name, keep highest level
  const uniqueMilestones = milestones.reduce((acc, current) => {
    const existing = acc.find(m => m.milestoneName === current.milestoneName && m.milestoneType === current.milestoneType);

    if (!existing) {
      acc.push(current);
    } else {
      // Keep the one with higher milestone level (more advanced/correct)
      if (current.milestoneLevel > existing.milestoneLevel) {
        const index = acc.findIndex(m => m.milestoneName === existing.milestoneName && m.milestoneType === existing.milestoneType);
        acc[index] = current;
        console.warn(`Duplicate milestone name detected: ${current.milestoneName}`, {
          kept: current,
          discarded: existing
        });
      } else {
        console.warn(`Duplicate milestone name detected: ${current.milestoneName}`, {
          kept: existing, 
          discarded: current
        });
      }
    }
    return acc;
  }, [] as UserMilestone[]);

  // Separate achieved and in-progress milestones, sorted by target value (low to high)
  const achievedMilestones = uniqueMilestones
    .filter(m => m.progress >= m.targetValue)
    .sort((a, b) => a.targetValue - b.targetValue);
  const inProgressMilestones = uniqueMilestones
    .filter(m => m.progress < m.targetValue)
    .sort((a, b) => a.targetValue - b.targetValue);

  return (
    <Card className="data-testid-milestone-badges">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Trophy className="w-5 h-5 text-yellow-500" />
          Milestones & Achievements
        </CardTitle>
        <CardDescription>
          Your exploration progress and badges
          {stats && (
            <span className="ml-2 text-sm">
              â€¢ {stats.totalIslands} islands â€¢ {stats.uniqueAtolls} atolls â€¢ {stats.totalVisits} visits
            </span>
          )}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Achieved Milestones */}
        {achievedMilestones.length > 0 && (
          <div>
            <h4 className="font-semibold mb-3 flex items-center gap-2">
              <Trophy className="w-4 h-4 text-yellow-500" />
              Earned Badges ({achievedMilestones.length})
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
              {achievedMilestones.map((milestone) => (
                <MilestoneBadge
                  key={milestone.id}
                  milestone={milestone}
                  isAchieved={true}
                  data-testid={`badge-achieved-${milestone.milestoneLevel}`}
                />
              ))}
            </div>
          </div>
        )}

        {/* In Progress Milestones */}
        {inProgressMilestones.length > 0 && (
          <div>
            <h4 className="font-semibold mb-3 flex items-center gap-2">
              <Target className="w-4 h-4 text-blue-500" />
              Next Goals ({inProgressMilestones.length})
            </h4>
            <div className="space-y-3">
              {inProgressMilestones.slice(0, 5).map((milestone) => (
                <MilestoneBadge
                  key={milestone.id}
                  milestone={milestone}
                  isAchieved={false}
                  data-testid={`badge-progress-${milestone.milestoneLevel}`}
                />
              ))}
            </div>
          </div>
        )}

        {/* No milestones message */}
        {milestones.length === 0 && (
          <div className="text-center py-8 text-gray-500">
            <MapPin className="w-12 h-12 mx-auto mb-3 text-gray-300" />
            <p className="text-lg font-medium">Start Your Journey!</p>
            <p className="text-sm">Check in to your first island to earn your first milestone badge.</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

interface MilestoneBadgeProps {
  milestone: UserMilestone;
  isAchieved: boolean;
  className?: string;
  'data-testid'?: string;
}

function MilestoneBadge({ milestone, isAchieved, className = '', ...props }: MilestoneBadgeProps) {
  const progressPercentage = Math.min((milestone.progress / milestone.targetValue) * 100, 100);

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'island_visits':
        return <MapPin className="w-4 h-4" />;
      case 'atolls_visited':
        return <Waves className="w-4 h-4" />;
      default:
        return <Target className="w-4 h-4" />;
    }
  };

  if (isAchieved) {
    return (
      <div
        className={`relative p-4 rounded-lg border-2 bg-gradient-to-br from-white to-gray-50 shadow-sm hover:shadow-md transition-shadow ${className}`}
        style={{ borderColor: milestone.badgeColor }}
        {...props}
      >
        <div className="flex items-start gap-3">
          <div 
            className="flex-shrink-0 w-12 h-12 rounded-full flex items-center justify-center text-white text-xl shadow-lg"
            style={{ backgroundColor: milestone.badgeColor }}
          >
            {milestone.badgeIcon}
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 mb-1">
              <h5 className="font-semibold text-sm truncate">{milestone.milestoneName}</h5>
              <Badge variant="default" className="text-xs px-2 py-0.5" style={{ backgroundColor: milestone.badgeColor }}>
                âœ“ Level {milestone.milestoneLevel}
              </Badge>
            </div>
            <p className="text-xs text-gray-600 mb-2">{milestone.description}</p>
            <div className="flex items-center gap-2 text-xs text-gray-500">
              {getTypeIcon(milestone.milestoneType)}
              <span className="font-medium">{milestone.progress}/{milestone.targetValue}</span>
              <span>Completed!</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div
      className={`p-4 rounded-lg border border-gray-200 bg-white hover:bg-gray-50 transition-colors ${className}`}
      {...props}
    >
      <div className="flex items-start gap-3">
        <div className="flex-shrink-0 w-10 h-10 rounded-full bg-gray-100 flex items-center justify-center text-gray-400">
          {milestone.badgeIcon}
        </div>
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1">
            <h5 className="font-semibold text-sm text-gray-700 truncate">{milestone.milestoneName}</h5>
            <Badge variant="outline" className="text-xs px-2 py-0.5">
              Level {milestone.milestoneLevel}
            </Badge>
          </div>
          <p className="text-xs text-gray-600 mb-3">{milestone.description}</p>
          <div className="space-y-2">
            <div className="flex items-center justify-between text-xs">
              <div className="flex items-center gap-1 text-gray-500">
                {getTypeIcon(milestone.milestoneType)}
                <span>{milestone.progress}/{milestone.targetValue}</span>
              </div>
              <span className="font-medium text-gray-700">{progressPercentage.toFixed(0)}%</span>
            </div>
            <Progress value={progressPercentage} className="h-2" />
          </div>
        </div>
      </div>
    </div>
  );
}

