export type UserRole = 'attachment' | 'agent' | 'coordinator' | 'admin';

export const ROLES: Record<string, UserRole> = {
  ATTACHMENT: 'attachment',
  AGENT: 'agent', 
  COORDINATOR: 'coordinator',
  ADMIN: 'admin'
} as const;

export interface Permission {
  viewMap: boolean;
  viewEvents: boolean;
  editEvents: boolean;
  viewMembers: boolean;
  editMembers: boolean;
  viewEquipment: boolean;
  editEquipment: boolean;
  transferEquipment: boolean;
  checkInWithEquipment: boolean;
  checkInBasic: boolean;
  accessAdminPanel: boolean;
}

export const ROLE_PERMISSIONS: Record<UserRole, Permission> = {
  attachment: {
    viewMap: true,
    viewEvents: true,
    editEvents: false,
    viewMembers: true,
    editMembers: false,
    viewEquipment: false,
    editEquipment: false,
    transferEquipment: false,
    checkInWithEquipment: false,
    checkInBasic: true,
    accessAdminPanel: false,
  },
  agent: {
    viewMap: true,
    viewEvents: true,
    editEvents: false,
    viewMembers: true,
    editMembers: false,
    viewEquipment: true,
    editEquipment: true,
    transferEquipment: true,
    checkInWithEquipment: true,
    checkInBasic: true,
    accessAdminPanel: false,
  },
  coordinator: {
    viewMap: true,
    viewEvents: true,
    editEvents: true,
    viewMembers: true,
    editMembers: true,
    viewEquipment: true,
    editEquipment: true,
    transferEquipment: true,
    checkInWithEquipment: true,
    checkInBasic: true,
    accessAdminPanel: false,
  },
  admin: {
    viewMap: true,
    viewEvents: true,
    editEvents: true,
    viewMembers: true,
    editMembers: true,
    viewEquipment: true,
    editEquipment: true,
    transferEquipment: true,
    checkInWithEquipment: true,
    checkInBasic: true,
    accessAdminPanel: true,
  },
};

export function hasPermission(userRole: UserRole | undefined, permission: keyof Permission): boolean {
  if (!userRole) return false;
  return ROLE_PERMISSIONS[userRole]?.[permission] ?? false;
}

export function canAccessRoute(userRole: UserRole | undefined, route: string): boolean {
  if (!userRole) return false;

  const permissions = ROLE_PERMISSIONS[userRole];

  switch (route) {
    case '/map':
      return permissions.viewMap;
    case '/events':
      return permissions.viewEvents;
    case '/members':
      return permissions.viewMembers;
    case '/equipment':
      return permissions.viewEquipment;
    case '/admin':
      return permissions.accessAdminPanel;
    default:
      return true; // Allow access to other routes like dashboard
  }
}

export function getRoleDisplayName(role: UserRole): string {
  switch (role) {
    case 'attachment':
      return 'Attachment';
    case 'agent':
      return 'Agent';
    case 'coordinator':
      return 'Coordinator';
    case 'admin':
      return 'Administrator';
    default:
      return 'Unknown';
  }
}

export function getRoleColor(role: UserRole): string {
  switch (role) {
    case 'attachment':
      return 'text-gray-600 bg-gray-100';
    case 'agent':
      return 'text-blue-600 bg-blue-100';
    case 'coordinator':
      return 'text-green-600 bg-green-100';
    case 'admin':
      return 'text-red-600 bg-red-100';
    default:
      return 'text-gray-600 bg-gray-100';
  }
}

