import { ReactNode } from 'react';
import { usePermissions, useConditionalRender, PermissionGuardProps, RoleGuardProps } from '@/hooks/usePermissions';
import { UserRole } from '@shared/permissions';

// Component wrapper for permission-based rendering
export function PermissionGuard({ permission, fallback = null, children }: PermissionGuardProps) {
  const canRender = useConditionalRender(permission);

  if (!canRender) {
    return <>{fallback}</>;
  }

  return <>{children}</>;
}

// Role-based component wrapper
export function RoleGuard({ allowedRoles, fallback = null, children }: RoleGuardProps) {
  const { userRole, isLoading } = usePermissions();

  if (isLoading) {
    return <>{fallback}</>;
  }

  if (!userRole || !allowedRoles.includes(userRole)) {
    return <>{fallback}</>;
  }

  return <>{children}</>;
}

// Convenience components for specific roles
interface AdminOnlyProps {
  fallback?: ReactNode;
  children: ReactNode;
}

export function AdminOnly({ fallback = null, children }: AdminOnlyProps) {
  return (
    <RoleGuard allowedRoles={['admin']} fallback={fallback}>
      {children}
    </RoleGuard>
  );
}

interface CoordinatorPlusProps {
  fallback?: ReactNode;
  children: ReactNode;
}

export function CoordinatorPlus({ fallback = null, children }: CoordinatorPlusProps) {
  return (
    <RoleGuard allowedRoles={['coordinator', 'admin']} fallback={fallback}>
      {children}
    </RoleGuard>
  );
}

interface AgentPlusProps {
  fallback?: ReactNode;
  children: ReactNode;
}

export function AgentPlus({ fallback = null, children }: AgentPlusProps) {
  return (
    <RoleGuard allowedRoles={['agent', 'coordinator', 'admin']} fallback={fallback}>
      {children}
    </RoleGuard>
  );
}

interface NoAttachmentProps {
  fallback?: ReactNode;
  children: ReactNode;
}

export function NoAttachment({ fallback = null, children }: NoAttachmentProps) {
  return (
    <RoleGuard allowedRoles={['agent', 'coordinator', 'admin']} fallback={fallback}>
      {children}
    </RoleGuard>
  );
}

client/src/hooks/usePermissions.ts.content:
import { useQuery } from '@tanstack/react-query';
import { hasPermission, canAccessRoute, UserRole, Permission } from '@shared/permissions';
import { ReactNode } from 'react';

interface User {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: UserRole;
  profileImageUrl?: string;
  serviceNumber?: string;
  mobile?: string;
  isActive: boolean;
  approvalStatus: string;
}

export function usePermissions() {
  const { data: user, isLoading } = useQuery<User>({
    queryKey: ['/api/auth/user'],
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const userRole = user?.role;

  const checkPermission = (permission: keyof Permission): boolean => {
    return hasPermission(userRole, permission);
  };

  const checkRouteAccess = (route: string): boolean => {
    return canAccessRoute(userRole, route);
  };

  return {
    user,
    userRole,
    isLoading,

    // Permission checkers
    canViewMap: checkPermission('viewMap'),
    canViewEvents: checkPermission('viewEvents'),
    canEditEvents: checkPermission('editEvents'),
    canViewMembers: checkPermission('viewMembers'),
    canEditMembers: checkPermission('editMembers'),
    canViewEquipment: checkPermission('viewEquipment'),
    canEditEquipment: checkPermission('editEquipment'),
    canTransferEquipment: checkPermission('transferEquipment'),
    canCheckInWithEquipment: checkPermission('checkInWithEquipment'),
    canCheckInBasic: checkPermission('checkInBasic'),
    canAccessAdminPanel: checkPermission('accessAdminPanel'),

    // General permission checker
    hasPermission: checkPermission,

    // Route access checker
    canAccessRoute: checkRouteAccess,
  };
}

// Hook for conditional rendering based on permissions
export function useConditionalRender(permission: keyof Permission) {
  const { hasPermission } = usePermissions();
  return hasPermission(permission);
}

// Hook for checking multiple permissions at once
export function useMultiplePermissions(permissions: (keyof Permission)[]) {
  const { hasPermission } = usePermissions();

  return {
    hasAll: permissions.every(permission => hasPermission(permission)),
    hasAny: permissions.some(permission => hasPermission(permission)),
    permissions: permissions.reduce((acc, permission) => {
      acc[permission] = hasPermission(permission);
      return acc;
    }, {} as Record<keyof Permission, boolean>),
  };
}

// Export types for component usage
export interface PermissionGuardProps {
  permission: keyof Permission;
  fallback?: ReactNode;
  children: ReactNode;
}

export interface RoleGuardProps {
  allowedRoles: UserRole[];
  fallback?: ReactNode;
  children: ReactNode;
}

client/src/lib/userDisplayUtils.ts.content:
// Utility functions for handling user name display consistently across the application

export interface UserDisplayInfo {
  displayName: string;
  initials: string;
}

/**
 * Safely generates display name and initials for any user object
 * Handles cases where firstName, lastName, or email might be missing
 */
export const getUserDisplayInfo = (user: any): UserDisplayInfo => {
  if (!user) {
    return { displayName: 'Unknown User', initials: 'UU' };
  }

  const firstName = user.firstName?.trim() || '';
  const lastName = user.lastName?.trim() || '';
  const email = user.email?.trim() || '';

  // Generate display name with fallbacks
  let displayName = '';
  if (firstName && lastName) {
    displayName = `${firstName} ${lastName}`;
  } else if (firstName) {
    displayName = firstName;
  } else if (lastName) {
    displayName = lastName;
  } else if (email) {
    // Use email username as fallback
    displayName = email.split('@')[0];
  } else {
    displayName = 'Unnamed Member';
  }

  // Generate initials with fallbacks
  let initials = '';
  if (firstName && lastName) {
    initials = firstName[0].toUpperCase() + lastName[0].toUpperCase();
  } else if (firstName) {
    initials = firstName.substring(0, 2).toUpperCase();
  } else if (lastName) {
    initials = lastName.substring(0, 2).toUpperCase();
  } else if (email) {
    const emailUser = email.split('@')[0];
    initials = emailUser.substring(0, 2).toUpperCase();
  } else {
    initials = 'UM'; // Unnamed Member
  }

  return { displayName, initials };
};

/**
 * Quick helper to get just the display name
 */
export const getDisplayName = (user: any): string => {
  return getUserDisplayInfo(user).displayName;
};

/**
 * Quick helper to get just the initials  
 */
export const getInitials = (user: any): string => {
  return getUserDisplayInfo(user).initials;
};

