import { useState } from "react";
import { Button } from "@/components/ui/button";
import { X, Navigation, Clock, MapPin, ArrowLeft, ArrowRight, ArrowUp, RotateCcw, ChevronDown, ChevronUp } from "lucide-react";

interface ModernDirectionsPanelProps {
  routeInstructions: string[];
  routeDistance: string;
  routeDuration: string;
  isLoadingDirections: boolean;
  onClose: () => void;
}

const getDirectionIcon = (instruction: string) => {
  const lower = instruction.toLowerCase();
  if (lower.includes('left')) return <ArrowLeft className="w-4 h-4" />;
  if (lower.includes('right')) return <ArrowRight className="w-4 h-4" />;
  if (lower.includes('straight') || lower.includes('continue')) return <ArrowUp className="w-4 h-4" />;
  if (lower.includes('u-turn') || lower.includes('turn around')) return <RotateCcw className="w-4 h-4" />;
  if (lower.includes('arrive')) return <MapPin className="w-4 h-4" />;
  return <Navigation className="w-4 h-4" />;
};

const getInstructionColor = (instruction: string) => {
  const lower = instruction.toLowerCase();
  if (lower.includes('arrive')) return 'text-green-600 bg-green-50';
  if (lower.includes('turn') || lower.includes('left') || lower.includes('right')) return 'text-orange-600 bg-orange-50';
  return 'text-blue-600 bg-blue-50';
};

export default function ModernDirectionsPanel({
  routeInstructions,
  routeDistance,
  routeDuration,
  isLoadingDirections,
  onClose
}: ModernDirectionsPanelProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);

  if (isLoadingDirections) {
    return (
      <div className="absolute bottom-2 left-2 right-2 bg-white/95 backdrop-blur-sm border border-white/20 shadow-lg rounded-lg p-4 z-[1000]">
        <div className="flex items-center justify-center gap-2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
          <span className="text-sm text-gray-600">Calculating route...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="absolute bottom-2 left-2 right-2 bg-white/95 backdrop-blur-sm border border-white/20 shadow-lg rounded-lg z-[1000] overflow-hidden directions-slide-up">
      {/* Header */}
      <div className="flex items-center justify-between p-3 border-b border-gray-100">
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
            <Navigation className="w-4 h-4 text-white" />
          </div>
          <div>
            <h3 className="font-semibold text-sm text-gray-900">Navigation</h3>
            <div className="flex items-center gap-2 text-xs text-gray-600">
              <span className="flex items-center gap-1">
                <MapPin className="w-3 h-3" />
                {routeDistance}
              </span>
              <span className="flex items-center gap-1">
                <Clock className="w-3 h-3" />
                {routeDuration}
              </span>
            </div>
          </div>
        </div>

        <div className="flex items-center gap-1">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            className="h-7 w-7 p-0 hover:bg-gray-100/80"
            data-testid="button-toggle-directions"
          >
            {isExpanded ? <ChevronDown className="w-3 h-3" /> : <ChevronUp className="w-3 h-3" />}
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            className="h-7 w-7 p-0 hover:bg-gray-100/80"
            data-testid="button-close-directions"
          >
            <X className="w-3 h-3" />
          </Button>
        </div>
      </div>

      {/* Quick view - first instruction */}
      {!isExpanded && routeInstructions.length > 0 && (
        <div className="p-3">
          <div className="flex items-start gap-3">
            <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${getInstructionColor(routeInstructions[currentStep])}`}>
              {getDirectionIcon(routeInstructions[currentStep])}
            </div>
            <div className="flex-1 min-w-0">
              <div className="text-sm font-medium text-gray-900 mb-1">
                Step {currentStep + 1} of {routeInstructions.length}
              </div>
              <div className="text-sm text-gray-700">
                {routeInstructions[currentStep]}
              </div>
            </div>
          </div>

          {/* Step navigation */}
          {routeInstructions.length > 1 && (
            <div className="flex items-center justify-between mt-3">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentStep(Math.max(0, currentStep - 1))}
                disabled={currentStep === 0}
                className="h-7 px-2"
                data-testid="button-prev-step"
              >
                <ArrowLeft className="w-3 h-3 mr-1" />
                Prev
              </Button>

              <div className="flex items-center gap-1">
                {routeInstructions.slice(0, 5).map((_, index) => (
                  <div
                    key={index}
                    className={`w-2 h-2 rounded-full transition-colors ${
                      index === currentStep ? 'bg-blue-600' : 
                      index < currentStep ? 'bg-green-500' : 'bg-gray-300'
                    }`}
                  />
                ))}
                {routeInstructions.length > 5 && (
                  <span className="text-xs text-gray-500 ml-1">
                    +{routeInstructions.length - 5}
                  </span>
                )}
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentStep(Math.min(routeInstructions.length - 1, currentStep + 1))}
                disabled={currentStep === routeInstructions.length - 1}
                className="h-7 px-2"
                data-testid="button-next-step"
              >
                Next
                <ArrowRight className="w-3 h-3 ml-1" />
              </Button>
            </div>
          )}
        </div>
      )}

      {/* Expanded view - all instructions */}
      {isExpanded && (
        <div className="max-h-64 overflow-y-auto">
          <div className="space-y-2 p-3">
            {routeInstructions.map((instruction, index) => (
              <div
                key={index}
                className={`flex items-start gap-3 p-2 rounded-lg transition-colors ${
                  index === currentStep ? 'bg-blue-50 border border-blue-200' : 
                  index < currentStep ? 'bg-green-50' : 'hover:bg-gray-50'
                }`}
                onClick={() => setCurrentStep(index)}
                data-testid={`direction-step-${index}`}
              >
                <div className={`w-8 h-8 rounded-lg flex items-center justify-center flex-shrink-0 ${getInstructionColor(instruction)}`}>
                  {getDirectionIcon(instruction)}
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="text-xs font-medium text-gray-500">
                      Step {index + 1}
                    </span>
                    {index === currentStep && (
                      <span className="text-xs px-2 py-0.5 bg-blue-600 text-white rounded-full">
                        Current
                      </span>
                    )}
                    {index < currentStep && (
                      <span className="text-xs px-2 py-0.5 bg-green-600 text-white rounded-full">
                        Done
                      </span>
                    )}
                  </div>
                  <div className="text-sm text-gray-700">
                    {instruction}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
